import type { Express } from "express";
import express from "express";
import { createServer, type Server } from "http";
import bcrypt from "bcrypt";
import { storage } from "./storage";
import { insertSongSchema, insertTrackSchema, broadcastSessions, broadcastSongs } from "@shared/schema";
import { eq, sql } from "drizzle-orm";
import { db } from "./db";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { subscriptionManager } from "./subscriptionManager";
import { setupBroadcastServer } from "./broadcast-server";
import Stripe from "stripe";
import multer from "multer";
import path from "path";
import fs from "fs";
import { promises as fsPromises } from "fs";
import { WebSocketServer } from 'ws';

let stripe: Stripe | null = null;
let isStripeEnabled = false;
let isTestMode = false;

if (process.env.STRIPE_SECRET_KEY && process.env.STRIPE_SECRET_KEY !== 'placeholder_for_deployment') {
  try {
    stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
    isStripeEnabled = true;
    isTestMode = process.env.STRIPE_SECRET_KEY.startsWith('sk_test_');
    console.log(`ğŸ”‘ Stripe API Mode: ${isTestMode ? 'TEST MODE âœ…' : 'LIVE MODE âš ï¸'}`);

    if (!isTestMode) {
      console.warn('âš ï¸ WARNING: Using live Stripe keys - test cards will be declined!');
    }
    console.log('âœ… Stripe initialized successfully for payments');
  } catch (error: any) {
    console.error('âŒ Failed to initialize Stripe for payments:', error.message);
    console.log('ğŸ”§ Payment features will be disabled');
  }
} else {
  console.log('âš ï¸ STRIPE_SECRET_KEY not available - payment features disabled');
  console.log('ğŸ’¡ For production deployment, ensure STRIPE_SECRET_KEY is properly configured');
}

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), "uploads");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const upload = multer({
  storage: multer.memoryStorage(), // Use memory storage to access file.buffer
  fileFilter: (req: any, file: any, cb: any) => {
    const allowedTypes = ['.mp3', '.wav', '.ogg', '.m4a'];
    const ext = path.extname(file.originalname).toLowerCase();
    if (allowedTypes.includes(ext)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only audio files are allowed.'));
    }
  },
  limits: {
    fileSize: 50 * 1024 * 1024 // 50MB limit
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Health check endpoint for Replit
  app.get('/api/health', (req, res) => {
    res.json({ status: 'ok', timestamp: new Date().toISOString() });
  });

  // Version endpoint - FIXED to return stable version
  app.get('/api/version', (req, res) => {
    // Use stable version that doesn't change every request
    const version = process.env.VITE_BUILD_ID || 
                   process.env.BUILD_ID || 
                   process.env.REPLIT_DEPLOYMENT_ID ||
                   'stable-v1.0';  // STABLE fallback instead of Date.now()

    res.json({ 
      version,
      timestamp: new Date().toISOString(),
      deploymentId: process.env.REPLIT_DEPLOYMENT_ID || 'stable'
    });
  });

  // SERVICE WORKER OVERRIDE DISABLED - was destroying user data

  console.log('ğŸ”§ Starting route registration...');

  // Global error handler for route registration
  const handleRouteError = (section: string, error: any, req: any, res: any) => {
    console.error(`âŒ Error in ${section}:`, error);
    if (res && !res.headersSent) {
      res.status(500).json({ 
        error: 'Internal server error', 
        message: 'Service temporarily unavailable',
        section: section 
      });
    }
  };

  try {
    // Enable auth middleware for user-specific songs
    console.log('ğŸ” Setting up authentication middleware...');
    await setupAuth(app);
    console.log('âœ… Authentication middleware configured successfully');
  } catch (error: any) {
    console.error('âŒ Failed to setup authentication:', error);
    throw new Error(`Authentication setup failed: ${error.message}`);
  }

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Profile photo upload endpoint (works with both local and Replit auth)
  app.put('/api/profile-photo', async (req: any, res) => {
    try {
      const { photoData, userEmail } = req.body;

      if (!photoData) {
        return res.status(400).json({ message: "Missing photo data" });
      }

      // For local auth, email comes from request body
      // For Replit auth, email comes from session
      let email = userEmail;
      if (!email && req.user?.claims?.email) {
        email = req.user.claims.email;
      }

      if (!email) {
        return res.status(400).json({ message: "Missing user email" });
      }

      const updatedUser = await storage.updateUserProfilePhoto(email, photoData);

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({ message: "Profile photo updated successfully", user: updatedUser });
    } catch (error) {
      console.error("Error updating profile photo:", error);
      res.status(500).json({ message: "Failed to update profile photo" });
    }
  });

  // Get profile photo endpoint (works with both local and Replit auth)
  app.get('/api/profile-photo', async (req: any, res) => {
    try {
      // For local auth, email comes from query params
      // For Replit auth, email comes from session
      let email = req.query.email;
      if (!email && req.user?.claims?.email) {
        email = req.user.claims.email;
      }

      if (!email) {
        return res.status(400).json({ message: "Missing user email" });
      }

      const user = await storage.getUserByEmail(email);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      console.log(`ğŸ“¸ Getting profile photo for ${email}: ${user.profilePhoto ? 'Found' : 'None'}`);

      // Set proper content type for JSON response
      res.setHeader('Content-Type', 'application/json');
      res.json({ profilePhoto: user.profilePhoto || null });
    } catch (error) {
      console.error('Error getting profile photo:', error);
      res.status(500).json({ message: 'Failed to get profile photo' });
    }
  });

  // Update user profile endpoint (works with both local and Replit auth)
  app.put('/api/profile', async (req: any, res) => {
    try {
      const { firstName, lastName, phone, customBroadcastId, userEmail } = req.body;

      // For local auth, email comes from request body
      // For Replit auth, email comes from session
      let email = userEmail;
      if (!email && req.user?.claims?.email) {
        email = req.user.claims.email;
      }

      if (!email) {
        return res.status(400).json({ message: "Missing user email" });
      }

      const updatedUser = await storage.updateUserProfile(email, { firstName, lastName, phone, customBroadcastId });

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      console.log(`âœ… Updated profile for user: ${email}`);
      res.json({ message: "Profile updated successfully", user: updatedUser });
    } catch (error) {
      console.error("Error updating profile:", error);
      res.status(500).json({ message: "Failed to update profile" });
    }
  });

  // Get user profile endpoint (works with both local and Replit auth)
  app.get('/api/profile', async (req: any, res) => {
    try {
      // For local auth, email comes from query params
      // For Replit auth, email comes from session
      let email = req.query.email;
      if (!email && req.user?.claims?.email) {
        email = req.user.claims.email;
      }

      if (!email) {
        return res.status(400).json({ message: "Missing user email" });
      }

      const user = await storage.getUserByEmail(email);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      console.log(`ğŸ“‹ Getting profile for ${email}`);

      // Calculate correct userType from subscriptionStatus
      const userType = user.subscriptionStatus === 1 ? 'free' : 
                       user.subscriptionStatus === 2 ? 'premium' : 'professional';

      // Set proper content type for JSON response
      res.setHeader('Content-Type', 'application/json');
      res.json({ ...user, userType });
    } catch (error) {
      console.error('Error getting user profile:', error);
      res.status(500).json({ message: 'Failed to get user profile' });
    }
  });

  // Email/password authentication endpoints for frontend
  app.post('/api/auth/register', async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: 'Email and password are required' });
      }

      if (password.length < 6) {
        return res.status(400).json({ error: 'Password must be at least 6 characters' });
      }

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(409).json({ error: 'User already exists with this email' });
      }

      // Create new user with hashed password
      const passwordHash = await bcrypt.hash(password, 10);

      const newUser = await storage.upsertUser({
        id: `user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        email: email.toLowerCase(),
        firstName: null,
        lastName: null,
        profileImageUrl: null,
        subscriptionStatus: 1, // 1 = free user
      });

      console.log('âœ… New user registered:', newUser.email);
      res.json({ 
        success: true, 
        user: { 
          id: newUser.id, 
          email: newUser.email,
          userType: newUser.subscriptionStatus === 1 ? 'free' : 
                   newUser.subscriptionStatus === 2 ? 'premium' : 'professional'
        }
      });
    } catch (error: any) {
      console.error('âŒ Registration error:', error);
      res.status(500).json({ error: 'Registration failed' });
    }
  });

  // Login endpoint for email/password authentication
  app.post('/api/auth/login', async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: 'Email and password are required' });
      }

      // Check if user exists
      const user = await storage.getUserByEmail(email.toLowerCase());
      if (!user) {
        return res.status(401).json({ error: 'Invalid email or password' });
      }

      console.log('âœ… User authenticated:', user.email);

      const userType = user.subscriptionStatus === 1 ? 'free' : 
                      user.subscriptionStatus === 2 ? 'premium' : 'professional';

      res.json({ 
        success: true, 
        user: { 
          id: user.id, 
          email: user.email,
          userType: userType
        }
      });
    } catch (error: any) {
      console.error('âŒ Login error:', error);
      res.status(500).json({ error: 'Login failed' });
    }
  });

  console.log('âœ… Authentication routes registered');

  // Sample ZIP file download routes
  console.log('ğŸ“¦ Registering sample file download routes...');
  app.get('/api/download/3am-sample', (req, res) => {
    const filePath = path.join(process.cwd(), 'attached_assets', '3AM_1755653001926.zip');
    res.download(filePath, '3AM_Matchbox20_Sample.zip', (err) => {
      if (err) {
        console.error('Error downloading 3AM sample:', err);
        res.status(404).json({ error: 'Sample file not found' });
      }
    });
  });

  app.get('/api/download/comfortably-numb-sample', (req, res) => {
    const filePath = path.join(process.cwd(), 'attached_assets', 'Comfortably Numb_1755653007913.zip');
    res.download(filePath, 'ComfortablyNumb_PinkFloyd_Sample.zip', (err) => {
      if (err) {
        console.error('Error downloading Comfortably Numb sample:', err);
        res.status(404).json({ error: 'Sample file not found' });
      }
    });
  });
  console.log('âœ… Sample file download routes registered');

  // Stripe payment routes for subscription management
  console.log('ğŸ’³ Registering Stripe payment routes...');

  if (!isStripeEnabled || !stripe) {
    console.log('âš ï¸ Stripe not available - payment routes will return errors');

    // Register disabled payment routes
    app.post('/api/create-subscription', (req, res) => {
      res.status(503).json({ 
        error: 'payment_unavailable',
        message: 'Payment services are temporarily unavailable. Please try again later.' 
      });
    });

    app.post('/api/cancel-subscription', (req, res) => {
      res.status(503).json({ 
        error: 'payment_unavailable',
        message: 'Payment services are temporarily unavailable. Please try again later.' 
      });
    });

    app.post('/api/verify-subscription', (req, res) => {
      res.json({ isPaid: false, userType: 'free' });
    });

    console.log('âœ… Disabled payment routes registered');
  } else {
    console.log('ğŸ’³ Registering active Stripe payment routes...');

    // Create subscription for premium/professional tiers
    app.post('/api/create-subscription', async (req, res) => {
    try {
      const { email, priceId, planName } = req.body;

      if (!email || !priceId) {
        return res.status(400).json({ error: 'Email and price ID are required' });
      }

      // Create or retrieve customer
      let customer;
      const existingCustomers = await stripe.customers.list({ email });

      if (existingCustomers.data.length > 0) {
        customer = existingCustomers.data[0];
      } else {
        customer = await stripe.customers.create({ email });
      }

      // Create subscription
      const subscription = await stripe.subscriptions.create({
        customer: customer.id,
        items: [{ price: priceId }],
        payment_behavior: 'default_incomplete',
        payment_settings: { save_default_payment_method: 'on_subscription' },
        expand: ['latest_invoice.payment_intent'],
        metadata: {
          email,
          planName: planName || 'premium'
        }
      });

      // Update user in database with subscription info
      const user = await storage.getUserByEmail(email);
      if (user) {
        const subscriptionStatus = planName === 'professional' ? 3 : 2; // 2=premium, 3=professional
        await storage.updateUserStripeInfo(user.id, customer.id, subscription.id);
        await storage.updateUserSubscription(user.id, {
          subscriptionStatus,
          subscriptionEndDate: null
        });
      }

      const clientSecret = subscription.latest_invoice?.payment_intent?.client_secret;

      console.log(`âœ… Created subscription for ${email}: ${subscription.id}`);

      res.json({
        subscriptionId: subscription.id,
        clientSecret,
        customer: customer.id
      });
    } catch (error: any) {
      console.error('âŒ Error creating subscription:', error);
      res.status(500).json({ error: 'Failed to create subscription' });
    }
  });

  // Check subscription status
  app.post('/api/verify-subscription', async (req, res) => {
    try {
      const { email } = req.body;
      console.log('ğŸ” Verifying subscription for email:', email);

      if (!email) {
        return res.status(400).json({ error: 'Email is required' });
      }

      // Get user from database
      const user = await storage.getUserByEmail(email.toLowerCase());
      if (!user) {
        return res.status(404).json({ error: 'User not found' });
      }

      console.log('ğŸ” Database subscription status:', user.subscriptionStatus);

      // Map subscription status to user type
      let userType = 'free';
      let isPaid = false;

      if (user.subscriptionStatus === 2) {
        userType = 'premium';
        isPaid = true;
      } else if (user.subscriptionStatus === 3) {
        userType = 'professional';
        isPaid = true;
      }

      console.log('ğŸ” Final userType:', userType);

      res.json({
        isPaid,
        userType,
        subscriptionStatus: user.subscriptionStatus,
        email: user.email
      });
    } catch (error: any) {
      console.error('âŒ Error verifying subscription:', error);
      res.status(500).json({ 
        error: 'Verification failed',
        message: 'Unable to verify subscription status' 
      });
    }
  });

  // Cancel subscription endpoint
  app.post('/api/cancel-subscription', async (req, res) => {
    try {
      const { email, reasons, feedback } = req.body;

      if (!email) {
        return res.status(400).json({ error: 'Email is required' });
      }

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(404).json({ error: 'User not found' });
      }

      // Cancel Stripe subscription if it exists
      if (user.stripeSubscriptionId) {
        try {
          await stripe.subscriptions.cancel(user.stripeSubscriptionId);
          console.log(`âœ… Cancelled Stripe subscription: ${user.stripeSubscriptionId}`);
        } catch (stripeError) {
          console.error('âŒ Error cancelling Stripe subscription:', stripeError);
          // Continue with database update even if Stripe fails
        }
      }

      // Update user subscription status to free (1)
      await storage.updateUserSubscription(user.id, {
        subscriptionStatus: 1,
        subscriptionEndDate: null
      });

      // Log cancellation feedback for improvement
      console.log(`ğŸ“ Subscription cancelled for ${email}`);
      console.log(`Reasons: ${reasons?.join(', ') || 'None provided'}`);
      if (feedback) {
        console.log(`Feedback: ${feedback}`);
      }

      res.json({
        success: true,
        message: 'Subscription cancelled successfully'
      });
    } catch (error: any) {
      console.error('âŒ Error cancelling subscription:', error);
      res.status(500).json({ error: 'Failed to cancel subscription' });
    }
  });

  // Test endpoint to create users with different subscription levels
  app.post('/api/create-test-users', async (req, res) => {
    try {
      // Create paid test user
      await storage.upsertUser({
        id: 'test_paid_user',
        email: 'paid@test.com',
        firstName: 'Paid',
        lastName: 'User'
      });
      await storage.updateUserSubscription('test_paid_user', {
        subscriptionStatus: 2, // Premium
        subscriptionEndDate: null
      });

      // Create professional test user  
      await storage.upsertUser({
        id: 'test_pro_user',
        email: 'pro@test.com',
        firstName: 'Pro',
        lastName: 'User'
      });
      await storage.updateUserSubscription('test_pro_user', {
        subscriptionStatus: 3, // Professional
        subscriptionEndDate: null
      });

      console.log('âœ… Test users created successfully');
      res.json({
        success: true,
        users: [
          { email: 'paid@test.com', type: 'Premium (tier 2)' },
          { email: 'pro@test.com', type: 'Professional (tier 3)' },
          { email: 'brooke@mnb.com', type: 'Free (tier 1)' }
        ]
      });
    } catch (error: any) {
      console.error('âŒ Error creating test users:', error);
      res.status(500).json({ error: 'Failed to create test users' });
    }
  });

  // Stripe webhook endpoint for subscription validation
  app.post('/api/stripe-webhook', express.raw({type: 'application/json'}), async (req, res) => {
    const sig = req.headers['stripe-signature'];
    let event;

    try {
      // For development, we'll skip signature verification
      // In production, you should verify the webhook signature
      event = JSON.parse(req.body);
    } catch (err) {
      console.error('Webhook signature verification failed.', err);
      return res.status(400).send(`Webhook Error: ${err}`);
    }

    // Handle the event
    switch (event.type) {
      case 'customer.subscription.created':
      case 'customer.subscription.updated':
        const subscription = event.data.object;
        console.log(`ğŸ“‹ Subscription ${event.type}: ${subscription.id} (${subscription.status})`);
        await handleSubscriptionChange(subscription);
        break;

      case 'customer.subscription.deleted':
        const deletedSubscription = event.data.object;
        console.log(`ğŸ—‘ï¸ Subscription deleted: ${deletedSubscription.id}`);
        await handleSubscriptionDeleted(deletedSubscription);
        break;

      case 'invoice.payment_failed':
        const failedInvoice = event.data.object;
        console.log(`ğŸ’³ Payment failed for subscription: ${failedInvoice.subscription}`);
        await handlePaymentFailed(failedInvoice);
        break;

      case 'invoice.payment_succeeded':
        const succeededInvoice = event.data.object;
        console.log(`âœ… Payment succeeded for subscription: ${succeededInvoice.subscription}`);
        await handlePaymentSucceeded(succeededInvoice);
        break;

      case 'checkout.session.completed':
        const checkoutSession = event.data.object;
        console.log(`ğŸ’³ Checkout session completed: ${checkoutSession.id} for customer: ${checkoutSession.customer}`);
        await handleCheckoutCompleted(checkoutSession);
        break;

      default:
        console.log(`Unhandled event type ${event.type}`);
    }

    res.json({received: true});
  });

  // Webhook handler functions
  async function handleSubscriptionChange(subscription: any) {
    try {
      // Find user by Stripe customer ID
      const user = await storage.getUserByStripeCustomerId(subscription.customer);
      if (!user) {
        console.log(`âš ï¸ User not found for customer ID: ${subscription.customer}`);
        return;
      }

      let newStatus = 1; // Default to free

      switch (subscription.status) {
        case 'active':
        case 'trialing':
          newStatus = 2; // Premium
          break;
        case 'past_due':
        case 'canceled':
        case 'incomplete':
        case 'incomplete_expired':
        case 'unpaid':
          newStatus = 1; // Free
          break;
      }

      // Update user subscription status
      await storage.updateUserSubscription(user.id, {
        subscriptionStatus: newStatus,
        subscriptionEndDate: subscription.current_period_end ? 
          new Date(subscription.current_period_end * 1000).toISOString() : null
      });

      console.log(`âœ… Updated user ${user.email} subscription status: ${newStatus}`);
    } catch (error) {
      console.error('âŒ Error handling subscription change:', error);
    }
  }

  async function handleSubscriptionDeleted(subscription: any) {
    try {
      // Find user by Stripe customer ID
      const user = await storage.getUserByStripeCustomerId(subscription.customer);
      if (!user) {
        console.log(`âš ï¸ User not found for customer ID: ${subscription.customer}`);
        return;
      }

      // Set user to free tier
      await storage.updateUserSubscription(user.id, {
        subscriptionStatus: 1, // Free
        subscriptionEndDate: null
      });

      console.log(`âœ… Subscription cancelled for user ${user.email}, set to free tier`);
    } catch (error) {
      console.error('âŒ Error handling subscription deletion:', error);
    }
  }

  async function handlePaymentFailed(invoice: any) {
    try {
      if (!invoice.subscription) return;

      // Get subscription details
      const subscription = await stripe.subscriptions.retrieve(invoice.subscription);
      const user = await storage.getUserByStripeCustomerId(subscription.customer as string);

      if (!user) {
        console.log(`âš ï¸ User not found for customer ID: ${subscription.customer}`);
        return;
      }

      // Set user to free tier on payment failure
      await storage.updateUserSubscription(user.id, {
        subscriptionStatus: 1, // Free
        subscriptionEndDate: null
      });

      console.log(`ğŸ’³ Payment failed for user ${user.email}, downgraded to free tier`);

      // Log the failure reason
      const paymentIntent = invoice.payment_intent;
      if (paymentIntent) {
        console.log(`ğŸ’³ Payment failure reason: ${paymentIntent.last_payment_error?.message || 'Unknown'}`);
      }
    } catch (error) {
      console.error('âŒ Error handling payment failure:', error);
    }
  }

  async function handlePaymentSucceeded(invoice: any) {
    try {
      if (!invoice.subscription) return;

      // Get subscription details
      const subscription = await stripe.subscriptions.retrieve(invoice.subscription);
      const user = await storage.getUserByStripeCustomerId(subscription.customer as string);

      if (!user) {
        console.log(`âš ï¸ User not found for customer ID: ${subscription.customer}`);
        return;
      }

      // Update user to premium tier on successful payment
      await storage.updateUserSubscription(user.id, {
        subscriptionStatus: 2, // Premium
        subscriptionEndDate: subscription.current_period_end ? 
          new Date(subscription.current_period_end * 1000).toISOString() : null
      });

      console.log(`âœ… Payment succeeded for user ${user.email}, upgraded to premium`);
    } catch (error) {
      console.error('âŒ Error handling payment success:', error);
    }
  }

  async function handleCheckoutCompleted(session: any) {
    try {
      console.log(`ğŸ¯ Processing checkout completion for session: ${session.id}`);

      // Find user by Stripe customer ID
      const user = await storage.getUserByStripeCustomerId(session.customer);
      if (!user) {
        console.log(`âš ï¸ User not found for customer ID: ${session.customer}`);
        return;
      }

      // Get the subscription from Stripe
      if (session.subscription) {
        const subscription = await stripe.subscriptions.retrieve(session.subscription);
        console.log(`ğŸ“‹ Retrieved subscription: ${subscription.id} (${subscription.status})`);

        // Determine subscription tier from amount or metadata
        let newStatus = 2; // Default to premium
        if (session.amount_total >= 699) { // $6.99 or higher = professional
          newStatus = 3; // Professional
