} from "lucide-react";

interface CompactTransportControlsProps {
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  onPlay: () => void;
  onPause: () => void;
  onStop: () => void;
}

export default function CompactTransportControls({
  isPlaying,
  currentTime,
  duration,
  onPlay,
  onPause,
  onStop
}: CompactTransportControlsProps) {
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
