    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };


  return (
    <div className="bg-surface border border-gray-700 rounded-lg p-3 w-full mt-[-8px] mb-[-8px] pt-[9px] pb-[9px] pl-[19px] pr-[19px] ml-[0px] mr-[0px]">
