import type { Track } from "@shared/schema";

interface PersistedFileInfo {
  trackId: string;
  trackName: string;
  filePath: string;
  fileName: string;
  mimeType: string;
  size: number;
  lastModified: number;
  waveformData?: number[]; // Store waveform visualization data
}

interface FileRegistry {
  version: string;
  lastUpdated: number;
  files: PersistedFileInfo[];
}

const REGISTRY_FILE_PATH = './data/file-registry.json';

export class FilePersistence {
  private static instance: FilePersistence;
  private registry: FileRegistry;

  static getInstance(): FilePersistence {
    if (!FilePersistence.instance) {
      FilePersistence.instance = new FilePersistence();
    }
    return FilePersistence.instance;
  }

  constructor() {
    this.registry = {
      version: '1.0.0',
      lastUpdated: Date.now(),
      files: []
    };
    this.loadRegistry();
  }

  // Load file registry from local JSON file
  private async loadRegistry(): Promise<void> {
    try {
      const response = await fetch('/api/file-registry');
      if (response.ok) {
        this.registry = await response.json();
        console.log(`Loaded file registry with ${this.registry.files.length} tracked files`);
      } else {
        console.log('No existing file registry found, creating new one');
        await this.saveRegistry();
      }
    } catch (error) {
      console.warn('Could not load file registry:', error);
      await this.saveRegistry(); // Create initial registry
    }
  }

  // Save file registry to local JSON file
  private async saveRegistry(): Promise<void> {
    try {
      this.registry.lastUpdated = Date.now();

      const response = await fetch('/api/file-registry', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(this.registry)
      });

      if (!response.ok) {
        throw new Error(`Failed to save registry: ${response.statusText}`);
      }

      console.log(`Saved file registry with ${this.registry.files.length} files`);
    } catch (error) {
      console.error('Failed to save file registry:', error);
    }
  }

  // Register a new file with persistent tracking
  async registerFile(track: Track, file: File): Promise<void> {
    try {
      const filePath = file.name;

      // Generate waveform data for visualization
      const waveformData = await this.generateWaveform(file);

      const fileInfo: PersistedFileInfo = {
        trackId: track.id,
        trackName: track.name,
        filePath: filePath,
        fileName: file.name,
        mimeType: file.type,
        size: file.size,
        lastModified: file.lastModified || Date.now(),
        waveformData: waveformData
      };

      // Update or add file info
      const existingIndex = this.registry.files.findIndex(f => f.trackId === track.id);
      if (existingIndex >= 0) {
        this.registry.files[existingIndex] = fileInfo;
      } else {
        this.registry.files.push(fileInfo);
      }

      await this.saveRegistry();
      console.log(`Registered file: ${file.name} for track: ${track.name}`);

    } catch (error) {
      console.error('Failed to register file:', error);
      throw error;
    }
  }

  // Get file info for a track
  getFileInfo(trackId: string): PersistedFileInfo | null {
    return this.registry.files.find(f => f.trackId === trackId) || null;
  }

  // Check if file info exists for a track
  hasFileInfo(trackId: string): boolean {
    return this.registry.files.some(f => f.trackId === trackId);
  }

  // Get waveform data for a track
  getWaveformData(trackId: string): number[] | undefined {
    const fileInfo = this.getFileInfo(trackId);
    return fileInfo?.waveformData;
  }

  // Generate waveform visualization data from audio file
  private async generateWaveform(file: File): Promise<number[]> {
    try {
      const arrayBuffer = await file.arrayBuffer();
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

      const channelData = audioBuffer.getChannelData(0); // Use first channel
      const samples = 200; // Generate 200 points for waveform visualization
      const blockSize = Math.floor(channelData.length / samples);
      const waveform: number[] = [];

      for (let i = 0; i < samples; i++) {
        let sum = 0;
        for (let j = 0; j < blockSize; j++) {
          sum += Math.abs(channelData[i * blockSize + j] || 0);
        }
        waveform.push(sum / blockSize);
      }

      audioContext.close();
      return waveform;
    } catch (error) {
      console.warn('Could not generate waveform data:', error);
      return [];
    }
  }

  // Get all registered files
  getAllFiles(): PersistedFileInfo[] {
    return [...this.registry.files];
  }

  // Remove file registration
  async unregisterFile(trackId: string): Promise<void> {
    this.registry.files = this.registry.files.filter(f => f.trackId !== trackId);
    await this.saveRegistry();
    console.log(`Unregistered file for track: ${trackId}`);
  }
}

client/src/lib/local-data-storage.ts.content:
// Complete local data management - replaces all database operations
// Uses the local file system exclusively

import { LocalFileSystem } from "./local-file-system";
import type { Song, Track } from "@shared/schema";

export interface LocalSong {
  id: string;
  title: string;
  artist: string;
  duration: number;
  bpm?: number;
  key?: string;
  lyrics?: string;
  createdAt: number;
  lastModified: number;
  tracks: LocalTrack[];
}

export interface LocalTrack {
  id: string;
  songId: string;
  name: string;
  trackNumber: number;
  audioUrl?: string; // Will be blob URL from local file
  volume: number;
  balance: number;
  isMuted: boolean;
  isSolo: boolean;
  hasAudioData: boolean;
}


export class LocalDataStorage {
  private static instance: LocalDataStorage;
  private localFS: LocalFileSystem;
  private isInitialized = false;

  static getInstance(): LocalDataStorage {
    if (!LocalDataStorage.instance) {
      LocalDataStorage.instance = new LocalDataStorage();
    }
    return LocalDataStorage.instance;
  }

  constructor() {
    this.localFS = LocalFileSystem.getInstance();
  }

  async initialize(): Promise<boolean> {
    try {
      const success = await this.localFS.initialize();
      this.isInitialized = success;
      return success;
    } catch (error) {
      console.error('Failed to initialize local data storage:', error);
      return false;
    }
  }

  isReady(): boolean {
    return this.isInitialized && this.localFS.isReady();
  }

  // Song operations
  async getAllSongs(): Promise<LocalSong[]> {
    const songs = this.localFS.getAllSongs();
    return songs.map(song => ({
      ...song,
      tracks: [],

    }));
  }

  async getSong(id: string): Promise<LocalSong | null> {
    const song = this.localFS.getSong(id);
    if (!song) return null;

    return {
      ...song,
      tracks: [],

    };
  }

  async createSong(songData: Partial<LocalSong>): Promise<LocalSong> {
    const now = Date.now();
    const song: LocalSong = {
      id: songData.id || crypto.randomUUID(),
      title: songData.title || 'Untitled Song',
      artist: songData.artist || '',
      duration: songData.duration || 0,
      bpm: songData.bpm,
      key: songData.key,
      lyrics: songData.lyrics,
      createdAt: songData.createdAt || now,
      lastModified: now,
      tracks: [],

    };

    await this.localFS.saveSong(song.id, song);
    console.log('Song created locally:', song.title);
    return song;
  }

  async updateSong(id: string, updates: Partial<LocalSong>): Promise<LocalSong | null> {
    const existing = this.localFS.getSong(id);
    if (!existing) return null;

    const updated = {
      ...existing,
      ...updates,
      lastModified: Date.now()
    };

    await this.localFS.saveSong(id, updated);
    console.log('Song updated locally:', updated.title);
    return updated;
  }

  async deleteSong(id: string): Promise<boolean> {
    const success = await this.localFS.deleteSong(id);
    if (success) {
      console.log('Song deleted locally:', id);
    }
    return success;
  }

  // Track operations (stored in song data and audio files separately)
  async getTracksBySongId(songId: string): Promise<LocalTrack[]> {
    const song = this.localFS.getSong(songId);
    if (!song || !song.tracks) return [];

    return song.tracks.map((track: LocalTrack) => ({
      ...track,
      hasAudioData: !!this.localFS.getTrackFileInfo(track.id)
    }));
  }

  async createTrack(songId: string, trackData: Partial<LocalTrack>): Promise<LocalTrack> {
    const song = this.localFS.getSong(songId);
    if (!song) throw new Error('Song not found');

    const track: LocalTrack = {
      id: trackData.id || crypto.randomUUID(),
      songId: songId,
      name: trackData.name || 'Untitled Track',
      trackNumber: trackData.trackNumber || 1,
      volume: trackData.volume || 100,
      balance: trackData.balance || 0,
      isMuted: trackData.isMuted || false,
      isSolo: trackData.isSolo || false,
      hasAudioData: false
    };

    // Add track to song
    if (!song.tracks) song.tracks = [];
    song.tracks.push(track);

    await this.localFS.saveSong(songId, song);
    console.log('Track created locally:', track.name);
    return track;
  }

  async updateTrack(trackId: string, updates: Partial<LocalTrack>): Promise<LocalTrack | null> {
    // Find the song containing this track
    const songs = this.localFS.getAllSongs();
    for (const song of songs) {
      if (!song.tracks) continue;

      const trackIndex = song.tracks.findIndex((t: LocalTrack) => t.id === trackId);
      if (trackIndex >= 0) {
        song.tracks[trackIndex] = {
          ...song.tracks[trackIndex],
          ...updates
        };

        await this.localFS.saveSong(song.id, song);
        console.log('Track updated locally:', song.tracks[trackIndex].name);
        return song.tracks[trackIndex];
      }
    }

    return null;
  }

  async deleteTrack(trackId: string): Promise<boolean> {
    // Find and remove track from song
    const songs = this.localFS.getAllSongs();
    for (const song of songs) {
      if (!song.tracks) continue;

      const trackIndex = song.tracks.findIndex((t: LocalTrack) => t.id === trackId);
      if (trackIndex >= 0) {
        song.tracks.splice(trackIndex, 1);
        await this.localFS.saveSong(song.id, song);
        console.log('Track deleted locally:', trackId);
        return true;
      }
    }

    return false;
  }


  // Waveform operations
  async saveWaveform(songId: string, waveformData: number[]): Promise<void> {
    await this.localFS.saveWaveform(songId, waveformData);
  }

  async getWaveform(songId: string): Promise<number[] | null> {
    return await this.localFS.getWaveform(songId);
  }

  // Status information
  getStatus(): string {
    return this.localFS.getStatus();
  }
}

client/src/lib/local-file-system.ts.content:
// Complete local file system management using File System Access API
// 100% offline, no web storage, all files stored locally

interface LocalFileConfig {
  version: string;
  lastUpdated: number;
  projectDirectory?: FileSystemDirectoryHandle;
  audioFiles: {
    [songId: string]: {
      [trackId: string]: {
        fileName: string;
        filePath: string;
        size: number;
        lastModified: number;
        mimeType: string;
        trackName: string;
      };
    };
  };
  waveforms: {
    [songId: string]: {
      filePath: string;
      lastGenerated: number;
    };
  };
  songs: {
    [songId: string]: {
      title: string;
      artist: string;
      duration: number;
      bpm?: number;
      key?: string;
      lyrics?: string;
      createdAt: number;
      lastModified: number;
    };
  };
}

export class LocalFileSystem {
  private static instance: LocalFileSystem;
  private config: LocalFileConfig;
  private projectDir: FileSystemDirectoryHandle | null = null;
  private audioDir: FileSystemDirectoryHandle | null = null;
  private waveformDir: FileSystemDirectoryHandle | null = null;
  private configFile: FileSystemFileHandle | null = null;
  private audioFileCache: Map<string, File> = new Map();

  static getInstance(): LocalFileSystem {
    if (!LocalFileSystem.instance) {
      LocalFileSystem.instance = new LocalFileSystem();
    }
    return LocalFileSystem.instance;
  }

  constructor() {
    this.config = {
      version: '1.0.0',
      lastUpdated: Date.now(),
      audioFiles: {},
      waveforms: {},
      songs: {}
    };
  }

  // Initialize the local file system
  async initialize(): Promise<boolean> {
    try {
      // Check if File System Access API is supported
      if (!('showDirectoryPicker' in window)) {
        throw new Error('File System Access API not supported in this browser. Please use Chrome, Edge, or another Chromium-based browser.');
      }

      // Check if we're in a secure context (required for File System Access API)
      if (!window.isSecureContext) {
        throw new Error('File System Access API requires a secure context (HTTPS). Please access this application over HTTPS.');
      }

      // Try to load existing project directory
      const existingDir = await this.loadExistingProject();
      if (existingDir) {
        console.log('Loaded existing project directory');
        return true;
      }

      // Ask user to select or create project directory
      console.log('Requesting project directory selection...');

      try {
        console.log('Calling showDirectoryPicker...');
        this.projectDir = await (window as any).showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'documents'
        });
        console.log('Directory picker successful, got handle:', !!this.projectDir);
      } catch (pickerError: any) {
        console.error('Directory picker error details:', {
          name: pickerError.name,
          message: pickerError.message,
          code: pickerError.code,
          stack: pickerError.stack
        });

        if (pickerError.name === 'AbortError') {
          console.log('User cancelled directory selection');
          return false;
        }

        if (pickerError.name === 'NotAllowedError') {
          throw new Error(`Permission denied. Your browser or system settings may be blocking file access. Please check browser permissions and try again.`);
        }

        if (pickerError.name === 'SecurityError') {
          throw new Error(`Security error: File system access blocked. Please ensure the page is loaded over HTTPS and try again.`);
        }

        throw new Error(`Failed to open directory picker: ${pickerError.name} - ${pickerError.message}`);
      }

      if (!this.projectDir) {
        throw new Error('No directory selected');
      }

      await this.setupProjectStructure();
      await this.loadConfig();

      console.log('Local file system initialized successfully');
      return true;

    } catch (error: any) {
      console.error('Failed to initialize local file system:', error);
      throw error; // Re-throw to let the UI handle the error display
    }
  }

  // Try to load existing project from stored handle
  private async loadExistingProject(): Promise<boolean> {
    try {
      // Check if we have stored directory handles (browser-specific storage)
      const storedHandle = localStorage.getItem('musicApp_projectDir');
      if (!storedHandle) return false;

      // Note: Directory handles can't be serialized, so this is a placeholder
      // In a real implementation, we'd need to ask user to re-select the directory
      return false;
    } catch (error) {
      return false;
    }
  }

  // Setup project directory structure
  private async setupProjectStructure(): Promise<void> {
    if (!this.projectDir) throw new Error('No project directory');

    try {
      // Create audio files directory
      this.audioDir = await this.projectDir.getDirectoryHandle('audio', { create: true });

      // Create waveforms directory
      this.waveformDir = await this.projectDir.getDirectoryHandle('waveforms', { create: true });

      // Create or get config file
      this.configFile = await this.projectDir.getFileHandle('music-app-config.json', { create: true });

      console.log('Project structure created successfully');
    } catch (error) {
      console.error('Failed to setup project structure:', error);
      throw error;
    }
  }

  // Load configuration from local file
  private async loadConfig(): Promise<void> {
    if (!this.configFile) return;

    try {
      const file = await this.configFile.getFile();
      if (file.size === 0) {
        // New config file, save default
        await this.saveConfig();
        return;
      }

      const text = await file.text();
      this.config = JSON.parse(text);
      console.log(`Loaded config with ${Object.keys(this.config.songs).length} songs`);
    } catch (error) {
      console.error('Failed to load config:', error);
      // Use default config
      await this.saveConfig();
    }
  }

  // Save configuration to local file
  private async saveConfig(): Promise<void> {
    if (!this.configFile) return;

    try {
      this.config.lastUpdated = Date.now();
      const writable = await this.configFile.createWritable();
      await writable.write(JSON.stringify(this.config, null, 2));
      await writable.close();
      console.log('Config saved successfully');
    } catch (error) {
      console.error('Failed to save config:', error);
    }
  }

  // Add audio file for a track
  async addAudioFile(songId: string, trackId: string, trackName: string, file: File): Promise<boolean> {
    if (!this.audioDir) {
      throw new Error('Audio directory not initialized');
    }

    try {
      // Create song directory if it doesn't exist
      const songDir = await this.audioDir.getDirectoryHandle(songId, { create: true });

      // Save audio file with track ID as filename
      const fileName = `${trackId}.${file.name.split('.').pop()}`;
      const fileHandle = await songDir.getFileHandle(fileName, { create: true });

      const writable = await fileHandle.createWritable();
      await writable.write(file);
      await writable.close();

      // Update config
      if (!this.config.audioFiles[songId]) {
        this.config.audioFiles[songId] = {};
      }

      this.config.audioFiles[songId][trackId] = {
        fileName: fileName,
        filePath: `audio/${songId}/${fileName}`,
        size: file.size,
        lastModified: file.lastModified || Date.now(),
        mimeType: file.type,
        trackName: trackName
      };

      // Cache the file
      this.audioFileCache.set(trackId, file);

      await this.saveConfig();
      console.log(`Audio file saved: ${fileName} for track: ${trackName}`);
      return true;

    } catch (error) {
      console.error('Failed to save audio file:', error);
      return false;
    }
  }

  // Get audio file for a track
  async getAudioFile(trackId: string): Promise<File | null> {
    // Check cache first
    if (this.audioFileCache.has(trackId)) {
      return this.audioFileCache.get(trackId)!;
    }

    // Find file info in config
    let fileInfo: any = null;
    let songId: string | null = null;

    for (const [sId, tracks] of Object.entries(this.config.audioFiles)) {
      if (tracks[trackId]) {
        fileInfo = tracks[trackId];
        songId = sId;
        break;
      }
    }

    if (!fileInfo || !songId || !this.audioDir) {
      console.warn(`No file info found for track: ${trackId}`);
      return null;
    }

    try {
      // Load file from local directory
      const songDir = await this.audioDir.getDirectoryHandle(songId);
      const fileHandle = await songDir.getFileHandle(fileInfo.fileName);
      const file = await fileHandle.getFile();

      // Cache the file
      this.audioFileCache.set(trackId, file);

      console.log(`Loaded audio file: ${fileInfo.fileName} for track: ${trackId}`);
      return file;

    } catch (error) {
      console.error(`Failed to load audio file for track ${trackId}:`, error);
      return null;
    }
  }

  // Save song data
  async saveSong(songId: string, songData: any): Promise<void> {
    this.config.songs[songId] = {
      ...songData,
      lastModified: Date.now()
    };
    await this.saveConfig();
  }

  // Get song data
  getSong(songId: string): any {
    return this.config.songs[songId] || null;
  }

  // Get all songs
  getAllSongs(): any[] {
    return Object.entries(this.config.songs).map(([id, data]) => ({
      id,
      ...data
    }));
  }

  // Delete song and all associated files
  async deleteSong(songId: string): Promise<boolean> {
    try {
      // Delete audio files directory
      if (this.audioDir) {
        try {
          const songDir = await this.audioDir.getDirectoryHandle(songId);
          await this.audioDir.removeEntry(songId, { recursive: true });
          console.log(`Deleted audio directory for song: ${songId}`);
        } catch (error) {
          console.log('Audio directory not found or already deleted');
        }
      }

      // Delete waveform file
      if (this.waveformDir && this.config.waveforms[songId]) {
        try {
          await this.waveformDir.removeEntry(`${songId}.json`);
          console.log(`Deleted waveform for song: ${songId}`);
        } catch (error) {
          console.log('Waveform file not found or already deleted');
        }
      }

      // Remove from config
      delete this.config.songs[songId];
      delete this.config.audioFiles[songId];
      delete this.config.waveforms[songId];

      // Clear cache
      if (this.config.audioFiles[songId]) {
        for (const trackId of Object.keys(this.config.audioFiles[songId])) {
          this.audioFileCache.delete(trackId);
        }
      }

      await this.saveConfig();
      console.log(`Song deleted successfully: ${songId}`);
      return true;

    } catch (error) {
      console.error('Failed to delete song:', error);
      return false;
    }
  }

  // Save waveform data
  async saveWaveform(songId: string, waveformData: number[]): Promise<void> {
    if (!this.waveformDir) return;

    try {
      const fileHandle = await this.waveformDir.getFileHandle(`${songId}.json`, { create: true });
      const writable = await fileHandle.createWritable();
      await writable.write(JSON.stringify(waveformData));
      await writable.close();

      this.config.waveforms[songId] = {
        filePath: `waveforms/${songId}.json`,
        lastGenerated: Date.now()
      };

      await this.saveConfig();
      console.log(`Waveform saved for song: ${songId}`);
    } catch (error) {
      console.error('Failed to save waveform:', error);
    }
  }

  // Load waveform data
  async getWaveform(songId: string): Promise<number[] | null> {
    if (!this.waveformDir || !this.config.waveforms[songId]) {
      return null;
    }

    try {
      const fileHandle = await this.waveformDir.getFileHandle(`${songId}.json`);
      const file = await fileHandle.getFile();
      const text = await file.text();
      return JSON.parse(text);
    } catch (error) {
      console.error('Failed to load waveform:', error);
      return null;
    }
  }

  // Get file info for a track
  getTrackFileInfo(trackId: string): any {
    for (const [songId, tracks] of Object.entries(this.config.audioFiles)) {
      if (tracks[trackId]) {
        return tracks[trackId];
      }
    }
    return null;
  }

  // Check if file system is ready
  isReady(): boolean {
    return !!(this.projectDir && this.audioDir && this.waveformDir && this.configFile);
  }

  // Get project status
  getStatus(): string {
    if (!this.isReady()) return 'Not initialized';

    const songCount = Object.keys(this.config.songs).length;
    const trackCount = Object.values(this.config.audioFiles)
      .reduce((total, tracks) => total + Object.keys(tracks).length, 0);

    return `Ready - ${songCount} songs, ${trackCount} tracks`;
  }
}

