      console.error('Failed to add mute region:', error);
      return null;
    }
  }

  static async updateMuteRegion(userEmail: string, songId: string, trackId: string, regionId: string, updates: Partial<MuteRegion>): Promise<MuteRegion | null> {
    console.log(`ğŸ”„ LocalSongStorage.updateMuteRegion: Updating region ${regionId} on track ${trackId}`);
    try {
      // Import dynamically to avoid circular dependency
      const { LocalSongStorageDB } = await import('./local-song-storage-db');
      const updatedRegion = await LocalSongStorageDB.updateMuteRegion(userEmail, songId, trackId, regionId, updates);
      return updatedRegion;
    } catch (error) {
      console.error('Failed to update mute region:', error);
      return null;
    }
  }

  static async deleteMuteRegion(userEmail: string, songId: string, trackId: string, regionId: string): Promise<boolean> {
    console.log(`ğŸ—‘ï¸ LocalSongStorage.deleteMuteRegion: Deleting region ${regionId} from track ${trackId}`);
    try {
      // Import dynamically to avoid circular dependency
      const { LocalSongStorageDB } = await import('./local-song-storage-db');
      const success = await LocalSongStorageDB.deleteMuteRegion(userEmail, songId, trackId, regionId);
      return success;
    } catch (error) {
      console.error('Failed to delete mute region:', error);
      return false;
    }
  }

  static async getMuteRegions(userEmail: string, songId: string, trackId: string): Promise<MuteRegion[]> {
    console.log(`ğŸ” LocalSongStorage.getMuteRegions: Getting regions for track ${trackId}`);
    try {
      // Import dynamically to avoid circular dependency
      const { LocalSongStorageDB } = await import('./local-song-storage-db');
      const regions = await LocalSongStorageDB.getMuteRegions(userEmail, songId, trackId);
      console.log(`âœ… LocalSongStorage.getMuteRegions: Found ${regions.length} regions for track ${trackId}`);
      return regions;
    } catch (error) {
      console.error('Failed to get mute regions:', error);
      return [];
    }
  }

  static async clearAllMuteRegions(userEmail: string, songId: string, trackId: string): Promise<boolean> {
    console.log(`ğŸ§¹ LocalSongStorage.clearAllMuteRegions: Clearing all regions for track ${trackId}`);
    try {
      // Import dynamically to avoid circular dependency
      const { LocalSongStorageDB } = await import('./local-song-storage-db');
      const db = await LocalSongStorageDB.getDB(userEmail);
      const success = await db.deleteAllMuteRegions(trackId);
      console.log(`âœ… LocalSongStorage.clearAllMuteRegions: All regions cleared for track ${trackId}`);
      return success;
    } catch (error) {
      console.error('Failed to clear all mute regions:', error);
      return false;
    }
  }

  static async deleteAllData(userEmail: string): Promise<boolean> {
    console.log(`ğŸ—‘ï¸ STARTING COMPLETE DATA DELETION FOR USER: ${userEmail}`);

    try {
      // Import all required modules
      const [
        { BrowserFileSystem },
        { AudioFileStorage },
        { IndexedDBStorage },
        { LocalSongStorageDB }
      ] = await Promise.all([
        import('./browser-file-system'),
        import('./audio-file-storage'),
        import('./indexed-db-storage'),
        import('./local-song-storage-db')
      ]);

      // Step 1: Get all songs to log count (not used for deletion)
      const allSongs = await this.getAllSongs(userEmail);
      console.log(`Found ${allSongs.length} songs to delete`);

      // Step 2: Clear BrowserFileSystem database (MusicAppStorage)
      const browserFS = BrowserFileSystem.getInstance(userEmail);
      await browserFS.waitForInitialization(); // Ensure it's ready
      await browserFS.clearAllData(); // This handles everything internally
      console.log('âœ… Cleared BrowserFileSystem');

      // Step 3: Clear AudioFileStorage (music-app-audio-files)
      const audioStorage = AudioFileStorage.getInstance(userEmail);
      await audioStorage.clearAllAudioFiles();
      console.log('âœ… Cleared AudioFileStorage');

      // Step 4: Clear LocalSongStorageDB (MusicAppStorage - songs/tracks/muteRegions)
      // This internally also clears IndexedDBStorage, so we don't need Step 4 separately
      await LocalSongStorageDB.clearAllData(userEmail);
      console.log('âœ… Cleared LocalSongStorageDB and IndexedDB');

      // Step 5: Clear localStorage keys related to this user
      const keysToDelete: string[] = [];
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key && (
          key.includes(userEmail) ||
          key.includes('waveform') ||
          key.includes('mute_regions') ||
          key.includes('lpp_songs') ||
          key.includes('audio_levels')
        )) {
          keysToDelete.push(key);
        }
      }

      keysToDelete.forEach(key => {
        localStorage.removeItem(key);
        console.log(`Removed localStorage: ${key}`);
      });
      console.log(`âœ… Cleared ${keysToDelete.length} localStorage items`);

      // Step 6: Revoke all blob URLs to free memory
      // This is handled by BrowserFileSystem.clearAllData() and AudioFileStorage.clearAllAudioFiles()
      console.log('âœ… Blob URLs revoked by storage cleanup');

      console.log('ğŸ‰ COMPLETE DATA DELETION SUCCESSFUL');
      console.log('All databases have been cleared while maintaining structure');
      return true;

    } catch (error) {
      console.error('âŒ Failed to delete all data:', error);
      return false;
    }
  }
}

