import { useState, useEffect, useCallback } from "react";
import { useLocation } from "wouter";
import CompactTransportControls from "@/components/compact-transport-controls";
import AudioMixer from "@/components/audio-mixer";
import { LyricsDisplay } from "@/components/lyrics-display";
import { LyricsControls } from "@/components/lyrics-controls";
import SongSelector from "@/components/song-selector";
import StatusBar from "@/components/status-bar";
import TrackManager from "@/components/track-manager-clean";
import StemSplitter from "@/components/stem-splitter";
import ProfessionalStereoVUMeter from "@/components/professional-stereo-vu-meter";
import { WaveformVisualizer } from "@/components/waveform-visualizer";

import { useAudioEngine } from "@/hooks/use-audio-engine";

import { useKeyboardShortcuts } from "@/hooks/use-keyboard-shortcuts";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Settings, Music, Menu, Plus, Edit, Play, Pause, Clock, Minus, Trash2, FileAudio, LogOut, User, Crown, Maximize, Minimize, Activity, Zap, X, Target, Send, Search, ExternalLink, Loader2, Volume2, Download, Upload, FolderOpen, Cast, Headphones } from "lucide-react";
import { Slider } from "@/components/ui/slider";
import { useToast } from "@/hooks/use-toast";
import { type UserType } from "@/hooks/useLocalAuth";
import { LocalSongStorageDB as LocalSongStorage, type LocalSong } from "@/lib/local-song-storage-db";
import type { SongWithTracks } from "@shared/schema";
import { useRef } from "react";
import { SimpleBackupManager } from "@/lib/simple-backup-manager";
import { useBroadcast } from "@/hooks/useBroadcast";
import { MidiDeviceManager } from "@/components/midi-device-manager";
import { useMidi } from "@/contexts/MidiProvider";

interface PerformanceProps {
  userType: UserType;
  userEmail?: string;
  logout?: () => void;
}

export default function Performance({ userType, userEmail, logout }: PerformanceProps) {

  const [selectedSongId, setSelectedSongId] = useState<string | null>(null);
  const [, setLocation] = useLocation();
  const [latency, setLatency] = useState(2.1);
  const [isTrackManagerOpen, setIsTrackManagerOpen] = useState(false);
  const [isAddSongOpen, setIsAddSongOpen] = useState(false);
  const [songTitle, setSongTitle] = useState("");
  const [songArtist, setSongArtist] = useState("");
  const [isEditLyricsOpen, setIsEditLyricsOpen] = useState(false);
  const [lyricsText, setLyricsText] = useState("");
  const [isDeleteSongOpen, setIsDeleteSongOpen] = useState(false);
  const [currentLyricsTab, setCurrentLyricsTab] = useState("lyrics");
  const [allSongs, setAllSongs] = useState<LocalSong[]>([]);
  const [selectedSong, setSelectedSong] = useState<LocalSong | null>(null);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isDeviceManagerOpen, setIsDeviceManagerOpen] = useState(false);
  const [isSearchingLyrics, setIsSearchingLyrics] = useState(false);
  const [searchResult, setSearchResult] = useState<any>(null);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState("");
  const [exportController, setExportController] = useState<AbortController | null>(null);
  const [isImporting, setIsImporting] = useState(false);
  const [importProgress, setImportProgress] = useState(0);
  const [importStatus, setImportStatus] = useState("");
  const [isExportDialogOpen, setIsExportDialogOpen] = useState(false);
  const [isMidiListening, setIsMidiListening] = useState(false);
  const [exportFilename, setExportFilename] = useState("");
  const lyricsTextareaRef = useRef<HTMLTextAreaElement>(null);

  // MIDI integration
  const { sendMidiCommand, parseMidiCommand, connectedDevices, registerMessageListener, unregisterMessageListener } = useMidi();

  // Optional broadcast integration - completely isolated
  const { isHost, isViewer, broadcastState, sendPerformanceState, currentRoom } = useBroadcast();

  // Check if viewer has broadcast data but no local song
  const showBroadcastViewerMode = isViewer && broadcastState && broadcastState.lyrics && !selectedSong;

  // Debug broadcast state changes
  const [debugMessage, setDebugMessage] = useState('');
  useEffect(() => {
    if (broadcastState) {
      console.log('ğŸ“º Performance page received broadcast state:', broadcastState);
      setDebugMessage(`ğŸ“º Received: ${broadcastState.songTitle || 'Unknown'} - Playing: ${broadcastState.isPlaying ? 'Yes' : 'No'} - Position: ${Math.round(broadcastState.position)}s`);

      // Clear debug message after 3 seconds
      setTimeout(() => setDebugMessage(''), 3000);
    }
  }, [broadcastState]);
  const fileInputRef = useRef<HTMLInputElement>(null);


  const { toast } = useToast();

  // Listen for fullscreen changes
  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    return () => {
      document.removeEventListener('fullscreenchange', handleFullscreenChange);
    };
  }, []);








  // Show export dialog with filename input
  const handleExportData = () => {
    if (!userEmail) {
      toast({
        title: "Export Failed",
        description: "Please log in to export your data",
        variant: "destructive",
      });
      return;
    }

    // Generate default filename suggestion
    const timestamp = new Date().toISOString().slice(0, 10); // YYYY-MM-DD
    const userPrefix = userEmail.split('@')[0].replace(/[^a-zA-Z0-9]/g, '');
    const defaultName = `stagetracker-backup-${userPrefix}-${timestamp}`;
    setExportFilename(defaultName);
    setIsExportDialogOpen(true);
  };

  // Actually perform the export with custom filename
  const performExport = async () => {
    if (!userEmail || !exportFilename.trim()) return;

    try {
      setIsExporting(true);
      setExportProgress(0);
      setExportStatus("Starting export...");
      setIsExportDialogOpen(false);

      // Create AbortController for cancellation
      const controller = new AbortController();
      setExportController(controller);

      const backupManager = SimpleBackupManager.getInstance();

      // Create progress callback
      const onProgress = (progress: number, status: string) => {
        setExportProgress(progress);
        setExportStatus(status);
      };

      const zipBlob = await backupManager.exportDatabase(userEmail, onProgress);

      // Create download with explicit MIME type for Android compatibility
      const zipBlobWithMime = new Blob([zipBlob], { type: 'application/zip' });
      const url = URL.createObjectURL(zipBlobWithMime);
      const link = document.createElement('a');
      link.href = url;

      // Use custom filename, ensure .zip extension
      let filename = exportFilename.trim();
      if (!filename.toLowerCase().endsWith('.zip')) {
        filename += '.zip';
      }
      link.download = filename;

      // Better mobile download handling
      link.style.display = 'none';
      link.setAttribute('rel', 'noopener');
      link.setAttribute('target', '_blank');

      document.body.appendChild(link);

      // Use setTimeout for better mobile compatibility
      setTimeout(() => {
        link.click();
        setTimeout(() => {
          document.body.removeChild(link);
          URL.revokeObjectURL(url);
        }, 100);
      }, 10);

      toast({
        title: "Export Complete",
        description: `Your music library has been exported as "${filename}"`,
      });
    } catch (error) {
      console.error('Export failed:', error);

      // Handle cancellation vs real errors differently
      if (error instanceof DOMException && error.name === 'AbortError') {
        toast({
          title: "Export Cancelled",
          description: "Library export was cancelled by user",
        });
      } else {
        toast({
          title: "Export Failed",
          description: error instanceof Error ? error.message : "Failed to export data",
          variant: "destructive",
        });
      }
    } finally {
      setIsExporting(false);
      setExportProgress(0);
      setExportStatus("");
      setExportController(null);
    }
  };

  // Handle MIDI listen toggle
  const handleMidiListen = () => {
    if (isMidiListening) {
      // Stop listening
      setIsMidiListening(false);
      if (unregisterMessageListener) {
        unregisterMessageListener('lyrics-editor');
      }
      toast({
        title: "MIDI Listen Off",
        description: "Stopped listening for MIDI messages",
      });
    } else {
      // Start listening
      if (connectedDevices.filter(d => d.type === 'input').length === 0) {
        toast({
          title: "No MIDI Input Devices",
          description: "Connect a MIDI input device first",
          variant: "destructive",
        });
        return;
      }

      setIsMidiListening(true);

      // Register message listener for incoming MIDI
      if (registerMessageListener) {
        registerMessageListener('lyrics-editor', (message: any) => {
          // Format the MIDI message into our bracket notation
          const formattedCommand = formatMidiMessage(message);
          if (formattedCommand) {
            insertMidiCommandAtCursor(formattedCommand);
          }
        });
      }

      toast({
        title: "MIDI Listen On",
        description: "Listening for MIDI messages. Play something on your controller!",
      });
    }
  };

  // Format incoming MIDI message to bracket notation
  const formatMidiMessage = (message: any): string | null => {
    if (!message.data || message.data.length < 2) return null;

    const [status, data1, data2] = message.data;
    const channel = (status & 0x0F) + 1; // MIDI channels are 1-16
    const command = status & 0xF0;

    switch (command) {
      case 0xC0: // Program Change
        return `[[PC:${data1}:${channel}]]`;
      case 0xB0: // Control Change
        return `[[CC:${data1}:${data2 || 0}:${channel}]]`;
      case 0x90: // Note On
        if (data2 > 0) { // Velocity > 0 means note on
          return `[[NOTE:${data1}:${data2}:${channel}]]`;
        }
        break;
      case 0x80: // Note Off - we'll ignore these for lyrics
        break;
    }

    return null;
  };

  // Insert MIDI command at cursor position in lyrics textarea
  const insertMidiCommandAtCursor = (command: string) => {
    const textarea = document.getElementById('lyrics') as HTMLTextAreaElement;
    if (textarea) {
      // Get current cursor position
      const startPos = textarea.selectionStart;
      const endPos = textarea.selectionEnd;

      // Get current textarea value
      const currentValue = textarea.value;

      // Insert command, replacing any selected text
      const newValue = currentValue.substring(0, startPos) + command + currentValue.substring(endPos);

      // Update both DOM and React state
      textarea.value = newValue;
      setLyricsText(newValue);

      // Position cursor after the inserted command
      const newCursorPos = startPos + command.length;
      textarea.setSelectionRange(newCursorPos, newCursorPos);
      textarea.focus();
    }
  };

  // Cleanup MIDI listener when lyrics dialog closes
  useEffect(() => {
    if (!isEditLyricsOpen && isMidiListening) {
      setIsMidiListening(false);
      if (unregisterMessageListener) {
        unregisterMessageListener('lyrics-editor');
      }
    }
  }, [isEditLyricsOpen, isMidiListening, unregisterMessageListener]);

  // Cancel export operation
  const handleCancelExport = () => {
    if (exportController) {
      exportController.abort();
    }
  };

  // Import data from zip file
  const handleImportData = () => {
    if (!userEmail) {
      toast({
        title: "Import Failed",
        description: "Please log in to import data",
        variant: "destructive",
      });
      return;
    }

    // Trigger file picker
    fileInputRef.current?.click();
  };

  // Handle file selection for import
  const handleFileSelected = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file || !userEmail) return;

    try {
      setIsImporting(true);
      setImportProgress(0);
      setImportStatus("Reading backup file...");

      const backupManager = SimpleBackupManager.getInstance();

      // Create progress callback
      const onProgress = (progress: number, status: string) => {
        setImportProgress(progress);
        setImportStatus(status);
      };

      await backupManager.importDatabase(file, userEmail, onProgress);

      // Don't try to refresh songs - the page will reload automatically
      // The SimpleBackupManager will reload the page after successful import

      toast({
        title: "Import Complete",
        description: "Your music library has been imported successfully. Page will refresh...",
      });
    } catch (error) {
      console.error('Import failed:', error);
      toast({
        title: "Import Failed",
        description: error instanceof Error ? error.message : "Failed to import data",
        variant: "destructive",
      });
    } finally {
      setIsImporting(false);
      setImportProgress(0);
      setImportStatus("");
      // Clear the input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };


  // Instant audio engine (now with zero decode delays)
  const audioEngine = useAudioEngine({ 
    song: selectedSong ? { ...selectedSong, userId: userEmail || '' } as SongWithTracks : undefined,
    userEmail: userEmail,
    onDurationUpdated: async (songId: string, newDuration: number) => {
      if (selectedSong && selectedSong.id === songId && userEmail) {
        await LocalSongStorage.updateSong(userEmail, songId, { duration: newDuration });
      }
    }
  });

  const {
    isPlaying,
    currentTime,
    duration,
    play,
    pause,
    stop,
    seek,
    isLoadingTracks,
    masterVolume,
    updateMasterVolume,
    updateTrackVolume,
    updateTrackBalance,
    updateTrackMute,
    updateTrackSolo,
    // Pitch and speed control removed
    isAudioEngineOnline,
    masterStereoLevels,
    audioLevels
  } = audioEngine;

  // Create toggle functions for track manager compatibility
  const toggleTrackMute = useCallback((trackId: string) => {
    updateTrackMute(trackId);
  }, [updateTrackMute]);

  const toggleTrackSolo = useCallback((trackId: string) => {
    updateTrackSolo(trackId);
  }, [updateTrackSolo]);


  // Keyboard shortcuts
  useKeyboardShortcuts({
    onPlay: play,
    onPause: pause,
    onStop: stop,
    onTogglePlayback: isPlaying ? pause : play,
    onTrackMute: (trackId: string) => updateTrackMute(trackId),
    isPlaying
  });

  // Load all songs on mount
  useEffect(() => {
    const loadAllSongs = async () => {
      if (!userEmail) return;

      try {
        const songs = await LocalSongStorage.getAllSongs(userEmail);
        // Sort songs alphabetically by title
        const sortedSongs = songs.sort((a, b) => a.title.localeCompare(b.title));
        setAllSongs(sortedSongs);
        console.log(`ğŸ“‹ Loaded ${sortedSongs.length} songs from IndexedDB (alphabetically sorted)`);
      } catch (error) {
        console.error('âŒ Failed to load songs:', error);
        toast({
          title: "Error Loading Songs",
          description: "Failed to load songs from IndexedDB",
          variant: "destructive"
        });
      }
    };

    loadAllSongs();
  }, [userEmail, toast]);

  const refreshSongs = useCallback(async () => {
    if (!userEmail) return;

    try {
      const songs = await LocalSongStorage.getAllSongs(userEmail);
      // Sort songs alphabetically by title
      const sortedSongs = songs.sort((a, b) => {
        if (!a.title || !b.title) return 0;
        return a.title.localeCompare(b.title);
      });
      setAllSongs(sortedSongs);
    } catch (error) {
      console.error('Failed to refresh songs:', error);
    }
  }, [userEmail]);

  // Refresh only the specific song that was edited in track manager
  const refreshSongsForTrackedSong = useCallback(async (songId: string) => {
    if (!userEmail) return;

    try {
      // Get the updated song from storage
      const updatedSong = await LocalSongStorage.getSong(userEmail, songId);
      if (updatedSong) {
        // Update just this song in the list
        setAllSongs(prev => prev.map(song => 
          song.id === songId ? updatedSong : song
        ));
        console.log(`ğŸ“‹ Refreshed track count for song: ${updatedSong.title} (${updatedSong.tracks?.length || 0} tracks)`);
      }
    } catch (error) {
      console.error('Error refreshing song track count:', error);
    }
  }, [userEmail]);

  // Track database entry ID for broadcasting
  const [songEntryId, setSongEntryId] = useState<string | null>(null);

  // Select song and load its tracks
  useEffect(() => {
    const loadSelectedSong = async () => {
      if (!selectedSongId || !userEmail) return;

      // Get fresh song data from storage to avoid circular dependencies
      const allSongsFromStorage = await LocalSongStorage.getAllSongs(userEmail);
      const song = allSongsFromStorage.find(s => s.id === selectedSongId);
      if (!song) return;

      console.log(`ğŸµ Loading song: ${song.title}`);
      setSelectedSong(song);

      // If we're hosting a broadcast, upload song to database immediately on selection
      if (isHost && currentRoom?.id) {
        console.log(`ğŸ“¡ Host selected song - uploading to database for broadcast: ${song.title}`);
        console.log(`ğŸ“¡ Upload function will be called with roomId: ${currentRoom.id}`);
        uploadSongToDatabase(song, currentRoom.id);
      } else {
        console.log('ğŸ“¡ Not uploading to database:', { isHost, hasCurrentRoom: !!currentRoom?.id, roomId: currentRoom?.id });
      }
    };

    loadSelectedSong();
  }, [selectedSongId, userEmail, isHost, currentRoom?.id]);

  // Debug current values to see why upload isn't triggering
  useEffect(() => {
    console.log('ğŸ” Debug values:', { 
      selectedSongId, 
      userEmail: userEmail, 
      isHost, 
      currentRoomId: currentRoom?.id,
      hasSelectedSongId: !!selectedSongId,
      hasUserEmail: !!userEmail,
      hasCurrentRoom: !!currentRoom?.id
    });
  }, [selectedSongId, userEmail, isHost, currentRoom?.id]);

  // Upload song to database and get entry ID for broadcasting
  const uploadSongToDatabase = async (song: any, broadcastId: string) => {
    console.log('ğŸš€ uploadSongToDatabase function called!', { song: song.title, broadcastId });
    try {
      const response = await fetch(`/api/broadcast/${broadcastId}/songs`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ songs: [song] })
      });

      if (response.ok) {
        const result = await response.json();
        console.log('ğŸ” Full API response:', result);
        console.log('ğŸ” Songs array:', result.songs);
        if (result.songs && result.songs[0]) {
          console.log('ğŸ” First song object:', result.songs[0]);
        }

        const entryId = result.songs?.[0]?.id;
        if (entryId) {
          setSongEntryId(entryId);
          console.log(`âœ… Song uploaded to database with entry ID: ${entryId}`);
        } else {
          console.log('âš ï¸ Song uploaded but no entry ID returned. Full result:', JSON.stringify(result, null, 2));
        }
      }
    } catch (error) {
      console.error('âŒ Failed to upload song to database:', error);
    }
  };

  // Broadcast viewer mode: Sync with broadcaster's performance state
  useEffect(() => {
    if (!isViewer || !broadcastState) return;

    console.log('ğŸ“º Broadcast viewer mode: Syncing with broadcaster state', broadcastState);

    // Sync current song if broadcaster changed it (only if logged in)
    if (broadcastState.currentSong && broadcastState.currentSong !== selectedSongId && userEmail) {
      console.log(`ğŸ“º Broadcaster changed song to: ${broadcastState.songTitle || broadcastState.currentSong}`);
      setSelectedSongId(broadcastState.currentSong);
    }

    // Sync playback position if significant difference (only if we have local song)
    if (selectedSong && Math.abs(broadcastState.position - currentTime) > 1) {
      console.log(`ğŸ“º Syncing playback position: ${broadcastState.position}s`);
      seek(broadcastState.position);
    }

    // Sync play/pause state (only if we have local song)
    if (selectedSong && broadcastState.isPlaying !== isPlaying) {
      console.log(`ğŸ“º Syncing playback state: ${broadcastState.isPlaying ? 'playing' : 'paused'}`);
      if (broadcastState.isPlaying) {
        play();
      } else {
        pause();
      }
    }

