      // Don't actually call loadStripe yet
      toast({
        title: "Test Success",
        description: "Stripe imports work correctly",
      });
    } catch (error: any) {
      console.error('âŒ Stripe load test error:', error);
      toast({
        title: "Test Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const testActualStripeLoad = async () => {
    try {
      console.log('ğŸ”„ Testing actual Stripe initialization...');

      const { loadStripe } = await import('@stripe/stripe-js');
      const stripe = await loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY!);

      console.log('Stripe instance:', stripe);

      toast({
        title: "Stripe Load Success",
        description: "Stripe loaded without error",
      });
    } catch (error: any) {
      console.error('âŒ Actual Stripe load error:', error);
      toast({
        title: "Stripe Load Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
