import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { ArrowLeft } from 'lucide-react';
import { useLocation } from 'wouter';

export default function SubscribeTest() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const handleTestSubscription = async () => {
    try {
      console.log('ğŸ”„ Testing subscription creation...');

      const storedUser = localStorage.getItem('lpp_local_user');
      if (!storedUser) {
        console.error('âŒ No stored user found');
        return;
      }

      const userData = JSON.parse(storedUser);
      console.log('ğŸ“§ User email:', userData.email);

      const response = await fetch('/api/create-subscription', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: userData.email,
          tier: 'premium',
          priceId: 'price_premium_placeholder'
        }),
      });

      const data = await response.json();
      console.log('ğŸ“Š API Response:', data);

      if (response.ok && data.clientSecret) {
        console.log('âœ… Client secret received successfully');
        toast({
          title: "Test Successful",
          description: "Subscription API is working correctly",
        });
      } else {
        console.error('âŒ API Error:', data);
        toast({
          title: "Test Failed",
          description: data.message || 'API call failed',
          variant: "destructive",
        });
      }
    } catch (error: any) {
      console.error('âŒ Test error:', error);
      toast({
        title: "Test Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <div className="max-w-md mx-auto p-6">
      <div className="mb-6">
        <Button variant="ghost" onClick={() => setLocation('/')} data-testid="button-back-home">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Home
        </Button>
      </div>

      <Card>
        <CardHeader className="text-center">
          <CardTitle>Subscription Test Page</CardTitle>
          <p className="text-gray-600">Test subscription API without Stripe Elements</p>
        </CardHeader>

        <CardContent className="space-y-4">
          <Button 
            onClick={handleTestSubscription}
            className="w-full"
            data-testid="button-test-subscription"
          >
            Test Subscription API
          </Button>

          <div className="text-sm text-gray-600">
            This page tests the subscription creation without loading Stripe Elements to isolate any errors.
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

mobile-app/src/components/BluetoothDevic.content:
import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Alert,
  Modal,
} from 'react-native';
import { useLocalAuth } from '../hooks/useLocalAuth';

interface BluetoothDevice {
  id: string;
  name: string;
  manufacturer?: string;
  isMIDIDevice: boolean;
  isConnected: boolean;
  signalStrength?: number;
  deviceType: string;
  lastSeen?: number;
}

interface BluetoothDevicesManagerProps {
  isVisible: boolean;
  onClose: () => void;
  onConnectedDevicesChange?: (devices: BluetoothDevice[]) => void;
}

export function BluetoothDevicesManager({ 
  isVisible, 
  onClose, 
  onConnectedDevicesChange 
}: BluetoothDevicesManagerProps) {
  const { user } = useLocalAuth();
  const isProfessional = user?.userType === 'professional';

  const [devices, setDevices] = useState<BluetoothDevice[]>([]);
  const [connectedDevices, setConnectedDevices] = useState<BluetoothDevice[]>([]);
  const [isScanning, setIsScanning] = useState(false);
  const [scanType, setScanType] = useState<'quick' | 'deep'>('quick');

  // Mock Bluetooth devices for development/demo
  const mockDevices: BluetoothDevice[] = [
    {
      id: 'bt-1',
      name: 'WIDI Jack',
      manufacturer: 'CME',
      isMIDIDevice: true,
      isConnected: false,
      signalStrength: -45,
      deviceType: 'MIDI Adapter',
      lastSeen: Date.now() - 5000
    },
    {
      id: 'bt-2',
      name: 'TC-Helicon VoiceLive',
      manufacturer: 'TC-Helicon',
      isMIDIDevice: true,
      isConnected: false,
      signalStrength: -60,
      deviceType: 'Audio Processor',
      lastSeen: Date.now() - 30000
    },
    {
      id: 'bt-3',
      name: 'BLE MIDI Controller',
      manufacturer: 'Korg',
      isMIDIDevice: true,
      isConnected: true,
      signalStrength: -35,
      deviceType: 'MIDI Controller',
      lastSeen: Date.now() - 1000
    },
    {
      id: 'bt-4',
      name: 'iPhone 14',
      manufacturer: 'Apple',
      isMIDIDevice: false,
      isConnected: false,
      signalStrength: -50,
      deviceType: 'Smartphone',
      lastSeen: Date.now() - 15000
    },
    {
      id: 'bt-5',
      name: 'AirPods Pro',
      manufacturer: 'Apple',
      isMIDIDevice: false,
      isConnected: false,
      signalStrength: -40,
      deviceType: 'Headphones',
      lastSeen: Date.now() - 8000
    }
  ];

  useEffect(() => {
    if (isVisible && isProfessional) {
      scanForDevices();
    } else if (isVisible && !isProfessional) {
      Alert.alert(
        'Professional Subscription Required',
        'Bluetooth MIDI features are only available for Professional subscribers',
        [{ text: 'OK', onPress: onClose }]
      );
    }
  }, [isVisible, isProfessional]);

  const scanForDevices = async () => {
    setIsScanning(true);
    console.log(`ğŸ” Starting ${scanType} Bluetooth scan...`);

    try {
      // Simulate scanning delay
      const scanDuration = scanType === 'deep' ? 3000 : 1500;
      await new Promise(resolve => setTimeout(resolve, scanDuration));

      // In a real implementation, this would use React Native Bluetooth libraries
      // For now, we'll use mock devices
      let foundDevices = [...mockDevices];

      if (scanType === 'quick') {
        // Quick scan finds fewer devices
        foundDevices = foundDevices.filter(device => 
          device.signalStrength! > -55 || device.isMIDIDevice
        );
      }

      // Sort MIDI devices first, then by signal strength
      foundDevices.sort((a, b) => {
        if (a.isMIDIDevice && !b.isMIDIDevice) return -1;
        if (!a.isMIDIDevice && b.isMIDIDevice) return 1;
        return (b.signalStrength || -100) - (a.signalStrength || -100);
      });

      setDevices(foundDevices);

      const connected = foundDevices.filter(device => device.isConnected);
      setConnectedDevices(connected);
      onConnectedDevicesChange?.(connected);

      console.log(`âœ… Found ${foundDevices.length} Bluetooth devices (${connected.length} connected)`);
    } catch (error) {
      console.error('âŒ Failed to scan for Bluetooth devices:', error);
      Alert.alert('Error', 'Failed to scan for Bluetooth devices');
    } finally {
      setIsScanning(false);
    }
  };

  const connectDevice = async (deviceId: string) => {
    const device = devices.find(d => d.id === deviceId);
    if (!device) return;

    try {
      console.log(`ğŸ”Œ Connecting to Bluetooth device: ${device.name}`);

      // Simulate connection
      const updatedDevices = devices.map(d => 
        d.id === deviceId ? { ...d, isConnected: true } : d
      );
      setDevices(updatedDevices);

      const connected = updatedDevices.filter(d => d.isConnected);
      setConnectedDevices(connected);
      onConnectedDevicesChange?.(connected);

      Alert.alert('Success', `Connected to ${device.name}`);
    } catch (error) {
      console.error('âŒ Failed to connect to device:', error);
      Alert.alert('Error', 'Failed to connect to device');
    }
  };

  const disconnectDevice = async (deviceId: string) => {
    const device = devices.find(d => d.id === deviceId);
    if (!device) return;

    try {
      console.log(`ğŸ”Œ Disconnecting from Bluetooth device: ${device.name}`);

      const updatedDevices = devices.map(d => 
        d.id === deviceId ? { ...d, isConnected: false } : d
      );
      setDevices(updatedDevices);

      const connected = updatedDevices.filter(d => d.isConnected);
      setConnectedDevices(connected);
      onConnectedDevicesChange?.(connected);

      Alert.alert('Success', `Disconnected from ${device.name}`);
    } catch (error) {
      console.error('âŒ Failed to disconnect from device:', error);
      Alert.alert('Error', 'Failed to disconnect from device');
    }
  };

  const removeDevice = async (deviceId: string) => {
    const device = devices.find(d => d.id === deviceId);
    if (!device) return;

    Alert.alert(
      'Remove Device',
      `Are you sure you want to remove ${device.name}?`,
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Remove',
          style: 'destructive',
          onPress: () => {
            const updatedDevices = devices.filter(d => d.id !== deviceId);
            setDevices(updatedDevices);

            const connected = updatedDevices.filter(d => d.isConnected);
            setConnectedDevices(connected);
            onConnectedDevicesChange?.(connected);
          }
        }
      ]
    );
  };

  const getSignalIcon = (strength?: number) => {
    if (!strength) return 'ğŸ“¶';
    if (strength > -40) return 'ğŸ“¶';
    if (strength > -60) return 'ğŸ“¶';
    if (strength > -80) return 'ğŸ“¶';
    return 'ğŸ“¶';
  };

  const getDeviceIcon = (device: BluetoothDevice) => {
    if (device.isMIDIDevice) return 'ğŸ¹';

    switch (device.deviceType.toLowerCase()) {
      case 'smartphone':
        return 'ğŸ“±';
      case 'headphones':
        return 'ğŸ§';
      case 'speaker':
        return 'ğŸ”Š';
      default:
        return 'ğŸ“Ÿ';
    }
  };

  const midiDevices = devices.filter(device => device.isMIDIDevice);
  const otherDevices = devices.filter(device => !device.isMIDIDevice);

  return (
    <Modal visible={isVisible} animationType="slide" presentationStyle="pageSheet">
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.title}>Bluetooth Devices</Text>
          <TouchableOpacity style={styles.closeButton} onPress={onClose}>
            <Text style={styles.closeButtonText}>âœ•</Text>
          </TouchableOpacity>
        </View>

        <ScrollView style={styles.content}>
          {/* Scan Controls */}
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Device Scanning</Text>

            <View style={styles.scanTypeContainer}>
              <TouchableOpacity
                style={[
                  styles.scanTypeButton,
                  scanType === 'quick' && styles.scanTypeButtonActive
                ]}
                onPress={() => setScanType('quick')}
              >
                <Text style={[
                  styles.scanTypeText,
                  scanType === 'quick' && styles.scanTypeTextActive
                ]}>
                  Quick Scan
                </Text>
              </TouchableOpacity>

              <TouchableOpacity
                style={[
                  styles.scanTypeButton,
                  scanType === 'deep' && styles.scanTypeButtonActive
                ]}
                onPress={() => setScanType('deep')}
              >
                <Text style={[
                  styles.scanTypeText,
                  scanType === 'deep' && styles.scanTypeTextActive
                ]}>
                  Deep Scan
                </Text>
              </TouchableOpacity>
            </View>

            <TouchableOpacity 
              style={[styles.scanButton, isScanning && styles.scanButtonDisabled]}
              onPress={scanForDevices}
              disabled={isScanning}
            >
              <Text style={styles.scanButtonText}>
                {isScanning ? `${scanType === 'deep' ? 'Deep ' : ''}Scanning...` : 'Start Scan'}
              </Text>
            </TouchableOpacity>
          </View>

          {/* MIDI Devices */}
          {midiDevices.length > 0 && (
            <View style={styles.section}>
              <Text style={styles.sectionTitle}>
                ğŸ¹ MIDI Devices ({midiDevices.length})
              </Text>

              {midiDevices.map((device) => (
                <View key={device.id} style={styles.deviceCard}>
                  <View style={styles.deviceHeader}>
                    <Text style={styles.deviceIcon}>{getDeviceIcon(device)}</Text>
                    <View style={styles.deviceInfo}>
                      <Text style={styles.deviceName}>{device.name}</Text>
                      <Text style={styles.deviceDetails}>
                        {device.manufacturer} â€¢ {device.deviceType}
                      </Text>
                    </View>
                    <Text style={styles.signalIcon}>{getSignalIcon(device.signalStrength)}</Text>
                  </View>

                  <View style={styles.deviceActions}>
                    <TouchableOpacity
                      style={[
                        styles.deviceButton,
                        device.isConnected ? styles.disconnectButton : styles.connectButton
                      ]}
                      onPress={() => device.isConnected 
                        ? disconnectDevice(device.id)
                        : connectDevice(device.id)
                      }
                    >
                      <Text style={styles.deviceButtonText}>
                        {device.isConnected ? 'Disconnect' : 'Connect'}
                      </Text>
                    </TouchableOpacity>

                    <TouchableOpacity
                      style={styles.removeButton}
                      onPress={() => removeDevice(device.id)}
                    >
                      <Text style={styles.removeButtonText}>Remove</Text>
                    </TouchableOpacity>
                  </View>
                </View>
              ))}
            </View>
          )}

          {/* Other Devices */}
          {otherDevices.length > 0 && (
            <View style={styles.section}>
              <Text style={styles.sectionTitle}>
                ğŸ“Ÿ Other Devices ({otherDevices.length})
              </Text>

              {otherDevices.map((device) => (
                <View key={device.id} style={styles.deviceCard}>
                  <View style={styles.deviceHeader}>
                    <Text style={styles.deviceIcon}>{getDeviceIcon(device)}</Text>
                    <View style={styles.deviceInfo}>
                      <Text style={styles.deviceName}>{device.name}</Text>
                      <Text style={styles.deviceDetails}>
                        {device.manufacturer} â€¢ {device.deviceType}
                      </Text>
                    </View>
                    <Text style={styles.signalIcon}>{getSignalIcon(device.signalStrength)}</Text>
                  </View>

                  <View style={styles.deviceActions}>
                    <TouchableOpacity
                      style={styles.removeButton}
                      onPress={() => removeDevice(device.id)}
                    >
                      <Text style={styles.removeButtonText}>Remove</Text>
                    </TouchableOpacity>
                  </View>
                </View>
              ))}
            </View>
          )}

          {devices.length === 0 && !isScanning && (
            <View style={styles.noDevicesContainer}>
              <Text style={styles.noDevicesText}>
                No devices found. Try scanning for devices.
              </Text>
            </View>
          )}
        </ScrollView>
      </View>
    </Modal>
  );
}

