import React, { useState, useCallback } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { Volume2, VolumeX, Play, Pause, RotateCcw } from "lucide-react";
import { StreamingAudioEngine } from "@/lib/streaming-audio-engine";

interface StreamingTrackManagerProps {
  song: any;
  trackFiles: any[];
  onClose: () => void;
}

export default function StreamingTrackManager({ song, trackFiles, onClose }: StreamingTrackManagerProps) {
  const [streamingEngine] = useState(() => new StreamingAudioEngine());
  const [isLoading, setIsLoading] = useState(false);
  const [isStreamingReady, setIsStreamingReady] = useState(false);
  const [engineState, setEngineState] = useState(streamingEngine.getState());

  // Subscribe to streaming engine updates
  React.useEffect(() => {
    const unsubscribe = streamingEngine.subscribe(() => {
      setEngineState(streamingEngine.getState());
    });

    return () => {
      unsubscribe();
      streamingEngine.dispose();
    };
  }, [streamingEngine]);

  const handleStreamingLoad = useCallback(async () => {
    if (trackFiles.length === 0) return;

    setIsLoading(true);
    console.log(`ğŸš€ Loading ${trackFiles.length} tracks for streaming (instant)`);

    try {
      // Convert track files to streaming format
      const trackData = trackFiles.map(file => ({
        id: file.id,
        name: file.name,
        url: file.url || '#', // Use actual URLs in production
      }));

      await streamingEngine.loadTracks(trackData);
      setIsStreamingReady(true);
      console.log(`âœ… Streaming ready: ${trackFiles.length} tracks loaded instantly`);
    } catch (error) {
      console.error('Streaming load failed:', error);
    } finally {
      setIsLoading(false);
    }
  }, [trackFiles, streamingEngine]);

  const handleStreamingPlay = useCallback(async () => {
    if (!isStreamingReady) return;

    console.log(`â–¶ï¸ Starting streaming playback`);
    await streamingEngine.play();
  }, [streamingEngine, isStreamingReady]);

  const handleStreamingPause = useCallback(() => {
    console.log(`â¸ï¸ Pausing streaming playback`);
    streamingEngine.pause();
  }, [streamingEngine]);

  const handleStreamingStop = useCallback(() => {
    console.log(`â¹ï¸ Stopping streaming playback`);
    streamingEngine.stop();
  }, [streamingEngine]);

  const handleTrackVolumeChange = useCallback((trackId: string, volume: number) => {
    streamingEngine.setTrackVolume(trackId, volume / 100);
  }, [streamingEngine]);

  const handleTrackMute = useCallback((trackId: string) => {
    streamingEngine.toggleTrackMute(trackId);
  }, [streamingEngine]);

  const handleMasterVolumeChange = useCallback((volume: number) => {
    streamingEngine.setMasterVolume(volume / 100);
  }, [streamingEngine]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white">{song?.title || 'Unknown Song'}</h2>
          <p className="text-gray-400">{song?.artist || 'Unknown Artist'}</p>
          <Badge variant="secondary" className="mt-2">
            ğŸš€ Streaming Mode - Zero Load Time
          </Badge>
        </div>
        <Button 
          onClick={onClose} 
          variant="outline"
          className="border-gray-600 text-gray-300 hover:bg-gray-700"
        >
          Close
        </Button>
      </div>

      {/* Streaming Load Section */}
      <Card className="bg-gray-800 border-gray-700">
        <CardHeader>
          <CardTitle className="text-white flex items-center gap-2">
            ğŸµ Streaming Audio Engine
            <Badge variant="outline" className="ml-2">
              {trackFiles.length} tracks available
            </Badge>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="text-sm text-gray-400">
            Streaming mode loads tracks instantly without preloading into memory.
            Perfect for live performance with zero delay.
          </div>

          {!isStreamingReady ? (
            <Button 
              onClick={handleStreamingLoad}
              disabled={isLoading || trackFiles.length === 0}
              className="w-full bg-green-600 hover:bg-green-700"
            >
              {isLoading ? 'Loading Streams...' : `ğŸš€ Load ${trackFiles.length} Tracks for Streaming`}
            </Button>
          ) : (
            <div className="space-y-4">
              {/* Transport Controls */}
              <div className="flex items-center gap-4">
                <Button
                  onClick={engineState.isPlaying ? handleStreamingPause : handleStreamingPlay}
                  className={engineState.isPlaying ? "bg-yellow-600 hover:bg-yellow-700" : "bg-green-600 hover:bg-green-700"}
                >
                  {engineState.isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                  {engineState.isPlaying ? 'Pause' : 'Play'}
                </Button>

                <Button
                  onClick={handleStreamingStop}
                  variant="outline"
                  className="border-gray-600 text-gray-300 hover:bg-gray-700"
                >
                  <RotateCcw className="w-4 h-4 mr-2" />
                  Stop
                </Button>

                <div className="text-white">
                  {formatTime(engineState.currentTime)} / {formatTime(engineState.duration)}
                </div>
              </div>

              {/* Master Volume */}
              <div className="space-y-2">
                <label className="text-white text-sm font-medium">Master Volume</label>
                <div className="flex items-center gap-4">
                  <Volume2 className="w-4 h-4 text-gray-400" />
                  <Slider
                    value={[engineState.masterVolume * 100]}
                    onValueChange={([value]) => handleMasterVolumeChange(value)}
                    max={100}
                    step={1}
                    className="flex-1"
                  />
                  <span className="text-gray-400 text-sm w-8">
                    {Math.round(engineState.masterVolume * 100)}
                  </span>
                </div>
              </div>

              {/* Track Controls */}
              <div className="space-y-3">
                <h3 className="text-white font-medium">Streaming Tracks</h3>
                {engineState.tracks.map((track) => (
                  <Card key={track.id} className="bg-gray-700 border-gray-600">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center gap-3">
                          <span className="text-white font-medium">{track.name}</span>
                          <Badge variant={track.muted ? "destructive" : "secondary"}>
                            {track.muted ? "Muted" : "Live"}
                          </Badge>
                        </div>
                        <Button
                          onClick={() => handleTrackMute(track.id)}
                          variant={track.muted ? "destructive" : "outline"}
                          size="sm"
                        >
                          {track.muted ? <VolumeX className="w-4 h-4" /> : <Volume2 className="w-4 h-4" />}
                        </Button>
                      </div>

                      <div className="flex items-center gap-4">
                        <Volume2 className="w-4 h-4 text-gray-400" />
                        <Slider
                          value={[track.volume * 100]}
                          onValueChange={([value]) => handleTrackVolumeChange(track.id, value)}
                          max={100}
                          step={1}
                          className="flex-1"
                          disabled={track.muted}
                        />
                        <span className="text-gray-400 text-sm w-8">
                          {Math.round(track.volume * 100)}
                        </span>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Performance Benefits */}
      <Card className="bg-blue-900/20 border-blue-700">
        <CardHeader>
          <CardTitle className="text-blue-400">ğŸš€ Streaming Benefits</CardTitle>
        </CardHeader>
        <CardContent className="text-sm text-blue-300">
          <ul className="space-y-1">
            <li>â€¢ Zero load time - tracks stream on demand</li>
            <li>â€¢ Minimal memory usage - no preloading required</li>
            <li>â€¢ Instant playback start</li>
            <li>â€¢ Perfect for live performance setups</li>
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/hooks/useStreamingAudio.ts.content:
import { useState, useEffect, useCallback, useRef } from 'react';
import { StreamingAudioEngine } from '@/lib/streaming-audio-engine';
import { AudioFileStorage } from '@/lib/audio-file-storage';
import type { SongWithTracks } from '@shared/schema';

interface UseStreamingAudioReturn {
  // Engine state
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  isLoading: boolean;
  isReady: boolean;
  tracks: Array<{
    id: string;
    name: string;
    volume: number;
    muted: boolean;
    solo: boolean;
    balance: number;
  }>;

  // Transport controls
  loadSong: (song: SongWithTracks) => Promise<void>;
  play: () => Promise<void>;
  pause: () => void;
  stop: () => void;
  seek: (time: number) => void;

  // Track controls
  setTrackVolume: (trackId: string, volume: number) => void;
  toggleTrackMute: (trackId: string) => void;
  toggleTrackSolo: (trackId: string) => void;
  setTrackBalance: (trackId: string, balance: number) => void;
  setMasterVolume: (volume: number) => void;

  // Audio levels for VU meters
  getTrackLevels: (trackId: string) => { left: number; right: number };
  getMasterLevels: () => { left: number; right: number };

  // Current song reference
  currentSong: SongWithTracks | null;
}

export function useStreamingAudio(): UseStreamingAudioReturn {
  const [streamingEngine] = useState(() => new StreamingAudioEngine());
  const [engineState, setEngineState] = useState(streamingEngine.getState());
  const [isLoading, setIsLoading] = useState(false);
  const [isReady, setIsReady] = useState(false);
  const [currentSong, setCurrentSong] = useState<SongWithTracks | null>(null);

  // Subscribe to engine state updates
  useEffect(() => {
    const unsubscribe = streamingEngine.subscribe(() => {
      setEngineState(streamingEngine.getState());
    });

    return () => {
      unsubscribe();
      streamingEngine.dispose();
    };
  }, [streamingEngine]);

  const loadSong = useCallback(async (song: SongWithTracks) => {
    if (!song || !song.tracks || song.tracks.length === 0) {
      console.warn('ğŸš€ No tracks to load for streaming');
      setIsLoading(false);
      setIsReady(false);
      return;
    }

    console.log(`ğŸš€ Streaming loadSong called: "${song.title}" with ${song.tracks.length} tracks`);
    setIsLoading(true);
    setIsReady(false);
    setCurrentSong(song);



    try {
      console.log(`ğŸš€ Streaming load: "${song.title}" with ${song.tracks.length} tracks (instant setup)`);

      // Import LocalSongStorage to load mute regions
      const { LocalSongStorage } = await import('@/lib/local-song-storage');

      // Get actual audio URLs from AudioFileStorage
      const audioStorage = AudioFileStorage.getInstance(song.userId || 'default@user.com');
      const trackDataPromises = song.tracks.map(async (track) => {
        try {
          const audioUrl = await audioStorage.getAudioUrl(track.id);
          if (!audioUrl) return null;
          console.log(`ğŸµ Streaming track: ${track.name} -> ${audioUrl.substring(0, 50)}...`);

          // Load mute regions from LocalSongStorage for persistence
          const muteRegions = await LocalSongStorage.getMuteRegions(
            song.userId || 'default@user.com', 
            song.id, 
            track.id
          );

          // Debug: Log the loaded mute regions
          if (muteRegions && muteRegions.length > 0) {
            console.log(`ğŸ”‡ Loaded ${muteRegions.length} persisted mute regions for track "${track.name}"`);
          }

          // Debug: Log the track's mute state from storage
          console.log(`ğŸ” Track "${track.name}" from storage: isMuted=${track.isMuted}, isSolo=${track.isSolo}`);

          return {
            id: track.id,
            name: track.name,
            url: audioUrl,
            volume: track.volume || 100,
            balance: track.balance || 0,
            isMuted: track.isMuted === true, // Ensure boolean, preserve true values
            isSolo: track.isSolo === true, // Ensure boolean, preserve true values
            muteRegions: muteRegions || [] // Use the persisted regions from LocalSongStorage
          };
        } catch (error) {
          console.warn(`âš ï¸ Failed to get URL for track ${track.name}:`, error);
          return null;
        }
      });

      const trackData = (await Promise.all(trackDataPromises)).filter(Boolean) as any[];

      if (trackData.length === 0) {
        throw new Error('No valid audio URLs found for streaming');
      }

      // Load tracks with enhanced error handling to prevent crashes
      console.log(`ğŸ”§ Loading tracks with enhanced error handling for: "${song.title}"`);

      try {
        await streamingEngine.loadTracks(trackData as any);

        // Warm up tracks and apply mute regions immediately (like Track Manager does)
        await streamingEngine.warmTracksAndApplyMuteRegions();
        console.log(`ğŸ”¥ Tracks warmed up and mute regions applied for "${song.title}"`);

        // Set up automatic waveform generation in background
        setTimeout(() => {
          streamingEngine.autoGenerateWaveform(song, song.userId || 'default@user.com').catch(error => {
            console.warn(`âš ï¸ Waveform generation failed (non-critical):`, error);
          });
        }, 100);

        setIsReady(true);
        console.log(`âœ… Streaming ready for "${song.title}" - instant playback available`);
      } catch (engineError) {
        console.error(`âŒ Streaming engine failed for "${song.title}":`, engineError);
        // Don't crash - just mark as failed and continue
        setIsReady(false);
        throw new Error(`Failed to set up audio engine for "${song.title}". The song may be corrupted.`);
      }
    } catch (error) {
      console.error('âŒ Streaming load failed:', error);
      setIsReady(false);
      setIsLoading(false);
    }
  }, [streamingEngine]);

  const play = useCallback(async () => {
    if (!isReady) {
      console.warn('Streaming not ready for playback');
      return;
    }

    console.log(`â–¶ï¸ Starting streaming playback`);
    await streamingEngine.play();
  }, [streamingEngine, isReady]);

  const pause = useCallback(() => {
    console.log(`â¸ï¸ Pausing streaming playback`);
    streamingEngine.pause();
  }, [streamingEngine]);

  const stop = useCallback(() => {
    console.log(`â¹ï¸ Stopping streaming playback`);
    streamingEngine.stop();
  }, [streamingEngine]);

  const seek = useCallback((time: number) => {
    console.log(`â¯ï¸ Streaming seek to ${time.toFixed(1)}s`);
    streamingEngine.seek(time);
  }, [streamingEngine]);

  const setTrackVolume = useCallback((trackId: string, volume: number) => {
    streamingEngine.setTrackVolume(trackId, volume / 100);
  }, [streamingEngine]);

  const toggleTrackMute = useCallback((trackId: string) => {
    streamingEngine.toggleTrackMute(trackId);
  }, [streamingEngine]);

  const toggleTrackSolo = useCallback((trackId: string) => {
    streamingEngine.toggleTrackSolo(trackId);
  }, [streamingEngine]);

  const setTrackBalance = useCallback((trackId: string, balance: number) => {
    streamingEngine.setTrackBalance(trackId, balance);
  }, [streamingEngine]);

  const setMasterVolume = useCallback((volume: number) => {
    streamingEngine.setMasterVolume(volume / 100);
  }, [streamingEngine]);

  const getTrackLevels = useCallback((trackId: string) => {
    return streamingEngine.getTrackLevels(trackId);
  }, [streamingEngine]);

  const getMasterLevels = useCallback(() => {
    return streamingEngine.getMasterLevels();
  }, [streamingEngine]);

  return {
    // Engine state
    isPlaying: engineState.isPlaying,
    currentTime: engineState.currentTime,
    duration: engineState.duration,
    isLoading,
    isReady,
    tracks: engineState.tracks,

    // Transport controls
    loadSong,
    play,
    pause,
    stop,
    seek,

    // Track controls
    setTrackVolume,
    toggleTrackMute,
    toggleTrackSolo,
    setTrackBalance,
    setMasterVolume,

    // Audio levels
    getTrackLevels,
    getMasterLevels,

    // Current song
    currentSong,
  };
}

