import JSZip from 'jszip';
import { LocalSongStorage, type LocalSong } from './local-song-storage';
import { BrowserFileSystem } from './browser-file-system';
import { waveformGenerator } from './waveform-generator';

export interface BackupManifest {
  version: string;
  createdAt: string;
  appVersion: string;
  songCount: number;
  trackCount: number;
  userEmail: string;
  songs: {
    originalId: string;
    newId: string;
    title: string;
    artist: string;
    tracks: {
      originalId: string;
      newId: string;
      name: string;
      fileName: string;
    }[];
  }[];
}

export class BackupManager {
  private static instance: BackupManager;

  static getInstance(): BackupManager {
    if (!BackupManager.instance) {
      BackupManager.instance = new BackupManager();
    }
    return BackupManager.instance;
  }

  /**
   * Export all user data (songs, tracks, audio files, waveforms) to a zip file
   */
  async exportAllData(userEmail: string, onProgress?: (progress: number, status: string) => void, options?: { signal?: AbortSignal }): Promise<Blob> {
    console.log(`ğŸ’ Starting complete backup export for user: ${userEmail}`);
    onProgress?.(0, "Initializing backup export...");

    // Check for cancellation at start
    if (options?.signal?.aborted) {
      throw new DOMException('Export cancelled', 'AbortError');
    }

    const zip = new JSZip();
    const browserFS = BrowserFileSystem.getInstance();

    // Get all songs
    const songs = LocalSongStorage.getAllSongs(userEmail);
    console.log(`ğŸ“‹ Found ${songs.length} songs to export`);
    onProgress?.(5, `Found ${songs.length} songs to export`);

    if (songs.length === 0) {
      throw new Error('No songs found to export');
    }

    let totalTracks = 0;
    const manifestSongs = [];

    // Calculate total tracks for progress calculation
    songs.forEach(song => totalTracks += song.tracks.length);
    console.log(`ğŸ“Š Total tracks to process: ${totalTracks}`);
    onProgress?.(10, `Processing ${songs.length} songs with ${totalTracks} tracks...`);

    // Create folders in zip
    const songsFolder = zip.folder('songs')!;
    const audioFolder = zip.folder('audio')!;
    const waveformsFolder = zip.folder('waveforms')!;

    // Process each song
    let processedTracks = 0;
    let currentSongIndex = 0;

    for (const song of songs) {
      // Check for cancellation before processing each song
      if (options?.signal?.aborted) {
        throw new DOMException('Export cancelled', 'AbortError');
      }

      currentSongIndex++;
      console.log(`ğŸµ Processing song: "${song.title}"`);
      onProgress?.(15 + (currentSongIndex / songs.length) * 60, `Processing song ${currentSongIndex}/${songs.length}: "${song.title}"`);

      // Generate new IDs for import (prevents conflicts)
      const newSongId = crypto.randomUUID();
      const trackMappings = [];

      // Export song data (without tracks, we'll handle those separately)
      const songData = {
        ...song,
        id: newSongId,
        tracks: [] // Reset tracks, will be rebuilt during import
      };

      songsFolder.file(`${newSongId}.json`, JSON.stringify(songData, null, 2), {
        compression: "DEFLATE",
        compressionOptions: { level: 6 }
      });

      // Process tracks for this song
      for (const track of song.tracks) {
        // Check for cancellation before processing each track
        if (options?.signal?.aborted) {
          throw new DOMException('Export cancelled', 'AbortError');
        }

        const newTrackId = crypto.randomUUID();
        processedTracks++;

        // Update progress for track processing
        const trackProgress = 15 + (processedTracks / totalTracks) * 60;
        onProgress?.(trackProgress, `Processing track ${processedTracks}/${totalTracks}: "${track.name}"`);

        try {
          // Get audio file data from browser storage first to determine proper extension
          const audioUrl = await browserFS.getAudioUrl(track.id);
          let fileExtension = '.mp3'; // Default fallback

          if (audioUrl) {
            // Fetch the audio data
            const response = await fetch(audioUrl);
            const audioBlob = await response.blob();

            // Determine proper file extension from MIME type
            if (audioBlob.type.includes('mp3') || audioBlob.type.includes('mpeg')) {
              fileExtension = '.mp3';
            } else if (audioBlob.type.includes('wav')) {
              fileExtension = '.wav';
            } else if (audioBlob.type.includes('m4a') || audioBlob.type.includes('aac')) {
              fileExtension = '.m4a';
            } else if (audioBlob.type.includes('ogg')) {
              fileExtension = '.ogg';
            }

            const fileName = `${newTrackId}${fileExtension}`;

            trackMappings.push({
              originalId: track.id,
              newId: newTrackId,
              name: track.name,
              fileName: fileName
            });

            // Store in zip with proper extension and compression
            audioFolder.file(fileName, audioBlob, {
              compression: "DEFLATE",
              compressionOptions: { level: 6 }
            });
            console.log(`âœ… Exported audio for track: ${track.name} as ${fileName}`);
          } else {
            console.warn(`âš ï¸ No audio data found for track: ${track.name}`);
            // Still add to mappings but without audio file
            trackMappings.push({
              originalId: track.id,
              newId: newTrackId,
              name: track.name,
              fileName: `${newTrackId}${fileExtension}`
            });
          }
        } catch (error) {
          console.error(`âŒ Failed to export audio for track ${track.name}:`, error);
        }
      }

      manifestSongs.push({
        originalId: song.id,
        newId: newSongId,
        title: song.title,
        artist: song.artist || 'Unknown',
        tracks: trackMappings
      });

      // Export waveform data if it exists
      const waveformData = waveformGenerator.getCachedWaveform(song.id);
      if (waveformData && waveformData.length > 0) {
        waveformsFolder.file(`${newSongId}.json`, JSON.stringify(waveformData), {
          compression: "DEFLATE",
          compressionOptions: { level: 6 }
        });
        console.log(`ğŸ“ˆ Exported waveform for: ${song.title}`);
      }
    }

    // Create manifest with mapping information
    const manifest: BackupManifest = {
      version: '1.0.0',
      createdAt: new Date().toISOString(),
      appVersion: 'StageTracker Pro v1.0',
      songCount: songs.length,
      trackCount: totalTracks,
      userEmail: userEmail,
      songs: manifestSongs
    };

    zip.file('manifest.json', JSON.stringify(manifest, null, 2), {
      compression: "DEFLATE",
      compressionOptions: { level: 6 }
    });

    // Final cancellation check before zip generation
    if (options?.signal?.aborted) {
      throw new DOMException('Export cancelled', 'AbortError');
    }

    onProgress?.(80, "Creating manifest and preparing zip file...");
    console.log(`âœ… Backup complete: ${songs.length} songs, ${totalTracks} tracks`);

    onProgress?.(90, "Generating zip file...");

    // Check for cancellation before expensive zip generation
    if (options?.signal?.aborted) {
      throw new DOMException('Export cancelled', 'AbortError');
    }

    // Generate zip file with Android-compatible settings
    const zipBlob = await zip.generateAsync({ 
      type: 'blob',
      mimeType: 'application/zip',
      compression: "DEFLATE",
      compressionOptions: { level: 6 },
      platform: "UNIX" // Better Android compatibility
    });

    onProgress?.(100, "Export complete!");
    return zipBlob;
  }

  /**
   * Import all data from a zip file backup
   */
  async importAllData(zipFile: File, userEmail: string, onProgress?: (progress: number, status: string) => void): Promise<void> {
    console.log(`ğŸ“¥ Starting complete backup import for user: ${userEmail}`);

    onProgress?.(5, "Reading backup file...");
    const zip = await JSZip.loadAsync(zipFile);
    const browserFS = BrowserFileSystem.getInstance();

    onProgress?.(10, "Parsing manifest...");
    // Read manifest
    const manifestFile = zip.file('manifest.json');
    if (!manifestFile) {
      throw new Error('Invalid backup file: missing manifest.json');
    }

    const manifestText = await manifestFile.async('text');
    const manifest: BackupManifest = JSON.parse(manifestText);

    // Validate backup belongs to current user
    if (manifest.userEmail !== userEmail) {
      throw new Error(`This backup was created by ${manifest.userEmail} and can only be imported by that user.`);
    }

    console.log(`ğŸ“‹ Importing backup from ${manifest.createdAt}: ${manifest.songCount} songs, ${manifest.trackCount} tracks`);

    // Get existing songs to check for conflicts
    const existingSongs = LocalSongStorage.getAllSongs(userEmail);
    const existingTitles = new Set(existingSongs.map(s => s.title.toLowerCase()));

    let importedSongs = 0;
    let importedTracks = 0;
    let skippedSongs = 0;
    const totalSongs = manifest.songs.length;

    onProgress?.(15, `Processing ${totalSongs} songs...`);

    // Process each song from manifest
    for (let songIndex = 0; songIndex < manifest.songs.length; songIndex++) {
      const manifestSong = manifest.songs[songIndex];
      try {
        // Check for title conflicts and resolve with suffix
        let finalTitle = manifestSong.title;
        let counter = 1;

        while (existingTitles.has(finalTitle.toLowerCase())) {
          finalTitle = `${manifestSong.title} (Import ${counter})`;
          counter++;
        }

        if (finalTitle !== manifestSong.title) {
          console.log(`ğŸ“ Renamed "${manifestSong.title}" to "${finalTitle}" to avoid duplicate`);
        }

        // Read song data
        const songFile = zip.file(`songs/${manifestSong.newId}.json`);
        if (!songFile) {
          console.error(`âŒ Song file missing for: ${manifestSong.title}`);
          continue;
        }

        const songData: LocalSong = JSON.parse(await songFile.async('text'));

        // Generate final new IDs for this import session
        const finalSongId = crypto.randomUUID();
        const finalTracks = [];

        // Process tracks
        for (let trackIndex = 0; trackIndex < manifestSong.tracks.length; trackIndex++) {
          const trackMapping = manifestSong.tracks[trackIndex];
          const finalTrackId = crypto.randomUUID();

          // Update progress for track processing
          const baseProgress = 15 + (songIndex / totalSongs) * 70;
          const trackProgress = (trackIndex / manifestSong.tracks.length) * (70 / totalSongs);
          const currentProgress = Math.round(baseProgress + trackProgress);
          onProgress?.(currentProgress, `Importing "${manifestSong.title}" - ${trackMapping.name}...`);

          // Import audio file using the actual filename with extension
          const audioFile = zip.file(`audio/${trackMapping.fileName}`);
          if (audioFile) {
            try {
              const audioBlob = await audioFile.async('blob');
              const audioFile_obj = new File([audioBlob], trackMapping.fileName, { 
                type: 'audio/*',
                lastModified: Date.now()
              });

              // Store in browser file system with final track ID
              await browserFS.addAudioFile(finalSongId, finalTrackId, trackMapping.name, audioFile_obj);
              console.log(`âœ… Imported audio for track: ${trackMapping.name}`);
              importedTracks++;
            } catch (error) {
              console.error(`âŒ Failed to import audio for track ${trackMapping.name}:`, error);
              continue; // Skip this track if audio import fails
            }
          } else {
            console.warn(`âš ï¸ Audio file missing for track: ${trackMapping.name}`);
            continue; // Skip tracks without audio
          }

          // Add track to song with final ID (matching Track schema)
          finalTracks.push({
            id: finalTrackId,
            name: trackMapping.name,
            songId: finalSongId,
            trackNumber: finalTracks.length + 1,
            audioUrl: 'blob:stored', // Will be resolved by browser storage
            localFileName: trackMapping.fileName,
            audioData: null,
            mimeType: 'audio/*',
            fileSize: null,
            volume: 100,
            balance: 0,
            isMuted: false,
            isSolo: false
          });
        }

        // Only add song if it has tracks
        if (finalTracks.length > 0) {
          // Create song with final ID and tracks
          const finalSongData: LocalSong = {
            ...songData,
            id: finalSongId,
            title: finalTitle, // Use the resolved title
            tracks: finalTracks,
            createdAt: new Date().toISOString() // Mark as newly imported
          };

          // Add to local storage
          const allSongs = LocalSongStorage.getAllSongs(userEmail);
          allSongs.push(finalSongData);
          LocalSongStorage.saveSongs(userEmail, allSongs);

          // Import waveform if available
          onProgress?.(85 + (songIndex / totalSongs) * 10, `Processing waveform for "${finalTitle}"...`);
          const waveformFile = zip.file(`waveforms/${manifestSong.newId}.json`);
          if (waveformFile) {
            try {
              const waveformData = JSON.parse(await waveformFile.async('text'));
              waveformGenerator.setCachedWaveform(finalSongId, waveformData);
              console.log(`ğŸ“ˆ Imported waveform for: ${manifestSong.title}`);
            } catch (error) {
              console.warn(`âš ï¸ Failed to import waveform for ${manifestSong.title}:`, error);
            }
          }

          console.log(`âœ… Imported song: "${manifestSong.title}" with ${finalTracks.length} tracks`);
          importedSongs++;
        } else {
          console.warn(`âš ï¸ Skipping song "${manifestSong.title}" - no valid tracks`);
          skippedSongs++;
        }

      } catch (error) {
        console.error(`âŒ Failed to import song "${manifestSong.title}":`, error);
        skippedSongs++;
      }
    }

    onProgress?.(100, "Import complete!");
    console.log(`âœ… Import complete: ${importedSongs} songs, ${importedTracks} tracks imported. ${skippedSongs} skipped.`);

    if (importedSongs === 0) {
      throw new Error('No songs were imported. Check for file format issues or duplicate titles.');
    }
  }

  /**
   * Generate a filename for the backup
   */
  static generateBackupFilename(userEmail: string): string {
    const timestamp = new Date().toISOString().slice(0, 19).replace(/[:.]/g, '-');
    const userPrefix = userEmail.split('@')[0].replace(/[^a-zA-Z0-9]/g, '');
    return `stagetracker-backup-${userPrefix}-${timestamp}.zip`;
  }
}

