import { useEffect, useState, useRef } from 'react';

interface ProfessionalStereoVUMeterProps {
  leftLevel: number; // Expected range: 0-100
  rightLevel: number; // Expected range: 0-100
  isPlaying?: boolean;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  showLabels?: boolean;
  horizontal?: boolean;
}

export default function ProfessionalStereoVUMeter({ 
  leftLevel, 
  rightLevel, 
  isPlaying = true, 
  className = "",
  size = 'md',
  showLabels = true,
  horizontal = false
}: ProfessionalStereoVUMeterProps) {
  const [leftDisplay, setLeftDisplay] = useState(0);
  const [rightDisplay, setRightDisplay] = useState(0);
  const [leftPeak, setLeftPeak] = useState(0);
  const [rightPeak, setRightPeak] = useState(0);

  const animationRef = useRef<number>();
  const lastUpdateRef = useRef<number>(0);

  // Smooth animation for both channels
  useEffect(() => {
    if (!isPlaying) {
      setLeftDisplay(0);
      setRightDisplay(0);
      setLeftPeak(0);
      setRightPeak(0);
      return;
    }

    const targetLeft = Math.max(0, Math.min(100, leftLevel));
    const targetRight = Math.max(0, Math.min(100, rightLevel));

    const animate = () => {
      const now = performance.now();
      const deltaTime = now - lastUpdateRef.current;
      lastUpdateRef.current = now;

      // Animate left channel
      setLeftDisplay(prev => {
        if (targetLeft > prev) {
          return prev + (targetLeft - prev) * 0.9; // Fast attack
        } else {
          const decayRate = Math.max(0.88, 1 - (deltaTime / 1000) * 1.5);
          return Math.max(targetLeft, prev * decayRate);
        }
      });

      // Animate right channel  
      setRightDisplay(prev => {
        if (targetRight > prev) {
          return prev + (targetRight - prev) * 0.9; // Fast attack
        } else {
          const decayRate = Math.max(0.88, 1 - (deltaTime / 1000) * 1.5);
          return Math.max(targetRight, prev * decayRate);
        }
      });

      // Peak hold for left
      setLeftPeak(prev => {
        if (targetLeft > prev) {
          return targetLeft;
        } else {
          const peakDecayRate = Math.max(0.985, 1 - (deltaTime / 1000) * 0.3);
          return Math.max(targetLeft, prev * peakDecayRate);
        }
      });

      // Peak hold for right
      setRightPeak(prev => {
        if (targetRight > prev) {
          return targetRight;
        } else {
          const peakDecayRate = Math.max(0.985, 1 - (deltaTime / 1000) * 0.3);
          return Math.max(targetRight, prev * peakDecayRate);
        }
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    lastUpdateRef.current = performance.now();
    animationRef.current = requestAnimationFrame(animate);

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [leftLevel, rightLevel, isPlaying]);

  const createChannel = (level: number, peak: number, label: string) => {
    const segments = horizontal ? 16 : 12;
    const activeSegments = Math.floor((level / 100) * segments);
    const peakSegment = Math.floor((peak / 100) * segments);

    const getSegmentColor = (index: number) => {
      if (!isPlaying) {
        return 'bg-gray-800';
      }

      const percentage = (index / segments) * 100;
      const isActive = index < activeSegments;
      const isPeak = index === peakSegment - 1 && peakSegment > activeSegments;

      if (percentage < 60) {
        if (isPeak) return 'bg-green-300 shadow-green-300/80';
        if (isActive) return 'bg-green-400 shadow-green-400/60';
        return 'bg-green-900/25';
      } else if (percentage < 85) {
        if (isPeak) return 'bg-yellow-300 shadow-yellow-300/80';
        if (isActive) return 'bg-yellow-400 shadow-yellow-400/60';
        return 'bg-yellow-900/25';
      } else {
        if (isPeak) return 'bg-red-300 shadow-red-300/80';
        if (isActive) return 'bg-red-400 shadow-red-400/60';
        return 'bg-red-900/25';
      }
    };

    const sizeConfig = {
      sm: horizontal ? 'w-0.5 h-1.5' : 'w-1 h-0.5',
      md: horizontal ? 'w-1 h-2' : 'w-1.5 h-0.5', 
      lg: horizontal ? 'w-1.5 h-3' : 'w-2 h-1'
    };

    const segmentClass = sizeConfig[size];
    const containerClass = horizontal ? 'flex space-x-0.5' : 'flex flex-col-reverse space-y-reverse space-y-0.5';

    return (
      <div className="flex items-center space-x-1">
        {showLabels && (
          <div className="text-xs font-mono text-gray-400 w-3 text-center">
            {label}
          </div>
        )}
        <div className="flex space-x-0.5 bg-black/30 p-1 rounded border border-gray-700 pl-[17px] pr-[17px] ml-[2px] mr-[2px] mt-[0px] mb-[0px] pt-[3px] pb-[3px]">
          {Array.from({ length: segments }, (_, index) => (
            <div
              key={index}
              className={`${segmentClass} rounded-sm transition-all duration-100 ${getSegmentColor(index)}`}
              style={{
                boxShadow: (index < activeSegments || index === peakSegment - 1) ? 
                  '0 0 2px currentColor' : 'none'
              }}
            />
          ))}
        </div>
      </div>
    );
  };

  // Always stack L and R vertically, but horizontal affects segment direction
  const containerClass = 'flex flex-col space-y-0.5';

  return (
    <div className={`${containerClass} ${className}`}>
      {createChannel(leftDisplay, leftPeak, 'L')}
      {createChannel(rightDisplay, rightPeak, 'R')}
    </div>
  );
}

client/src/components/professional-vu-me.content:
import { useEffect, useState, useRef } from 'react';

interface ProfessionalVUMeterProps {
  level: number; // Expected range: 0-100
  isMuted?: boolean;
  isPlaying?: boolean;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  showValue?: boolean;
  label?: string;
}

export default function ProfessionalVUMeter({ 
  level, 
  isMuted = false, 
  isPlaying = true, 
  className = "", 
  size = 'md', 
  showValue = false,
  label 
}: ProfessionalVUMeterProps) {
  const [displayLevel, setDisplayLevel] = useState(0);
  const [peakLevel, setPeakLevel] = useState(0);
  const animationRef = useRef<number>();
  const lastUpdateRef = useRef<number>(0);

  // Smooth level animation with proper falloff
  useEffect(() => {
    if (!isPlaying || isMuted) {
      setDisplayLevel(0);
      setPeakLevel(0);
      return;
    }

    const targetLevel = Math.max(0, Math.min(100, level));

    const animate = () => {
      const now = performance.now();
      const deltaTime = now - lastUpdateRef.current;
      lastUpdateRef.current = now;

      setDisplayLevel(prev => {
        // Fast attack, slower decay for natural VU meter behavior
        if (targetLevel > prev) {
          // Attack: 95% of the way to target immediately
          return prev + (targetLevel - prev) * 0.95;
        } else {
          // Decay: slower falloff based on time
          const decayRate = Math.max(0.85, 1 - (deltaTime / 1000) * 2);
          return Math.max(targetLevel, prev * decayRate);
        }
      });

      // Peak hold logic
      setPeakLevel(prev => {
        if (targetLevel > prev) {
          return targetLevel;
        } else {
          // Peak decay over time
          const peakDecayRate = Math.max(0.98, 1 - (deltaTime / 1000) * 0.5);
          return Math.max(targetLevel, prev * peakDecayRate);
        }
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    lastUpdateRef.current = performance.now();
    animationRef.current = requestAnimationFrame(animate);

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [level, isPlaying, isMuted]);

  // Professional VU meter configuration
  const segments = 20; // More segments for professional look
  const activeSegments = Math.floor((displayLevel / 100) * segments);
  const peakSegment = Math.floor((peakLevel / 100) * segments);

  const getSegmentColor = (index: number) => {
    if (!isPlaying || isMuted) {
      return 'bg-gray-800 border-gray-700';
    }

    const percentage = (index / segments) * 100;
    const isActive = index < activeSegments;
    const isPeak = index === peakSegment - 1 && peakSegment > activeSegments;

    // Professional VU meter color scheme
    if (percentage < 50) {
      // Green zone (safe levels)
      if (isPeak) return 'bg-green-400 border-green-300 shadow-green-400/60';
      if (isActive) return 'bg-green-500 border-green-400 shadow-green-500/40';
      return 'bg-green-900/30 border-green-800/50';
    } else if (percentage < 75) {
      // Yellow zone (caution levels)
      if (isPeak) return 'bg-yellow-400 border-yellow-300 shadow-yellow-400/60';
      if (isActive) return 'bg-yellow-500 border-yellow-400 shadow-yellow-500/40';
      return 'bg-yellow-900/30 border-yellow-800/50';
    } else {
      // Red zone (danger levels)
      if (isPeak) return 'bg-red-400 border-red-300 shadow-red-400/60';
      if (isActive) return 'bg-red-500 border-red-400 shadow-red-500/40';
      return 'bg-red-900/30 border-red-800/50';
    }
  };

  const sizeConfig = {
    sm: { 
      container: 'w-3 h-20', 
      segment: 'h-0.5 mb-0.5',
      text: 'text-xs',
      label: 'text-xs'
    },
    md: { 
      container: 'w-4 h-28', 
      segment: 'h-1 mb-0.5',
      text: 'text-xs',
      label: 'text-sm'
    },
    lg: { 
      container: 'w-6 h-36', 
      segment: 'h-1.5 mb-1',
      text: 'text-sm',
      label: 'text-base'
    }
  };

  const config = sizeConfig[size];

  return (
    <div className={`flex flex-col items-center gap-1 ${className}`}>
      {label && (
        <div className={`text-gray-400 font-mono ${config.label} text-center`}>
          {label}
        </div>
      )}

      <div className={`${config.container} flex flex-col-reverse justify-start bg-black/20 rounded border border-gray-700 p-0.5`}>
        {Array.from({ length: segments }, (_, i) => (
          <div
            key={i}
            className={`w-full ${config.segment} rounded-sm border transition-all duration-75 ${getSegmentColor(i)}`}
            style={{
              boxShadow: i < activeSegments || i === peakSegment - 1 ? 
                '0 0 2px currentColor' : 'none'
            }}
          />
        ))}
      </div>

      {showValue && (
        <div className={`text-gray-400 font-mono ${config.text} text-center`}>
          {Math.round(displayLevel)}
        </div>
      )}
    </div>
  );
}

