// Simple broadcast service - completely isolated from existing code
export interface BroadcastState {
  currentSong?: string;
  songEntryId?: string; // Database ID of the broadcast song entry
  position: number; // seconds
  isPlaying: boolean;
  currentLyricLine?: string;
  waveformProgress: number; // 0-1
  // Removed full song data - viewers fetch from database using songEntryId
}

export interface BroadcastRoom {
  id: string;
  name: string;
  hostId: string;
  hostName: string;
  participantCount: number;
  isActive: boolean;
}

class BroadcastService {
  private ws: WebSocket | null = null;
  private isHost = false;
  private roomId: string | null = null;
  private listeners: Array<(state: BroadcastState) => void> = [];
  private roomListeners: Array<(room: BroadcastRoom | null) => void> = [];

  // Subscribe to broadcast state changes
  onStateChange(callback: (state: BroadcastState) => void) {
    this.listeners.push(callback);
    return () => {
      this.listeners = this.listeners.filter(cb => cb !== callback);
    };
  }

  // Subscribe to room info changes  
  onRoomChange(callback: (room: BroadcastRoom | null) => void) {
    this.roomListeners.push(callback);
    return () => {
      this.roomListeners = this.roomListeners.filter(cb => cb !== callback);
    };
  }

  // Host: Start broadcasting - Fixed with Promise to wait for connection
  async startBroadcast(userId: string, userName: string, broadcastName: string): Promise<string> {
    const roomId = broadcastName.trim();

    try {
      // First create database entry
      console.log('ğŸ“¡ Creating broadcast session in database...');
      const dbResponse = await fetch('/api/broadcast/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: roomId,
          name: broadcastName,
          hostId: userId,
          hostName: userName
        })
      });

      if (!dbResponse.ok) {
        throw new Error('Failed to create broadcast session in database');
      }

      console.log('ğŸ“¡ Database session created successfully');

      // Upload all songs from local library to database for this broadcast
      console.log(`ğŸµ Uploading songs for broadcast: ${broadcastName}`);
      const songs = JSON.parse(localStorage.getItem('songs') || '[]');
      if (songs.length > 0) {
        const songsResponse = await fetch(`/api/broadcast/${roomId}/songs`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ songs })
        });

        if (songsResponse.ok) {
          console.log(`âœ… Uploaded ${songs.length} songs to database for broadcast`);
        } else {
          console.warn('âš ï¸ Failed to upload songs, continuing without them');
        }
      } else {
        console.log('âš ï¸ No songs found in local library to upload');
      }

      // Then establish WebSocket connection  
      return new Promise((resolve, reject) => {
        try {
          const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
          const wsUrl = `${protocol}//${window.location.host}/ws/broadcast/${encodeURIComponent(roomId)}`;

          console.log(`ğŸ“¡ Attempting WebSocket connection to: ${wsUrl}`);
          this.ws = new WebSocket(wsUrl);

          this.ws.onopen = () => {
            console.log('ğŸ“¡ WebSocket connection established');
            this.ws?.send(JSON.stringify({
              type: 'host_connect',
              userId,
              userName,
              broadcastName
            }));
            this.isHost = true;
            this.roomId = roomId;
            console.log(`ğŸ“¡ Started broadcasting: "${roomId}"`);
            resolve(roomId); // âœ… Wait for connection before resolving
          };

          this.ws.onmessage = (event) => {
            const message = JSON.parse(event.data);
            console.log('ğŸ“¡ Received server message:', message);
            if (message.type === 'room_info') {
              this.roomListeners.forEach(cb => cb(message.room));
            }
          };

        this.ws.onclose = (event) => {
          console.log(`ğŸ“¡ Host WebSocket closed - Code: ${event.code}, Reason: ${event.reason}`);
          if (event.code === 1006) {
            // Browser blocked connection - use fallback
            console.log('ğŸ“¡ Browser blocked WebSocket (1006), using fallback broadcast indicator');
            this.isHost = true;
            this.roomId = roomId;
            localStorage.setItem('fallback_broadcast', JSON.stringify({
              userId,
              userName,
              broadcastName,
              roomId,
              timestamp: Date.now()
            }));
            resolve(roomId); // Still resolve so UI shows broadcast status
          } else if (event.code !== 1000 && event.code !== 1001) {
            // Other abnormal close
            reject(new Error(`WebSocket closed unexpectedly: ${event.code} - ${event.reason}`));
          }
        };

        this.ws.onerror = (error) => {
          console.error('ğŸ“¡ Broadcast WebSocket error:', error);
          console.error('ğŸ“¡ WebSocket URL:', wsUrl);
          console.error('ğŸ“¡ WebSocket state:', this.ws?.readyState);

          // Fallback: Show broadcast status even if WebSocket fails
          console.log('ğŸ“¡ WebSocket failed, using fallback broadcast indicator');
          this.isHost = true;
          this.roomId = roomId;
          localStorage.setItem('fallback_broadcast', JSON.stringify({
            userId,
            userName,
            broadcastName,
            roomId,
            timestamp: Date.now()
          }));

          // Still resolve so UI shows broadcast status
          resolve(roomId);
        };

        // Add timeout for connection
        setTimeout(() => {
          if (this.ws?.readyState === WebSocket.CONNECTING) {
            console.error('ğŸ“¡ WebSocket connection timeout');
            this.ws?.close();
            reject(new Error('WebSocket connection timeout'));
          }
        }, 10000); // 10 second timeout

        } catch (error) {
          console.warn('ğŸ“¡ Broadcast WebSocket setup failed:', error);
          reject(error);
        }
      });

    } catch (error) {
      console.error('ğŸ“¡ Failed to create broadcast session:', error);
      throw error;
    }
  }

  // Viewer: Join broadcast
  async joinBroadcast(broadcastName: string, userId: string, userName: string): Promise<boolean> {
    const roomId = broadcastName.trim();

    return new Promise((resolve, reject) => {
      try {
        const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
        const wsUrl = `${protocol}//${window.location.host}/ws/broadcast/${encodeURIComponent(roomId)}`;

        console.log(`ğŸ“º Attempting to join broadcast: ${wsUrl}`);
        this.ws = new WebSocket(wsUrl);

        this.ws.onopen = () => {
          console.log('ğŸ“º Viewer WebSocket connection established');
          console.log('ğŸ“º Sending viewer_connect message:', { userId, userName, roomId });
          this.ws?.send(JSON.stringify({
            type: 'viewer_connect', 
            userId,
            userName
          }));
          this.isHost = false;
          this.roomId = roomId;
          console.log(`ğŸ“º Joined broadcast: "${roomId}"`);
          resolve(true);
        };

        this.ws.onmessage = (event) => {
          const message = JSON.parse(event.data);
          console.log('ğŸ“º Viewer received message:', message);
          if (message.type === 'state_update') {
            console.log('ğŸ“º Processing broadcast state update:', message.state);
            this.listeners.forEach(cb => cb(message.state));
          } else if (message.type === 'room_info') {
            console.log('ğŸ“º Room info updated:', message.room);
            this.roomListeners.forEach(cb => cb(message.room));
          } else if (message.type === 'error') {
            console.log('ğŸ“º Server error:', message.message);
            reject(new Error(message.message || 'Broadcast not found'));
          }
        };

        this.ws.onclose = (event) => {
          console.log(`ğŸ“º Viewer WebSocket closed - Code: ${event.code}, Reason: ${event.reason}`);
          if (event.code === 1006) {
            // Browser blocked connection - use fallback for visual indicator
            console.log('ğŸ“º Browser blocked WebSocket (1006), using fallback viewer indicator');
            this.isHost = false;
            this.roomId = roomId;
            localStorage.setItem('fallback_viewer', JSON.stringify({
              userId,
              userName,
              broadcastName,
              roomId,
              timestamp: Date.now()
            }));
            resolve(true); // Still resolve so UI shows viewer status
          } else if (event.code !== 1000 && event.code !== 1001) {
            // Other abnormal close
            reject(new Error(`Connection failed: ${event.code} - ${event.reason}`));
          }
        };

        this.ws.onerror = (error) => {
          console.error('ğŸ“º Viewer WebSocket error:', error);
          console.error('ğŸ“º WebSocket URL:', wsUrl);
          console.error('ğŸ“º WebSocket state:', this.ws?.readyState);

          // Fallback: Show viewer status even if WebSocket fails
          console.log('ğŸ“º WebSocket failed, using fallback viewer indicator');
          this.isHost = false;
          this.roomId = roomId;
          localStorage.setItem('fallback_viewer', JSON.stringify({
            userId,
            userName,
            broadcastName,
            roomId,
            timestamp: Date.now()
          }));

          // Still resolve so UI shows viewer status
          resolve(true);
        };

        // Add timeout for connection
        setTimeout(() => {
          if (this.ws?.readyState === WebSocket.CONNECTING) {
            console.error('ğŸ“º Viewer WebSocket connection timeout');
            this.ws?.close();
            reject(new Error('Connection timeout'));
          }
        }, 10000); // 10 second timeout

      } catch (error) {
        console.warn('ğŸ“º Viewer broadcast setup failed:', error);
        reject(error);
      }
    });
  }

  // Host: Send state update to viewers
  sendState(state: BroadcastState) {
    if (this.isHost && this.ws && this.ws.readyState === WebSocket.OPEN) {
      console.log('ğŸ“¡ Broadcasting state to viewers:', state);
      this.ws.send(JSON.stringify({
        type: 'state_update',
        state
      }));
    } else {
      console.log('ğŸ“¡ Not broadcasting - host:', this.isHost, 'ws ready:', this.ws?.readyState === WebSocket.OPEN);
    }
  }

  // Leave broadcast
  disconnect() {
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    this.isHost = false;
    this.roomId = null;
    this.listeners = [];
    this.roomListeners = [];

    // Clear fallback broadcast and viewer
    localStorage.removeItem('fallback_broadcast');
    localStorage.removeItem('fallback_viewer');
  }

  // Getters
  getRoomId() { return this.roomId; }
  getIsHost() { return this.isHost; }

  getIsViewer() { return !this.isHost && this.roomId !== null; }
  getIsConnected() { return this.ws?.readyState === WebSocket.OPEN; }
}

// Singleton instance
export const broadcastService = new BroadcastService();

