import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { 
  Cast, Users, Radio, Link2, LogOut, Upload, User, Copy, Crown, X, HelpCircle, Megaphone,
  Trash2, AlertTriangle 
} from 'lucide-react';
import { useLocalAuth } from '@/hooks/useLocalAuth';
import { useBroadcast } from '@/hooks/useBroadcast';
import { useToast } from '@/hooks/use-toast';
import { useLocation } from 'wouter';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { LocalSongStorage } from '@/lib/local-song-storage';

export default function Dashboard() {
  const { user, logout } = useLocalAuth();
  const { 
    currentRoom, 
    isHost, 
    isViewer, 
    isConnected,
    startBroadcast, 
    joinBroadcast, 
    leaveBroadcast 
  } = useBroadcast();

  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [broadcastName, setBroadcastName] = useState('');
  const [roomIdToJoin, setRoomIdToJoin] = useState('');
  const [isStarting, setIsStarting] = useState(false);
  const [isJoining, setIsJoining] = useState(false);
  const [profilePhoto, setProfilePhoto] = useState<string | null>(null);
  const [isUploadingPhoto, setIsUploadingPhoto] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [devMessage, setDevMessage] = useState<string>('');
  const [showDeleteAllDialog, setShowDeleteAllDialog] = useState(false);
  const [isDeletingAll, setIsDeletingAll] = useState(false);

  // Profile form state
  const [profileData, setProfileData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    customBroadcastId: ''
  });
  const [isUpdatingProfile, setIsUpdatingProfile] = useState(false);

  // Inline editing states

  const [editingField, setEditingField] = useState<string | null>(null);
  const [editValues, setEditValues] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    customBroadcastId: ''
  });

  // Generate display broadcast ID based on custom ID or fallback
  const getBroadcastId = () => {
    // Use custom ID if set
    if (profileData.customBroadcastId) {
      return profileData.customBroadcastId;
    }
    // Generate fallback for professional users
    if (user?.userType === 'professional') {
      return `PRO-${user.email.split('@')[0].toUpperCase().substring(0, 6)}-${user.email.length}${Date.now().toString().slice(-3)}`;
    }
    return null;
  };

  const displayBroadcastId = getBroadcastId();

  // Determine broadcast permissions
  const canBroadcast = user?.userType === 'professional';
  const canJoin = user?.userType === 'premium' || user?.userType === 'professional';

  // Load dev message
  useEffect(() => {
    fetch('/devmessage.txt')
      .then(res => res.text())
      .then(text => setDevMessage(text.trim()))
      .catch(() => setDevMessage('')); // Silently fail if file doesn't exist
  }, []);

  // Load profile photo and user data when component mounts
  useEffect(() => {
    const loadUserData = async () => {
      if (!user?.email) return;

      try {
        // Load complete user profile data from database
        const userResponse = await fetch(`/api/profile?email=${encodeURIComponent(user.email)}`, {
          method: 'GET',
          credentials: 'include',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
        });

        if (userResponse.ok) {
          const contentType = userResponse.headers.get('content-type');
          if (contentType && contentType.includes('application/json')) {
            const userData = await userResponse.json();
            // Set profile data from database
            const newProfileData = {
              firstName: userData.firstName || '',
              lastName: userData.lastName || '',
              phone: userData.phone || '',
              customBroadcastId: userData.customBroadcastId || ''
            };
            setProfileData(newProfileData);
            setEditValues(newProfileData);

            // Set profile photo
            setProfilePhoto(userData.profilePhoto);
            console.log('âœ… User profile data loaded successfully');
          } else {
            console.error('âŒ Profile API returned non-JSON response');
            // Fall back to photo-only loading
            await loadPhotoOnly();
          }
        } else {
          console.error('âŒ Profile API request failed:', userResponse.status);
          // Fall back to photo-only loading
          await loadPhotoOnly();
        }
      } catch (error) {
        console.error('Error loading user data:', error);
        // Fall back to photo-only loading
        await loadPhotoOnly();
      }
    };

    const loadPhotoOnly = async () => {
      if (!user?.email) return;

      try {
        // Load photo separately as fallback
        const photoResponse = await fetch(`/api/profile-photo?email=${encodeURIComponent(user.email)}`, {
          method: 'GET',
          credentials: 'include',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
        });

        if (photoResponse.ok) {
          const contentType = photoResponse.headers.get('content-type');
          if (contentType && contentType.includes('application/json')) {
            const data = await photoResponse.json();
            setProfilePhoto(data.profilePhoto);
          }
        }
      } catch (error) {
        console.error('Error loading profile photo:', error);
      }
    };

    loadUserData();
  }, [user?.email]);

  const handleStartBroadcast = async () => {
    if (!user || !broadcastName.trim()) return;

    // Check if user has permission to broadcast
    if (!canBroadcast) {
      toast({
        title: "Permission Denied",
        description: "Only Professional users can start broadcasts. Upgrade to Professional to broadcast.",
        variant: "destructive"
      });
      return;
    }

    setIsStarting(true);
    try {
      const roomId = await startBroadcast(user.email, user.email, broadcastName);
      toast({
        title: "ğŸ­ Broadcast Started!",
        description: `"${broadcastName}" is now live!\nRedirecting to performance page...`
      });
      setBroadcastName('');

      // Redirect to performance page after successful broadcast start
      setTimeout(() => {
        setLocation('/');
      }, 1000); // Small delay to let user see the success message

    } catch (error: any) {
      console.error('ğŸ“¡ Broadcast start error:', error);
      toast({
        title: "Failed to start broadcast",
        description: error?.message || "Please check your network connection and try again",
        variant: "destructive"
      });
      setIsStarting(false); // Only reset if failed
    }
  };

  const handleJoinBroadcast = async () => {
    if (!user || !roomIdToJoin.trim()) return;

    // Check if user has permission to join
    if (!canJoin) {
      toast({
        title: "Permission Denied",
        description: "Only Premium and Professional users can join broadcasts. Upgrade to Premium to join broadcasts.",
        variant: "destructive"
      });
      return;
    }

    setIsJoining(true);
    try {
      // First, check if broadcast exists in database
      const response = await fetch(`/api/broadcast/check/${encodeURIComponent(roomIdToJoin.trim())}`);
      const { exists, session } = await response.json();

      if (!exists) {
        toast({
          title: "Broadcast not found",
          description: `No active broadcast named "${roomIdToJoin}" was found`,
          variant: "destructive",
        });
        setIsJoining(false);
        return;
      }

      console.log('ğŸ“¡ Broadcast exists in database:', session);

      // Now attempt WebSocket connection
      const success = await joinBroadcast(roomIdToJoin, user.email, user.email);
      if (success) {
        toast({
          title: "ğŸµ Joined Broadcast!",
          description: `Connected to "${session.name}"! Redirecting to viewer...`
        });
        setRoomIdToJoin('');

        // Redirect to dedicated broadcast viewer page after successful join
        setTimeout(() => {
          setLocation('/broadcast-viewer');
        }, 1000); // Small delay to let user see the success message

      } else {
        toast({
          title: "Failed to join broadcast",
          description: "Unable to establish connection to the broadcast",
          variant: "destructive"
        });
        setIsJoining(false); // Only reset if failed
      }
    } catch (error) {
      console.error('Join broadcast error:', error);
      toast({
        title: "Connection failed",
        description: "Unable to connect to the broadcast",
        variant: "destructive"
      });
      setIsJoining(false); // Only reset if failed
    }
  };

