                    <h4 className="text-sm font-medium text-muted-foreground flex items-center">
                      <HelpCircle className="h-4 w-4 mr-2" />
                      Need Help?
                    </h4>
                    <p className="text-sm text-muted-foreground">
                      Send help requests to{' '}
                      <a 
                        href="mailto:mazzu001@hotmail.com" 
                        className="text-blue-600 hover:underline"
                        data-testid="link-help-email"
                      >
                        mazzu001@hotmail.com
                      </a>
                    </p>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Danger Zone */}
            <div className="lg:col-span-2 mt-8">
              <Card className="border-red-200 dark:border-red-800">
                <CardHeader>
                  <CardTitle className="text-red-600 dark:text-red-400 flex items-center gap-2">
                    <AlertTriangle className="h-5 w-5" />
                    Danger Zone
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <h3 className="font-medium mb-2">Delete All Data</h3>
                    <p className="text-sm text-muted-foreground mb-4">
                      This will permanently delete all your songs, audio files, tracks, and associated data. 
                      The database structure will remain intact for future use. This action cannot be undone.
                    </p>
                    <Button 
                      variant="destructive"
                      onClick={async () => {
                        console.log("Empty All Databases clicked");
                        if (!user?.email) {
                          console.log("No user email");
                          return;
                        }

                        // Simple confirmation
                        if (!confirm("This will empty all databases. Are you sure?")) {
                          console.log("User cancelled");
                          return;
                        }

                        console.log("Starting database deletion for:", user.email);
                        setIsDeletingAll(true);

                        try {
                          // First, delete all songs from PostgreSQL
                          console.log("Deleting songs from server database...");
                          const deleteResponse = await fetch('/api/songs/delete-all', {
                            method: 'DELETE',
                            credentials: 'include',
                            headers: {
                              'Content-Type': 'application/json'
                            }
                          });

                          if (!deleteResponse.ok) {
                            console.error("Failed to delete songs from server");
                            throw new Error("Failed to delete songs from server database");
                          }
                          console.log("Successfully deleted songs from server database");

                          const userKey = user.email.replace(/[@.]/g, '_');

                          // List all IndexedDB databases
                          const allDatabases = await indexedDB.databases();
                          console.log("All databases:", allDatabases);

                          // Clear each database that matches the user
                          for (const dbInfo of allDatabases) {
                            if (!dbInfo.name) continue;

                            // Check if this database belongs to the user
                            if (dbInfo.name.includes(userKey) || 
                                dbInfo.name === `MusicAppStorage::${userKey}` ||
                                dbInfo.name === `MusicAppDB_${userKey}`) {

                              console.log(`Opening database: ${dbInfo.name}`);

                              try {
                                const db = await new Promise<IDBDatabase>((resolve, reject) => {
                                  const request = indexedDB.open(dbInfo.name!);
                                  request.onsuccess = () => resolve(request.result);
                                  request.onerror = () => reject(request.error);
                                });

                                // Get all object stores
                                const storeNames = Array.from(db.objectStoreNames);
                                console.log(`Stores in ${dbInfo.name}:`, storeNames);

                                // Clear each store
                                for (const storeName of storeNames) {
                                  try {
                                    const tx = db.transaction(storeName, 'readwrite');
                                    const store = tx.objectStore(storeName);
                                    const clearRequest = store.clear();

                                    await new Promise<void>((resolve, reject) => {
                                      clearRequest.onsuccess = () => {
                                        console.log(`âœ… Cleared ${storeName} in ${dbInfo.name}`);
                                        resolve();
                                      };
                                      clearRequest.onerror = () => {
                                        console.error(`âŒ Failed to clear ${storeName}:`, clearRequest.error);
                                        reject(clearRequest.error);
                                      };
                                    });
                                  } catch (e) {
                                    console.error(`Error clearing ${storeName}:`, e);
                                  }
                                }

                                db.close();
                                console.log(`âœ… Closed database: ${dbInfo.name}`);
                              } catch (e) {
                                console.error(`Failed to open/clear database ${dbInfo.name}:`, e);
                              }
                            }
                          }

                          // Clear ALL localStorage (simpler approach)
                          console.log("Clearing localStorage...");
                          localStorage.clear();
                          console.log("âœ… LocalStorage cleared");

                          toast({
                            title: "All Data Cleared",
                            description: "All databases have been emptied successfully."
                          });

                          // Reload page
                          console.log("Reloading page in 1 second...");
                          setTimeout(() => window.location.reload(), 1000);

                        } catch (error) {
                          console.error("âŒ Failed to empty databases:", error);
                          toast({
                            title: "Failed to empty databases",
                            description: "Check console for details",
                            variant: "destructive"
                          });
                        } finally {
                          setIsDeletingAll(false);
                        }
                      }}
                      disabled={isDeletingAll}
                      data-testid="button-delete-all"
                    >
                      <Trash2 className="h-4 w-4 mr-2" />
                      {isDeletingAll ? "Emptying..." : "Empty All Databases"}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        )}

      </div>

      {/* Delete All Confirmation Dialog */}
      <AlertDialog open={showDeleteAllDialog} onOpenChange={setShowDeleteAllDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle className="text-red-600">
              <AlertTriangle className="h-5 w-5 inline mr-2" />
              Delete All Data?
            </AlertDialogTitle>
            <AlertDialogDescription>
              This action will permanently delete:
              <ul className="list-disc list-inside mt-2 mb-2">
                <li>All your songs and their metadata</li>
                <li>All audio files and tracks</li>
                <li>All waveforms and cached data</li>
                <li>All mute regions and settings</li>
                <li>All performance data</li>
              </ul>
              The database structure will remain intact, allowing you to start fresh.
              <br /><br />
              <strong className="text-red-600">This action cannot be undone!</strong>
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel disabled={isDeletingAll}>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleDeleteAll}
              disabled={isDeletingAll}
              className="bg-red-600 hover:bg-red-700"
            >
              {isDeletingAll ? "Deleting..." : "Delete Everything"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

server/broadcast-server.ts.content:
import WebSocket, { WebSocketServer } from 'ws';
import { db } from './db';
import { broadcastSessions } from '../shared/schema';
import { eq } from 'drizzle-orm';
import type { Server } from 'http';

interface BroadcastRoom {
  id: string;
  name: string;
  hostId: string;
  hostName: string;
  host: WebSocket | null;
  viewers: Map<string, { ws: WebSocket; userId: string; userName: string }>;
  isActive: boolean;
  createdAt: Date;
}

interface BroadcastState {
  currentSong?: string;
  songEntryId?: string; // Database ID of the broadcast song entry
  position: number;
  isPlaying: boolean;
  currentLyricLine?: string;
  waveformProgress: number;
  // Removed full song data - viewers fetch from database using songEntryId
}

class BroadcastServer {
  private rooms = new Map<string, BroadcastRoom>();
  private wss: WebSocketServer;

  constructor(server: Server) {
    // Create WebSocket server in noServer mode to avoid intercepting all upgrades
    this.wss = new WebSocketServer({ noServer: true });

    this.wss.on('connection', this.handleConnection.bind(this));
    console.log('ğŸ“¡ Broadcast WebSocket server initialized on /ws/broadcast/*');
  }

  // Expose the WebSocket server instance for upgrade handling
  getWebSocketServer() {
    return this.wss;
  }

  private handleConnection(ws: WebSocket, request: any) {
    const url = new URL(request.url!, `http://${request.headers.host}`);
    const roomId = url.pathname.split('/').pop();

    if (!roomId) {
      ws.close(1000, 'Invalid room ID');
      return;
    }

    console.log(`ğŸ“¡ New WebSocket connection for room: ${roomId}`);

    ws.on('message', (data) => {
      try {
        const message = JSON.parse(data.toString());
        this.handleMessage(ws, roomId, message);
      } catch (error) {
        console.error('Invalid WebSocket message:', error);
      }
    });

    ws.on('close', () => {
      this.handleDisconnection(ws, roomId);
    });

    ws.on('error', (error) => {
      console.error('WebSocket error:', error);
    });
  }

  private async handleMessage(ws: WebSocket, roomId: string, message: any) {
    console.log(`ğŸ“¨ Server received message:`, { 
      type: message.type, 
      roomId, 
      hasSongEntryId: !!message.state?.songEntryId 
    });

    switch (message.type) {
      case 'host_connect':
        this.handleHostConnect(ws, roomId, message);
        break;
      case 'viewer_connect':
        await this.handleViewerConnect(ws, roomId, message);
        break;
      case 'state_update':
        this.handleStateUpdate(roomId, message.state);
        break;
      default:
        console.log(`â“ Unknown message type: ${message.type}`);
    }
  }

  private handleHostConnect(ws: WebSocket, roomId: string, message: any) {
    const { userId, userName, broadcastName } = message;

    const room: BroadcastRoom = {
      id: roomId,
      name: broadcastName,
      hostId: userId,
      hostName: userName,
      host: ws,
      viewers: new Map(),
      isActive: true,
      createdAt: new Date()
    };

    this.rooms.set(roomId, room);
    console.log(`ğŸ“¡ Host ${userName} started broadcast: ${roomId} - ${broadcastName}`);

    // Send room info to host
    this.sendRoomInfo(room);
  }

  private async handleViewerConnect(ws: WebSocket, roomId: string, message: any) {
    const { userId, userName } = message;
    console.log(`ğŸ“º Processing viewer_connect for room ${roomId}, user: ${userName}`);

    let room = this.rooms.get(roomId);

    // If room not found in memory, check database and recreate if active
    if (!room || !room.isActive) {
      try {
        const [dbSession] = await db.select().from(broadcastSessions)
          .where(eq(broadcastSessions.id, roomId))
          .limit(1);

        if (dbSession && dbSession.isActive) {
          console.log(`ğŸ”„ Recreating WebSocket room from database session: ${roomId}`);
          // Recreate room from database session
          room = {
            id: dbSession.id,
            name: dbSession.name,
            hostId: dbSession.hostId,
            hostName: dbSession.hostName,
            host: null, // Host will reconnect separately
            viewers: new Map(),
            isActive: true,
            createdAt: new Date(dbSession.createdAt)
          };
          this.rooms.set(roomId, room);
        } else {
          console.log(`âŒ Room ${roomId} not found in database or inactive`);
          ws.send(JSON.stringify({
            type: 'error',
            message: 'Room not found or not active'
          }));
          ws.close(1000, 'Room not found');
          return;
        }
      } catch (error) {
        console.error(`âŒ Error checking database for room ${roomId}:`, error);
        ws.send(JSON.stringify({
          type: 'error',
          message: 'Room not found or not active'
        }));
        ws.close(1000, 'Room not found');
        return;
      }
    }

    room.viewers.set(userId, { ws, userId, userName });
    console.log(`âœ… Viewer ${userName} joined broadcast: ${roomId}, total viewers: ${room.viewers.size}`);

    // Update room info for all participants
    this.sendRoomInfo(room);
  }

  private handleStateUpdate(roomId: string, state: BroadcastState) {
    const room = this.rooms.get(roomId);
    if (!room || !room.isActive) {
      console.log(`âŒ State update failed: Room ${roomId} not found or inactive`);
      return;
    }

    console.log(`ğŸ­ Server received state update for room ${roomId}:`, {
      songEntryId: state.songEntryId,
      songTitle: state.songTitle,
      viewerCount: room.viewers.size
    });

    // Broadcast state to all viewers
    const stateMessage = JSON.stringify({
      type: 'state_update',
      state
    });

    let sentCount = 0;
    Array.from(room.viewers.values()).forEach(({ ws, userName }) => {
      if (ws.readyState === WebSocket.OPEN) {
        ws.send(stateMessage);
        sentCount++;
        console.log(`ğŸ“º State forwarded to viewer: ${userName}`);
      }
    });

    console.log(`âœ… State update broadcasted to ${sentCount}/${room.viewers.size} viewers`);
  }

  private handleDisconnection(ws: WebSocket, roomId: string) {
    const room = this.rooms.get(roomId);
    if (!room) return;

    // Check if host disconnected
    if (room.host === ws) {
      console.log(`ğŸ“¡ Host disconnected from room: ${roomId}`);
      room.isActive = false;

      // Notify all viewers
      const closeMessage = JSON.stringify({
        type: 'broadcast_ended',
        message: 'Host has ended the broadcast'
      });

      Array.from(room.viewers.values()).forEach(({ ws: viewerWs }) => {
        if (viewerWs.readyState === WebSocket.OPEN) {
          viewerWs.send(closeMessage);
          viewerWs.close(1000, 'Broadcast ended');
        }
      });

      // Clean up room
      this.rooms.delete(roomId);
      return;
    }

    // Remove viewer
    for (const [userId, viewer] of room.viewers) {
      if (viewer.ws === ws) {
        room.viewers.delete(userId);
        console.log(`ğŸ“º Viewer disconnected from room: ${roomId}`);
        this.sendRoomInfo(room);
        break;
      }
    }
  }

  private sendRoomInfo(room: BroadcastRoom) {
    const roomInfo = {
      type: 'room_info',
      room: {
        id: room.id,
        name: room.name,
        hostId: room.hostId,
        hostName: room.hostName,
        participantCount: room.viewers.size + 1, // +1 for host
        isActive: room.isActive
      }
    };

    const message = JSON.stringify(roomInfo);

    // Send to host
    if (room.host && room.host.readyState === WebSocket.OPEN) {
      room.host.send(message);
    }

    // Send to all viewers
    Array.from(room.viewers.values()).forEach(({ ws }) => {
      if (ws.readyState === WebSocket.OPEN) {
        ws.send(message);
      }
    });
  }

  // Clean up inactive rooms periodically
  public startCleanup() {
    setInterval(() => {
      const now = new Date();
      const maxAge = 24 * 60 * 60 * 1000; // 24 hours

      for (const [roomId, room] of Array.from(this.rooms.entries())) {
        if (!room.isActive || (now.getTime() - room.createdAt.getTime()) > maxAge) {
          console.log(`ğŸ§¹ Cleaning up inactive room: ${roomId}`);
          this.rooms.delete(roomId);
        }
      }
    }, 60 * 60 * 1000); // Check every hour
  }
}

export function setupBroadcastServer(server: Server) {
  const broadcastServer = new BroadcastServer(server);
  broadcastServer.startCleanup();
  return broadcastServer;
}

