client/src/hooks/use-secure-storage.ts.content:
/**
 * React hook for secure direct storage system
 * Manages user's music library with built-in security and trial protection
 */

import { useState, useCallback, useEffect, useMemo } from 'react';
import SecureDirectStorage, { type DirectStorageStatus } from '@/lib/secure-direct-storage';

interface SecureStorageState {
  status: DirectStorageStatus;
  isLoading: boolean;
  error: string | null;
  needsLibrarySelection: boolean;
}

interface SecureStorageActions {
  initialize: (userEmail: string) => Promise<boolean>;
  selectLibraryFolder: () => Promise<boolean>;
  writeSong: (songId: string, songData: any) => Promise<boolean>;
  readSong: (songId: string) => Promise<any | null>;
  writeAudio: (trackId: string, audioBlob: Blob, filename: string) => Promise<boolean>;
  readAudio: (filename: string) => Promise<Blob | null>;
  listSongs: () => Promise<string[]>;
  refreshStatus: () => void;
}

export function useSecureStorage(): [SecureStorageState, SecureStorageActions] {
  const [state, setState] = useState<SecureStorageState>({
    status: {
      isSupported: SecureDirectStorage.isSupported(),
      isInitialized: false,
      hasLibraryFolder: false,
      libraryPath: 'No folder selected'
    },
    isLoading: false,
    error: null,
    needsLibrarySelection: false
  });

  const storage = SecureDirectStorage.getInstance();

  // Update state from storage status
  const refreshStatus = useCallback(() => {
    const status = storage.getStatus();
    setState(prev => {
      // Only update if something actually changed to prevent infinite loops
      if (
        prev.status.isInitialized !== status.isInitialized ||
        prev.status.hasLibraryFolder !== status.hasLibraryFolder ||
        prev.status.libraryPath !== status.libraryPath ||
        prev.status.lastError !== status.lastError
      ) {
        return {
          ...prev,
          status,
          needsLibrarySelection: status.isSupported && !status.isInitialized,
          error: status.lastError || null
        };
      }
      return prev; // No change, return same state object
    });
  }, [storage]);

  // Initialize storage with user email
  const initialize = useCallback(async (userEmail: string): Promise<boolean> => {
    console.log('ğŸ”§ Initializing secure storage for:', userEmail);
    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const initialized = await storage.initialize(userEmail);
      refreshStatus();

      if (!initialized) {
        console.log('â„¹ï¸ No existing library found, user needs to select folder');
        setState(prev => ({ ...prev, needsLibrarySelection: true }));
      }

      setState(prev => ({ ...prev, isLoading: false }));
      return initialized;

    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : 'Failed to initialize storage';
      console.error('âŒ Storage initialization failed:', error);
      setState(prev => ({ 
        ...prev, 
        isLoading: false, 
        error: errorMsg 
      }));
      return false;
    }
  }, [storage, refreshStatus]);

  // Select library folder
  const selectLibraryFolder = useCallback(async (): Promise<boolean> => {
    console.log('ğŸ“ Requesting library folder selection...');
    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const selected = await storage.selectLibraryFolder();
      refreshStatus();

      if (selected) {
        setState(prev => ({ 
          ...prev, 
          needsLibrarySelection: false, 
          isLoading: false 
        }));
        console.log('âœ… Library folder selected successfully');
      } else {
        setState(prev => ({ 
          ...prev, 
          isLoading: false,
          error: 'Failed to select library folder' 
        }));
      }

      return selected;

    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : 'Failed to select folder';
      console.error('âŒ Folder selection failed:', error);
      setState(prev => ({ 
        ...prev, 
        isLoading: false, 
        error: errorMsg 
      }));
      return false;
    }
  }, [storage, refreshStatus]);

  // Write song data
  const writeSong = useCallback(async (songId: string, songData: any): Promise<boolean> => {
    try {
      return await storage.writeSongData(songId, songData);
    } catch (error) {
      console.error('âŒ Failed to write song:', error);
      setState(prev => ({ 
        ...prev, 
        error: error instanceof Error ? error.message : 'Failed to write song' 
      }));
      return false;
    }
  }, [storage]);

  // Read song data
  const readSong = useCallback(async (songId: string): Promise<any | null> => {
    try {
      return await storage.readSongData(songId);
    } catch (error) {
      console.error('âŒ Failed to read song:', error);
      setState(prev => ({ 
        ...prev, 
        error: error instanceof Error ? error.message : 'Failed to read song' 
      }));
      return null;
    }
  }, [storage]);

  // Write audio file
  const writeAudio = useCallback(async (trackId: string, audioBlob: Blob, filename: string): Promise<boolean> => {
    try {
      return await storage.writeAudioFile(trackId, audioBlob, filename);
    } catch (error) {
      console.error('âŒ Failed to write audio:', error);
      setState(prev => ({ 
        ...prev, 
        error: error instanceof Error ? error.message : 'Failed to write audio' 
      }));
      return false;
    }
  }, [storage]);

  // Read audio file
  const readAudio = useCallback(async (filename: string): Promise<Blob | null> => {
    try {
      return await storage.readAudioFile(filename);
    } catch (error) {
      console.error('âŒ Failed to read audio:', error);
      setState(prev => ({ 
        ...prev, 
        error: error instanceof Error ? error.message : 'Failed to read audio' 
      }));
      return null;
    }
  }, [storage]);

  // List all songs
  const listSongs = useCallback(async (): Promise<string[]> => {
    try {
      return await storage.listSongs();
    } catch (error) {
      console.error('âŒ Failed to list songs:', error);
      setState(prev => ({ 
        ...prev, 
        error: error instanceof Error ? error.message : 'Failed to list songs' 
      }));
      return [];
    }
  }, [storage]);

  // Clear any existing error after some time
  useEffect(() => {
    if (state.error) {
      const timer = setTimeout(() => {
        setState(prev => ({ ...prev, error: null }));
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [state.error]);

  const actions: SecureStorageActions = useMemo(() => ({
    initialize,
    selectLibraryFolder,
    writeSong,
    readSong,
    writeAudio,
    readAudio,
    listSongs,
    refreshStatus
  }), [initialize, selectLibraryFolder, writeSong, readSong, writeAudio, readAudio, listSongs, refreshStatus]);

  return [state, actions];
}

export default useSecureStorage;

client/src/lib/secure-direct-storage.ts.content:
/**
 * Secure Direct Storage System
 * Stores all app data directly in user-chosen folder with built-in security
 * Prevents trial abuse through encrypted file headers
 */

// Extend FileSystemDirectoryHandle with experimental methods
interface ExtendedFileSystemDirectoryHandle extends FileSystemDirectoryHandle {
  queryPermission(descriptor?: FileSystemPermissionDescriptor): Promise<PermissionState>;
  requestPermission(descriptor?: FileSystemPermissionDescriptor): Promise<PermissionState>;
  entries(): AsyncIterableIterator<[string, FileSystemHandle]>;
}

interface FileSystemPermissionDescriptor {
  mode?: 'read' | 'readwrite';
}

interface SecureFileHeader {
  userEmail: string;
  accountId: string;
  createdAt: number;
  signature: string;
  version: string;
}

export interface DirectStorageStatus {
  isSupported: boolean;
  isInitialized: boolean;
  hasLibraryFolder: boolean;
  libraryPath: string;
  lastError?: string;
}

export class SecureDirectStorage {
  private static instance: SecureDirectStorage | null = null;
  private directoryHandle: ExtendedFileSystemDirectoryHandle | null = null;
  private isInitialized = false;
  private currentUserEmail: string = '';
  private accountId: string = '';
  private lastError: string | undefined = undefined;

  static getInstance(): SecureDirectStorage {
    if (!SecureDirectStorage.instance) {
      SecureDirectStorage.instance = new SecureDirectStorage();
    }
    return SecureDirectStorage.instance;
  }

  /**
   * Check if File System Access API is supported
   */
  static isSupported(): boolean {
    return typeof window !== 'undefined' && 
           'showDirectoryPicker' in window && 
           typeof window.showDirectoryPicker === 'function';
  }

  /**
   * Initialize with user credentials
   */
  async initialize(userEmail: string): Promise<boolean> {
    console.log('ğŸ”§ Initializing secure direct storage for:', userEmail);

    this.currentUserEmail = userEmail;
    this.accountId = await this.generateAccountId(userEmail);

    // Try to restore previous library folder
    const restored = await this.restoreLibraryFolder();
    if (restored) {
      this.isInitialized = true;
      console.log('âœ… Restored existing library folder');
      return true;
    }

    console.log('â„¹ï¸ No existing library folder found');
    this.isInitialized = false;
    return false;
  }

  /**
   * Request user to select a folder for their music library
   */
  async selectLibraryFolder(): Promise<boolean> {
    if (!SecureDirectStorage.isSupported()) {
      console.error('âŒ File System Access API not supported');
      this.lastError = 'File System Access API is not supported in this browser';
      return false;
    }

    try {
      console.log('ğŸ“ Requesting user to select music library folder...');

      // Check if we have a secure context
      if (!window.isSecureContext) {
        throw new Error('File System Access API requires a secure context (HTTPS)');
      }

      const handle = await window.showDirectoryPicker({
        mode: 'readwrite',
        startIn: 'documents'
      }) as unknown as ExtendedFileSystemDirectoryHandle;

      this.directoryHandle = handle;

      // Create library folder structure
      await this.createLibraryStructure();

      // Persist the folder handle
      await this.persistLibraryFolder(handle);

      this.isInitialized = true;
      this.lastError = undefined;
      console.log('âœ… Music library folder selected and initialized');
      return true;

    } catch (error) {
      let errorMessage = 'Unknown error occurred';

      if (error instanceof Error) {
        if (error.name === 'AbortError') {
          errorMessage = 'Folder selection was cancelled';
          console.log('â„¹ï¸ User cancelled folder selection');
        } else if (error.name === 'SecurityError') {
          errorMessage = 'File System Access API is blocked by browser security settings';
          console.error('âŒ Security error - API blocked:', error.message);
        } else if (error.name === 'NotAllowedError') {
          errorMessage = 'Permission to access files was denied';
          console.error('âŒ Permission denied:', error.message);
        } else {
          errorMessage = error.message;
          console.error('âŒ Failed to select library folder:', error.message);
        }
      } else {
        console.error('âŒ Failed to select library folder:', error);
      }

      this.lastError = errorMessage;
      return false;
    }
  }

  /**
   * Get current storage status
   */
  getStatus(): DirectStorageStatus {
    return {
      isSupported: SecureDirectStorage.isSupported(),
      isInitialized: this.isInitialized,
      hasLibraryFolder: !!this.directoryHandle,
      libraryPath: this.directoryHandle?.name || 'No folder selected',
      lastError: undefined
    };
  }

  /**
   * Create the music library folder structure
   */
  private async createLibraryStructure(): Promise<boolean> {
    if (!this.directoryHandle) return false;

    try {
      // Create main library folders
      await this.directoryHandle.getDirectoryHandle('songs', { create: true });
      await this.directoryHandle.getDirectoryHandle('audio', { create: true });
      await this.directoryHandle.getDirectoryHandle('lyrics', { create: true });
      await this.directoryHandle.getDirectoryHandle('settings', { create: true });

      console.log('âœ… Created music library structure');
      return true;
    } catch (error) {
      console.error('âŒ Failed to create library structure:', error);
      return false;
    }
  }

  /**
   * Generate a consistent account ID for the user
   */
  private async generateAccountId(email: string): Promise<string> {
    // Create a consistent hash of the email for account ID
    const encoder = new TextEncoder();
    const data = encoder.encode(email.toLowerCase().trim());
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
  }

  /**
   * Create encrypted header for secure files
   */
  private async createFileHeader(): Promise<SecureFileHeader> {
    const header: SecureFileHeader = {
      userEmail: this.currentUserEmail,
      accountId: this.accountId,
      createdAt: Date.now(),
      signature: await this.generateFileSignature(),
      version: '1.0.0'
    };
    return header;
  }

  /**
   * Generate cryptographic signature for file integrity
   */
  private async generateFileSignature(): Promise<string> {
    const data = `${this.currentUserEmail}:${this.accountId}:${Date.now()}`;
    const encoder = new TextEncoder();
    const dataBuffer = encoder.encode(data);
    const hashBuffer = await crypto.subtle.digest('SHA-256', dataBuffer);
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
  }

  /**
   * Verify file belongs to current user
   */
  private async verifyFileOwnership(header: SecureFileHeader): Promise<boolean> {
    // Check email match
    if (header.userEmail !== this.currentUserEmail) {
      console.warn(`ğŸ”’ File belongs to ${header.userEmail}, current user is ${this.currentUserEmail}`);
      return false;
    }

    // Check account ID match
    if (header.accountId !== this.accountId) {
      console.warn('ğŸ”’ Account ID mismatch - possible account switching detected');
      return false;
    }

    return true;
  }

  /**
   * Write secure song data to library
   */
  async writeSongData(songId: string, songData: any): Promise<boolean> {
    if (!this.directoryHandle || !this.isInitialized) {
      console.error('âŒ Library not initialized');
      return false;
    }

    try {
      const header = await this.createFileHeader();
      const secureData = {
        header,
        data: songData
      };

      const songsFolder = await this.directoryHandle.getDirectoryHandle('songs');
      const fileHandle = await songsFolder.getFileHandle(`${songId}.json`, { create: true });
      const writable = await fileHandle.createWritable();

      await writable.write(JSON.stringify(secureData, null, 2));
      await writable.close();

      console.log(`âœ… Securely saved song: ${songData.title || songId}`);
      return true;

    } catch (error) {
      console.error('âŒ Failed to write song data:', error);
      return false;
    }
  }

  /**
   * Read and verify song data from library
   */
  async readSongData(songId: string): Promise<any | null> {
    if (!this.directoryHandle || !this.isInitialized) {
      console.error('âŒ Library not initialized');
      return null;
    }

    try {
      const songsFolder = await this.directoryHandle.getDirectoryHandle('songs');
      const fileHandle = await songsFolder.getFileHandle(`${songId}.json`);
      const file = await fileHandle.getFile();
      const content = await file.text();
      const secureData = JSON.parse(content);

      // Verify file ownership
      if (!await this.verifyFileOwnership(secureData.header)) {
        throw new Error('File ownership verification failed');
      }

      return secureData.data;

    } catch (error) {
      console.error(`âŒ Failed to read song data for ${songId}:`, error);
      return null;
    }
  }

  /**
   * Write secure audio file to library
   */
  async writeAudioFile(trackId: string, audioBlob: Blob, filename: string): Promise<boolean> {
    if (!this.directoryHandle || !this.isInitialized) {
      console.error('âŒ Library not initialized');
      return false;
    }

    try {
      const header = await this.createFileHeader();

      // Create a secure audio file with header prefix
      const headerBlob = new Blob([JSON.stringify(header) + '\n---AUDIO_DATA---\n'], { type: 'text/plain' });
      const secureBlob = new Blob([headerBlob, audioBlob], { type: audioBlob.type });

      const audioFolder = await this.directoryHandle.getDirectoryHandle('audio');
      const fileHandle = await audioFolder.getFileHandle(filename, { create: true });
      const writable = await fileHandle.createWritable();

      await writable.write(secureBlob);
      await writable.close();

      console.log(`âœ… Securely saved audio: ${filename}`);
      return true;

    } catch (error) {
      console.error('âŒ Failed to write audio file:', error);
      return false;
    }
  }

  /**
   * Read and verify audio file from library
   */
  async readAudioFile(filename: string): Promise<Blob | null> {
    if (!this.directoryHandle || !this.isInitialized) {
      console.error('âŒ Library not initialized');
      return null;
    }

    try {
      const audioFolder = await this.directoryHandle.getDirectoryHandle('audio');
      const fileHandle = await audioFolder.getFileHandle(filename);
      const file = await fileHandle.getFile();

      // Extract header and audio data
      const text = await file.text();
      const parts = text.split('\n---AUDIO_DATA---\n');

      if (parts.length !== 2) {
        throw new Error('Invalid secure audio file format');
      }

      const header = JSON.parse(parts[0]);

      // Verify file ownership
      if (!await this.verifyFileOwnership(header)) {
        throw new Error('Audio file ownership verification failed');
      }

      // Return audio data as blob
      const audioData = parts[1];
      return new Blob([audioData], { type: file.type });

    } catch (error) {
      console.error(`âŒ Failed to read audio file ${filename}:`, error);
      return null;
    }
  }

  /**
   * List all songs in the library
   */
  async listSongs(): Promise<string[]> {
    if (!this.directoryHandle || !this.isInitialized) {
      return [];
    }

    try {
      const songsFolder = await this.directoryHandle.getDirectoryHandle('songs');
      const songIds: string[] = [];

      for await (const [name, handle] of (songsFolder as ExtendedFileSystemDirectoryHandle).entries()) {
        if (handle.kind === 'file' && name.endsWith('.json')) {
          const songId = name.replace('.json', '');

          // Verify we can read this song (ownership check)
          const songData = await this.readSongData(songId);
          if (songData) {
            songIds.push(songId);
          }
        }
      }

      return songIds;

    } catch (error) {
      console.error('âŒ Failed to list songs:', error);
      return [];
    }
  }

  /**
   * Persist library folder handle in IndexedDB
   */
  private async persistLibraryFolder(handle: FileSystemDirectoryHandle): Promise<void> {
    try {
      const db = await this.openStorageDB();
      const transaction = db.transaction(['folders'], 'readwrite');
      const store = transaction.objectStore('folders');

      return new Promise((resolve, reject) => {
        const request = store.put({
          id: 'musicLibrary',
          handle: handle,
          userEmail: this.currentUserEmail,
          timestamp: Date.now()
        });

        request.onsuccess = () => {
          console.log('âœ… Library folder handle persisted');
          resolve();
        };

        request.onerror = () => {
          console.error('âŒ Failed to persist library folder:', request.error);
          reject(request.error);
        };
      });
    } catch (error) {
      console.error('âŒ Failed to persist library folder:', error);
      throw error;
    }
  }

  /**
   * Restore library folder handle from IndexedDB
   */
  private async restoreLibraryFolder(): Promise<boolean> {
    try {
      const db = await this.openStorageDB();
      const transaction = db.transaction(['folders'], 'readonly');
      const store = transaction.objectStore('folders');

      return new Promise((resolve) => {
        const request = store.get('musicLibrary');

        request.onsuccess = async () => {
          const result = request.result;

          if (result && result.handle && result.userEmail === this.currentUserEmail) {
            this.directoryHandle = result.handle as ExtendedFileSystemDirectoryHandle;

            try {
              // Verify we still have permission
              const permission = await this.directoryHandle.queryPermission({ mode: 'readwrite' });
              if (permission === 'granted') {
                resolve(true);
                return;
              }

              // Try to request permission again
              const newPermission = await this.directoryHandle.requestPermission({ mode: 'readwrite' });
              resolve(newPermission === 'granted');
            } catch (error) {
              console.error('âŒ Permission check failed:', error);
              resolve(false);
            }
          } else {
            resolve(false);
          }
        };

        request.onerror = () => {
          console.error('âŒ Failed to get library folder from IndexedDB');
          resolve(false);
        };
      });
    } catch (error) {
      console.error('âŒ Failed to restore library folder:', error);
      return false;
    }
  }

  /**
   * Open IndexedDB for storing folder handles
   */
  private async openStorageDB(): Promise<IDBDatabase> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('SecureDirectStorage', 1);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => resolve(request.result);

      request.onupgradeneeded = (event) => {
        const db = (event.target as IDBOpenDBRequest).result;
        if (!db.objectStoreNames.contains('folders')) {
          db.createObjectStore('folders', { keyPath: 'id' });
        }
      };
    });
  }
}

export default SecureDirectStorage;

