  // Manual subscription status check endpoint
  app.post('/api/check-subscriptions', async (req, res) => {
    try {
      console.log('üîç Manual subscription status check triggered');

      // Get all users with subscriptions
      const users = await storage.getAllUsersWithSubscriptions();
      const results = [];

      for (const user of users) {
        if (!user.stripeSubscriptionId) continue;

        try {
          // Get subscription from Stripe
          const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId, {
            expand: ['latest_invoice', 'latest_invoice.payment_intent']
          });

          let status = 'unknown';
          let needsUpdate = false;
          let newStatus = user.subscriptionStatus;

          switch (subscription.status) {
            case 'active':
              status = 'Active';
              newStatus = 2; // Premium
              break;
            case 'trialing':
              status = 'Trial';
              newStatus = 2; // Premium
              break;
            case 'past_due':
              status = 'Payment Past Due';
              newStatus = 1; // Free
              needsUpdate = true;
              break;
            case 'canceled':
              status = 'Cancelled';
              newStatus = 1; // Free
              needsUpdate = true;
              break;
            case 'incomplete':
            case 'incomplete_expired':
              status = 'Payment Incomplete';
              newStatus = 1; // Free
              needsUpdate = true;
              break;
            case 'unpaid':
              status = 'Unpaid';
              newStatus = 1; // Free
              needsUpdate = true;
              break;
          }

          // Check if subscription has expired
          const currentPeriodEnd = subscription.current_period_end * 1000;
          const now = Date.now();
          if (currentPeriodEnd < now && subscription.status !== 'canceled') {
            status = 'Expired';
            newStatus = 1; // Free
            needsUpdate = true;
          }

          // Update if needed
          if (needsUpdate && user.subscriptionStatus !== newStatus) {
            await storage.updateUserSubscription(user.id, {
              subscriptionStatus: newStatus,
              subscriptionEndDate: subscription.current_period_end ? 
                new Date(subscription.current_period_end * 1000).toISOString() : null
            });
            console.log(`‚úÖ Updated ${user.email}: ${user.subscriptionStatus} ‚Üí ${newStatus}`);
          }

          results.push({
            email: user.email,
            subscriptionId: user.stripeSubscriptionId,
            stripeStatus: subscription.status,
            readableStatus: status,
            currentDbStatus: newStatus,
            previousDbStatus: user.subscriptionStatus,
            updated: needsUpdate,
            expiresAt: subscription.current_period_end ? 
              new Date(subscription.current_period_end * 1000).toISOString() : null
          });

        } catch (error: any) {
          if (error.code === 'resource_missing') {
            await storage.updateUserSubscription(user.id, {
              subscriptionStatus: 1,
              subscriptionEndDate: null
            });
            results.push({
              email: user.email,
              subscriptionId: user.stripeSubscriptionId,
              error: 'Subscription not found in Stripe',
              currentDbStatus: 1,
              previousDbStatus: user.subscriptionStatus,
              updated: true
            });
          } else {
            results.push({
              email: user.email,
              subscriptionId: user.stripeSubscriptionId,
              error: error.message
            });
          }
        }
      }

      res.json({
        success: true,
        checked: results.length,
        results: results
      });

    } catch (error: any) {
      console.error('‚ùå Error in manual subscription check:', error);
      res.status(500).json({ error: 'Failed to check subscriptions' });
    }
  });

