                      </div>

                      <div className="flex items-center gap-2 mobile-hidden">
                        <Button
                          onClick={() => deleteTrack(track.id)}
                          variant="ghost"
                          size="sm"
                          data-testid={`button-delete-track-${track.id}-desktop`}
                        >
                          <Trash2 className="h-4 w-4" />
