import { Button } from "@/components/ui/button";
import { 
  SkipBack, 
  FastForward, 
  Play, 
  Pause, 
  Square, 
  Rewind, 
  SkipForward 
} from "lucide-react";

interface TransportControlsProps {
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  progress: number;
  onPlay: () => void;
  onPause: () => void;
  onStop: () => void;
  onSeek: (time: number) => void;
}

export default function TransportControls({
  isPlaying,
  currentTime,
  duration,
  progress,
  onPlay,
  onPause,
  onStop,
  onSeek
}: TransportControlsProps) {
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleProgressClick = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const percentage = clickX / rect.width;
    const newTime = percentage * duration;
    onSeek(newTime);
  };

  return (
    <div className="bg-surface rounded-xl p-6 border border-gray-700">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold flex items-center">
          <Play className="mr-2 text-secondary w-5 h-5" />
          Transport Controls
        </h2>
        <div className="flex items-center space-x-4">
          <div className="text-sm text-gray-400">
            Position: <span className="text-white font-mono">{formatTime(currentTime)} / {formatTime(duration)}</span>
          </div>
          <div className="flex items-center space-x-1">
            <span className="text-xs text-gray-400">Audio</span>
            <div className="w-2 h-2 rounded-full bg-secondary" />
          </div>
        </div>
      </div>

      <div className="flex items-center justify-center space-x-6">
        <Button
          variant="secondary"
          size="icon"
          className="w-14 h-14 rounded-full bg-gray-700 hover:bg-gray-600"
          title="Previous (P)"
          data-testid="button-previous"
        >
          <SkipBack className="w-5 h-5" />
        </Button>

        <Button
          variant="secondary"
          size="icon"
          className="w-14 h-14 rounded-full bg-gray-700 hover:bg-gray-600"
          title="Rewind (R)"
          data-testid="button-rewind"
        >
          <Rewind className="w-5 h-5" />
        </Button>

        <Button
          variant={isPlaying ? "default" : "default"}
          size="icon"
          className={`w-20 h-20 rounded-full shadow-lg ${
            isPlaying 
              ? 'bg-secondary hover:bg-green-700' 
              : 'bg-secondary hover:bg-green-700'
          }`}
          title="Play/Pause (Space)"
          onClick={isPlaying ? onPause : onPlay}
          data-testid="button-play-pause"
        >
          {isPlaying ? <Pause className="w-6 h-6" /> : <Play className="w-6 h-6 ml-1" />}
        </Button>

        <Button
          variant="destructive"
          size="icon"
          className="w-14 h-14 rounded-full bg-error hover:bg-red-700"
          title="Stop (S)"
          onClick={onStop}
          data-testid="button-stop"
        >
          <Square className="w-5 h-5" />
        </Button>

        <Button
          variant="secondary"
          size="icon"
          className="w-14 h-14 rounded-full bg-gray-700 hover:bg-gray-600"
          title="Forward (F)"
          data-testid="button-forward"
        >
          <FastForward className="w-5 h-5" />
        </Button>

        <Button
          variant="secondary"
          size="icon"
          className="w-14 h-14 rounded-full bg-gray-700 hover:bg-gray-600"
          title="Next (N)"
          data-testid="button-next"
        >
          <SkipForward className="w-5 h-5" />
        </Button>
      </div>

      {/* Progress Bar */}
      <div className="mt-6">
        <div 
          className="bg-gray-700 h-2 rounded-full overflow-hidden cursor-pointer"
          onClick={handleProgressClick}
          data-testid="progress-bar"
        >
          <div 
            className="bg-primary h-full transition-all duration-300" 
            style={{ width: `${progress}%` }}
          />
        </div>
        <div className="flex justify-between text-xs text-gray-400 mt-1">
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>
    </div>
  );
}

client/src/components/vu-meter.tsx.content:
import { useEffect, useState } from 'react';

interface VUMeterProps {
  level: number; // 0-100
  isMuted?: boolean;
  isPlaying?: boolean; // Add isPlaying prop like stereo VU meters
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  showValue?: boolean;
}

export default function VUMeter({ level, isMuted = false, isPlaying = true, className = "", size = 'md', showValue = true }: VUMeterProps) {
  const [peakLevel, setPeakLevel] = useState(0);

  // Use direct level - no smoothing since audio engine already provides smooth responsive data
  const currentLevel = isMuted || !isPlaying ? 0 : Math.max(0, Math.min(100, level));

  // Simple responsive updates - use the level directly from the responsive audio engine

  // Peak hold with responsive updates
  useEffect(() => {
    if (currentLevel > peakLevel) {
      setPeakLevel(currentLevel);
    } else {
      const decay = () => {
        setPeakLevel(prev => Math.max(currentLevel, prev - 2.0)); // Faster peak decay for responsiveness
      };
      const interval = setInterval(decay, 8); // Faster decay updates
      return () => clearInterval(interval);
    }
  }, [currentLevel, peakLevel]);

  // Create LED segments with direct responsive level
  const segments = 12;
  const activeSegments = Math.floor((currentLevel / 100) * segments);
  const peakSegment = Math.floor((peakLevel / 100) * segments);

  const getSegmentColor = (index: number) => {
    if (isMuted || !isPlaying) return 'bg-gray-700'; // Same inactive color as stereo meters

    const percentage = (index / segments) * 100;

    if (index === peakSegment - 1 && peakSegment > activeSegments) {
      // Peak indicator - bright with strong glow (SAME AS STEREO)
      if (percentage < 60) return 'bg-green-300 shadow-green-300/70';
      if (percentage < 80) return 'bg-yellow-300 shadow-yellow-300/70';
      return 'bg-red-300 shadow-red-300/70';
    }

    if (index < activeSegments) {
      // Active segments - brighter colors with glow (SAME AS STEREO)
      if (percentage < 60) return 'bg-green-400 shadow-green-400/50';
      if (percentage < 80) return 'bg-yellow-400 shadow-yellow-400/50';
      return 'bg-red-400 shadow-red-400/50';
    }

    // Inactive segments (SAME AS STEREO)
    if (percentage < 60) return 'bg-green-900/20';
    if (percentage < 80) return 'bg-yellow-900/20';
    return 'bg-red-900/20';
  };

  const sizeClasses = {
    sm: { meter: 'h-12', segment: 'w-3 h-1', text: 'text-xs' },
    md: { meter: 'h-16', segment: 'w-4 h-1', text: 'text-xs' },
    lg: { meter: 'h-20', segment: 'w-5 h-1.5', text: 'text-sm' }
  };

  const classes = sizeClasses[size];

  return (
    <div className={`flex flex-col items-center gap-1 ${className}`}>
      <div className={`flex flex-col-reverse gap-0.5 ${classes.meter}`}>
        {Array.from({ length: segments }, (_, i) => (
          <div
            key={i}
            className={`${classes.segment} rounded-sm transition-all duration-150 ${getSegmentColor(i)}`}
          />
        ))}
      </div>
      {showValue && (
        <div className={`text-gray-500 text-center ${classes.text}`}>
          {Math.round(currentLevel)}%
        </div>
      )}
    </div>
  );
}

client/src/components/waveform-visualize.content:
import React, { useEffect, useRef, useMemo, useState } from 'react';
import type { SongWithTracks } from '@shared/schema';
import { waveformGenerator } from '@/lib/waveform-generator';

interface WaveformVisualizerProps {
  song: SongWithTracks | null;
  currentTime: number;
  isPlaying: boolean;
  audioLevels?: Record<string, number>;
  className?: string;
  onSeek?: (time: number) => void;
}

export function WaveformVisualizer({ 
  song, 
  currentTime, 
  isPlaying, 
  audioLevels = {},
  className = "",
  onSeek
}: WaveformVisualizerProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationFrameRef = useRef<number>();
  const [waveformData, setWaveformData] = useState<number[]>([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isHovering, setIsHovering] = useState(false);
  const [previewPosition, setPreviewPosition] = useState<number | null>(null);

  // Cache and waveform generation is now handled by waveformGenerator utility

  // Use cached waveform or generate new one
  useEffect(() => {
    if (!song) {
      setWaveformData([]);
      return;
    }

    // Check for cached waveform first (auto-generated by audio engine)
    const cachedData = waveformGenerator.getCachedWaveform(song.id);
    if (cachedData) {
      console.log(`Loading cached waveform for "${song.title}" (${cachedData.length} data points)`);
      setWaveformData(cachedData);
      setIsGenerating(false);
      return;
    }

    // If no cache found, generate waveform (should rarely happen now due to auto-generation)
    if (song.tracks.length > 0) {
      console.log(`No cached waveform found for "${song.title}", generating...`);
      setIsGenerating(true);
      waveformGenerator.generateWaveformFromSong(song).then((waveformData) => {
        setWaveformData(waveformData);
        setIsGenerating(false);
      }).catch((error) => {
        console.error(`Failed to generate waveform for "${song.title}":`, error);
        setWaveformData([]);
        setIsGenerating(false);
      });
    } else {
      // No tracks and no cache - show empty waveform
      console.log(`No tracks or cached waveform for "${song.title}"`);
      setWaveformData([]);
      setIsGenerating(false);
    }
  }, [song?.id]); // Only trigger on song change, not track count changes

  const draw = () => {
    const canvas = canvasRef.current;
    if (!canvas || !song) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const { width, height } = canvas;
    const duration = song.duration || 240;

    // Clear canvas
    ctx.clearRect(0, 0, width, height);

    // Show loading state
    if (isGenerating) {
      ctx.fillStyle = 'rgba(148, 163, 184, 0.7)';
      ctx.font = '12px -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText('Generating waveform from audio...', width / 2, height / 2);
      return;
    }

    // Show message if no waveform data and no song
    if (waveformData.length === 0 && !song) {
      ctx.fillStyle = 'rgba(148, 163, 184, 0.5)';
      ctx.font = '11px -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText('Select a song to see waveform', width / 2, height / 2);
      return;
    }

    // If we have a song but no waveform data, something went wrong - show placeholder
    if (waveformData.length === 0 && song) {
      ctx.fillStyle = 'rgba(148, 163, 184, 0.5)';
      ctx.font = '11px -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText('Waveform unavailable', width / 2, height / 2);
      return;
    }

    // Draw background
    ctx.fillStyle = 'rgba(15, 23, 42, 0.8)'; // slate-900 with opacity
    ctx.fillRect(0, 0, width, height);

    if (waveformData.length === 0) return;

    const barWidth = width / waveformData.length;
    const centerY = height / 2;
    const maxHeight = height * 0.9; // Use more of the available height

    // Draw waveform bars
    waveformData.forEach((amplitude, index) => {
      const x = index * barWidth;
      const barHeight = amplitude * maxHeight;

      // Calculate position in song
      const position = index / waveformData.length;
      const timeAtPosition = position * duration;

      // Determine color based on playback position and activity
      let color;
      if (timeAtPosition <= currentTime) {
        if (isPlaying) {
          // Played portion while playing - use dynamic green based on audio levels
          const avgLevel = Object.values(audioLevels).reduce((sum, level) => sum + level, 0) / Object.keys(audioLevels).length || 0;
          const intensity = Math.min(1, avgLevel / 50 + 0.3);
          color = `rgba(34, 197, 94, ${intensity})`; // Green with variable intensity
        } else {
          // Played portion while paused - blue hue
          color = `rgba(59, 130, 246, 0.7)`; // Blue for played portions when paused
        }
      } else if (timeAtPosition <= currentTime + 5 && isPlaying) {
        // Upcoming section (next 5 seconds) - blue hint
        const proximity = 1 - (timeAtPosition - currentTime) / 5;
        color = `rgba(59, 130, 246, ${proximity * 0.4})`; // Blue with fade
      } else {
        // Unplayed portion - gray, with subtle highlight when hovering and interactive
        const isInteractive = !isPlaying && onSeek && waveformData.length > 0;
        const baseOpacity = isInteractive && isHovering ? 0.8 : 0.6;
        color = `rgba(71, 85, 105, ${baseOpacity})`; // slate-600
      }

      ctx.fillStyle = color;
      ctx.fillRect(x, centerY - barHeight / 2, Math.max(1, barWidth - 0.5), barHeight);
    });

    // Draw position line (always visible)
    if (duration > 0) {
      const positionX = (currentTime / duration) * width;

      // Position line - green when playing, white when paused
      if (isPlaying) {
        ctx.shadowColor = 'rgba(34, 197, 94, 0.8)';
        ctx.shadowBlur = 4;
        ctx.strokeStyle = 'rgba(34, 197, 94, 0.9)';
      } else {
        ctx.shadowColor = 'rgba(255, 255, 255, 0.6)';
        ctx.shadowBlur = 3;
        ctx.strokeStyle = 'rgba(255, 255, 255, 0.8)';
      }

      ctx.lineWidth = 2;
      ctx.beginPath();
      ctx.moveTo(positionX, 0);
      ctx.lineTo(positionX, height);
      ctx.stroke();

      // Reset shadow
      ctx.shadowBlur = 0;
    }

    // Draw preview line when hovering (only when interactive)
    if (!isPlaying && previewPosition !== null && onSeek && waveformData.length > 0) {
      const previewX = previewPosition * width;

      // Preview line with subtle glow
      ctx.shadowColor = 'rgba(59, 130, 246, 0.6)';
      ctx.shadowBlur = 3;
      ctx.strokeStyle = 'rgba(59, 130, 246, 0.8)'; // Blue preview line
      ctx.lineWidth = 2;
      ctx.setLineDash([4, 4]); // Dashed line to differentiate from progress
      ctx.beginPath();
      ctx.moveTo(previewX, 0);
      ctx.lineTo(previewX, height);
      ctx.stroke();

      // Reset line dash and shadow
      ctx.setLineDash([]);
      ctx.shadowBlur = 0;
    }

    // Draw time indicators
    if (duration > 0) {
      ctx.fillStyle = 'rgba(148, 163, 184, 0.7)'; // slate-400
      ctx.font = '10px -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif';
      ctx.textAlign = 'center';

      // Draw time markers every 30 seconds
      const interval = 30;
      for (let time = 0; time <= duration; time += interval) {
        const x = (time / duration) * width;
        const minutes = Math.floor(time / 60);
        const seconds = Math.floor(time % 60);
        const timeLabel = `${minutes}:${seconds.toString().padStart(2, '0')}`;

        if (x > 25 && x < width - 25) { // Don't draw too close to edges
          ctx.fillText(timeLabel, x, height - 6);
        }
      }
    }
  };

  useEffect(() => {
    const animate = () => {
      draw();
      if (isPlaying) {
        animationFrameRef.current = requestAnimationFrame(animate);
      }
    };

    animate();

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [song, currentTime, isPlaying, audioLevels, waveformData, isHovering, previewPosition]);

  // Redraw when not playing but data changes
  useEffect(() => {
    if (!isPlaying) {
      draw();
    }
  }, [song, currentTime, waveformData, isHovering, previewPosition]);

  // Calculate position from mouse/touch coordinates
  const getPositionFromEvent = (event: React.MouseEvent<HTMLCanvasElement> | React.TouchEvent<HTMLCanvasElement> | MouseEvent | TouchEvent) => {
    const canvas = canvasRef.current;
    if (!canvas || !song) return null;

    const rect = canvas.getBoundingClientRect();
    let clientX: number;

    // Handle both mouse and touch events
    if ('touches' in event) {
      if (event.touches.length === 0) return null;
      clientX = event.touches[0].clientX;
    } else {
      clientX = event.clientX;
    }

    // Calculate position relative to canvas
    const x = clientX - rect.left;
    const canvasWidth = rect.width;

    // Convert X position to normalized position (0-1)
    return Math.max(0, Math.min(1, x / canvasWidth));
  };

  // Handle mouse movement for preview
  const handleMouseMove = (event: React.MouseEvent<HTMLCanvasElement>) => {
    if (!song || !onSeek || isPlaying || waveformData.length === 0) {
      setPreviewPosition(null);
      return;
    }

    const position = getPositionFromEvent(event);
    setPreviewPosition(position);
  };

  // Handle touch movement for preview
  const handleTouchMove = (event: React.TouchEvent<HTMLCanvasElement>) => {
    if (!song || !onSeek || isPlaying || waveformData.length === 0) {
      setPreviewPosition(null);
      return;
    }

    event.preventDefault(); // Prevent scrolling while dragging
    const position = getPositionFromEvent(event);
    setPreviewPosition(position);
  };

  // Handle click/touch events for seeking
  const handleCanvasInteraction = (event: React.MouseEvent<HTMLCanvasElement> | React.TouchEvent<HTMLCanvasElement>) => {
    if (!song || !onSeek || isPlaying || waveformData.length === 0) return;

    const position = getPositionFromEvent(event);
    if (position === null) return;

    // Convert position to time and seek
    const duration = song.duration || 240;
    const seekTime = position * duration;
    onSeek(seekTime);
  };

  if (!song || song.tracks.length === 0) {
    return (
      <div className={`bg-slate-900/80 rounded-lg border border-slate-700 ${className}`}>
        <div className="flex items-center justify-center h-full text-slate-400 text-sm">
          Load a song to see waveform
        </div>
      </div>
    );
  }

  return (
    <div className="bg-slate-900/80 overflow-hidden border border-gray-600 rounded w-full h-[68px] mt-[-10px] mb-[-10px] pt-[4px] pb-[4px]">
      <canvas
        ref={canvasRef}
        width={400}
        height={60}
        className="w-full h-full"
        style={{ 
          display: 'block', 
          height: '60px',
          cursor: !isPlaying && song && waveformData.length > 0 && onSeek ? 'pointer' : 'default'
        }}
        onClick={handleCanvasInteraction}
        onTouchStart={handleCanvasInteraction}
        onMouseMove={handleMouseMove}
        onTouchMove={handleTouchMove}
        onMouseEnter={() => setIsHovering(true)}
        onMouseLeave={() => {
          setIsHovering(false);
          setPreviewPosition(null);
        }}
        onTouchEnd={() => setPreviewPosition(null)}
      />
    </div>
  );
}

