import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement } from '@stripe/react-stripe-js';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { useLocation } from 'wouter';

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY!);

function TestComponent() {
  return <div>Elements component loaded successfully!</div>;
}

function PaymentTestComponent() {
  console.log('üîÑ PaymentElement about to render...');
  return (
    <div>
      <p>Testing PaymentElement:</p>
      <PaymentElement />
    </div>
  );
}

export default function SubscribeElementsTest() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [clientSecret, setClientSecret] = useState('');
  const [showElements, setShowElements] = useState(false);
  const [showPaymentElement, setShowPaymentElement] = useState(false);

  const createClientSecret = async () => {
    try {
      console.log('üîÑ Creating client secret for Elements test...');

      const storedUser = localStorage.getItem('lpp_local_user');
      if (!storedUser) {
        toast({
          title: "Error",
          description: "No user found",
          variant: "destructive",
        });
        return;
      }

      const userData = JSON.parse(storedUser);

      const response = await fetch('/api/create-subscription', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: userData.email,
          tier: 'premium',
          priceId: 'price_premium_placeholder'
        }),
      });

      const data = await response.json();

      if (response.ok && data.clientSecret) {
        console.log('‚úÖ Client secret created:', data.clientSecret.substring(0, 20) + '...');
        setClientSecret(data.clientSecret);
        toast({
          title: "Success",
          description: "Client secret created",
        });
      } else {
        throw new Error('Failed to create client secret');
      }
    } catch (error: any) {
      console.error('‚ùå Client secret creation error:', error);
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const testElementsRender = () => {
    console.log('üîÑ Testing Elements component render...');
    if (!clientSecret) {
      toast({
        title: "Error",
        description: "Need client secret first",
        variant: "destructive",
      });
      return;
    }

    console.log('üîÑ About to render Elements component...');
    setShowElements(true);
    console.log('‚úÖ Elements component render triggered');
  };

  const testPaymentElementRender = () => {
    console.log('üîÑ Testing PaymentElement component render...');
    if (!showElements || !clientSecret) {
      toast({
        title: "Error",
        description: "Need Elements component first",
        variant: "destructive",
      });
      return;
    }

    console.log('üîÑ About to render PaymentElement...');
    setShowPaymentElement(true);
    console.log('‚úÖ PaymentElement render triggered');
  };

  return (
    <div className="max-w-md mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle>Elements Component Test</CardTitle>
          <p className="text-gray-600">Test Stripe Elements step by step</p>
        </CardHeader>
        <CardContent className="space-y-4">

          <Button onClick={createClientSecret} className="w-full">
