import { useState, useEffect } from 'react';
import * as SecureStore from 'expo-secure-store';

export type UserType = 'free' | 'paid' | 'premium' | 'professional';

interface LocalUser {
  email: string;
  userType: UserType;
  loginTime: number;
  lastVerified?: number;
}

const STORAGE_KEY = 'lpp_local_user';
const SESSION_DURATION = 24 * 60 * 60 * 1000; // 24 hours
const VERIFICATION_INTERVAL = 4 * 60 * 60 * 1000; // 4 hours

// Mock API request function for mobile (in real app, this would connect to backend)
const apiRequest = async (method: string, endpoint: string, data?: any) => {
  // Simulate API response for subscription verification
  if (endpoint === '/api/verify-subscription') {
    // Mock verification - in real app this would call your backend
    return {
      ok: true,
      json: () => Promise.resolve({
        isPaid: data.email.includes('paid') || data.email.includes('professional'),
        userType: data.email.includes('professional') ? 'professional' : 
                 data.email.includes('paid') ? 'paid' : 'free'
      })
    };
  }
  return { ok: false };
};

export function useLocalAuth() {
  const [user, setUser] = useState<LocalUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Always check fresh subscription status on app launch
    const checkExistingSession = async () => {
      try {
        const stored = await SecureStore.getItemAsync(STORAGE_KEY);
        if (stored) {
          const userData = JSON.parse(stored) as LocalUser;

          // Check if session is still valid (within 24 hours)
          if (Date.now() - userData.loginTime < SESSION_DURATION) {
            // ALWAYS verify subscription status on launch - no caching
            if (userData.email) {
              try {
                console.log('üîÑ Checking fresh subscription status for:', userData.email);
                const response = await apiRequest('POST', '/api/verify-subscription', {
                  email: userData.email
                });

                if (response.ok) {
                  const verificationResult = await response.json();
                  console.log('‚úÖ Fresh subscription status:', verificationResult.userType);

                  // Always update with fresh subscription status from server
                  const updatedUserData = {
                    ...userData,
                    userType: verificationResult.userType as UserType,
                    lastVerified: Date.now()
                  };

                  await SecureStore.setItemAsync(STORAGE_KEY, JSON.stringify(updatedUserData));
                  setUser(updatedUserData);
                } else {
                  console.log('‚ùå Subscription verification failed, logging out');
                  await SecureStore.deleteItemAsync(STORAGE_KEY);
                  setUser(null);
                }
              } catch (verificationError) {
                console.error('‚ùå Error verifying subscription:', verificationError);
                // If verification fails, log out to be safe
                await SecureStore.deleteItemAsync(STORAGE_KEY);
                setUser(null);
              }
            } else {
              // No email stored, invalid session
              await SecureStore.deleteItemAsync(STORAGE_KEY);
              setUser(null);
            }
          } else {
            // Session expired, remove it
            await SecureStore.deleteItemAsync(STORAGE_KEY);
            setUser(null);
          }
        }
      } catch (error) {
        console.error('Error checking local session:', error);
        await SecureStore.deleteItemAsync(STORAGE_KEY);
        setUser(null);
      } finally {
        setIsLoading(false);
      }
    };

    checkExistingSession();
  }, []);

  const login = async (userType: UserType, email: string) => {
    const userData: LocalUser = {
      email,
      userType,
      loginTime: Date.now(),
      lastVerified: Date.now()
    };

    await SecureStore.setItemAsync(STORAGE_KEY, JSON.stringify(userData));
    setUser(userData);
    setIsLoading(false);
  };

  const logout = async () => {
    await SecureStore.deleteItemAsync(STORAGE_KEY);
    setUser(null);
  };

  const upgrade = async () => {
    if (user) {
      const upgradedUser = { ...user, userType: 'paid' as const };
      await SecureStore.setItemAsync(STORAGE_KEY, JSON.stringify(upgradedUser));
      setUser(upgradedUser);
    }
  };

  return {
    user,
    isLoading,
    isAuthenticated: !!user,
    isPaidUser: user?.userType === 'paid' || user?.userType === 'premium' || user?.userType === 'professional',
    isFreeUser: user?.userType === 'free',
    isProfessionalUser: user?.userType === 'professional',
    login,
    logout,
    upgrade
  };
}

mobile-app/src/hooks/useMIDISequencer.ts.content:
import { useState, useEffect, useRef, useCallback } from 'react';
import { parseMIDICommand, MIDIParseResult } from '../utils/midiFormatter';

export interface MIDICommand {
  timestamp: number;
  type: 'note_on' | 'note_off' | 'control_change' | 'program_change';
  channel?: number;
  note?: number;
  velocity?: number;
  controller?: number;
  value?: number;
  program?: number;
  description?: string;
  originalText?: string;
}

interface MIDISequencerProps {
  onExecuteCommand?: (command: string) => Promise<boolean>;
}

export function useMIDISequencer({ onExecuteCommand }: MIDISequencerProps = {}) {
  const [commands, setCommands] = useState<MIDICommand[]>([]);
  const [isActive, setIsActive] = useState(false);
  const [lastTriggeredIndex, setLastTriggeredIndex] = useState(-1);
  const [currentPlaybackTime, setCurrentPlaybackTime] = useState(0);

  const intervalRef = useRef<NodeJS.Timeout>();
  const commandsRef = useRef<MIDICommand[]>([]);

  // Update commands ref when commands change
  useEffect(() => {
    commandsRef.current = commands;
    console.log(`üîÑ Commands ref updated: ${commands.length} commands`, commands);
  }, [commands]);

  // Parse MIDI commands from lyrics text
  const parseMIDICommands = useCallback((lyricsText: string): MIDICommand[] => {
    const lines = lyricsText.split('\n');
    const parsedCommands: MIDICommand[] = [];
    console.log(`üîç Parsing lyrics with ${lines.length} lines:`, lines);

    lines.forEach((line, lineIndex) => {
      console.log(`üìù Processing line ${lineIndex}: "${line}"`);

      // Look for MIDI commands in bracket format: [[PC:12:1]] or with timestamps: [00:30] [[PC:12:1]]
      const timestampMatch = line.match(/\[(\d{1,2}):(\d{2})\]/);
      const midiMatches = line.match(/\[\[([^\]]+)\]\]/g);

      console.log(`‚è∞ Timestamp match:`, timestampMatch);
      console.log(`üéµ MIDI matches:`, midiMatches);

      if (timestampMatch && midiMatches) {
        const minutes = parseInt(timestampMatch[1]);
        const seconds = parseInt(timestampMatch[2]);
        const timestamp = (minutes * 60 + seconds) * 1000; // Convert to milliseconds
        console.log(`‚è±Ô∏è Parsed timestamp: ${timestampMatch[0]} ‚Üí ${timestamp}ms`);

        midiMatches.forEach(midiMatch => {
          const commandText = midiMatch.slice(2, -2); // Remove [[ ]]
          console.log(`üéØ Processing MIDI command: "${commandText}"`);
          try {
            const midiResult = parseMIDICommand(commandText);
            console.log(`üîß Parse result:`, midiResult);
            if (midiResult && midiResult.bytes.length > 0) {
              const midiBytes = midiResult.bytes;
              // Convert MIDI bytes to MIDICommand structure
              const command: MIDICommand = {
                timestamp,
                type: midiResult.type as MIDICommand['type'],
                channel: midiResult.channel,
                note: midiResult.note,
                velocity: midiResult.velocity,
                controller: midiResult.controller,
                value: midiResult.value,
                program: midiResult.program,
                description: midiResult.description,
                originalText: midiMatch
              };

              parsedCommands.push(command);
              console.log(`‚úÖ Added MIDI command:`, command);
            } else {
              console.warn(`‚ö†Ô∏è Invalid MIDI command: "${commandText}"`);
            }
          } catch (error) {
            console.error(`‚ùå Error parsing MIDI command "${commandText}":`, error);
          }
        });
      } else {
        console.log(`‚ö†Ô∏è Line has no valid timestamp + MIDI command combination`);
      }
    });

    // Sort commands by timestamp
    parsedCommands.sort((a, b) => a.timestamp - b.timestamp);
    console.log(`üéº Final parsed commands (${parsedCommands.length}):`, parsedCommands);

    return parsedCommands;
  }, []);

  // Execute a MIDI command
  const executeMIDICommand = useCallback(async (command: MIDICommand): Promise<boolean> => {
    try {
      console.log(`üéπ Executing MIDI command:`, command);

      if (onExecuteCommand && command.originalText) {
        const success = await onExecuteCommand(command.originalText);
        console.log(`üì§ MIDI command execution result: ${success ? 'success' : 'failed'}`);
        return success;
      } else {
        // Mock execution for mobile - in real app this would send to MIDI devices
        console.log(`üì± Mock MIDI execution: ${command.description}`);
        return true;
      }
    } catch (error) {
      console.error(`‚ùå Error executing MIDI command:`, error);
      return false;
    }
  }, [onExecuteCommand]);

  // Start/stop sequencer
  const startSequencer = useCallback((currentTime: number = 0) => {
    console.log(`üéπ Starting MIDI sequencer at time: ${currentTime}ms`);
    setIsActive(true);
    setCurrentPlaybackTime(currentTime);
    setLastTriggeredIndex(-1);

    // Check for commands to execute immediately
    checkAndExecuteCommands(currentTime);
  }, []);

  const stopSequencer = useCallback(() => {
    console.log(`üõë Stopping MIDI sequencer`);
    setIsActive(false);
    setLastTriggeredIndex(-1);

    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = undefined;
    }
  }, []);

  // Check and execute commands at current time
  const checkAndExecuteCommands = useCallback((currentTime: number) => {
    const currentCommands = commandsRef.current;

    if (!currentCommands || currentCommands.length === 0) {
      return;
    }

    // Find commands that should be triggered at current time
    currentCommands.forEach((command, index) => {
      if (index > lastTriggeredIndex && command.timestamp <= currentTime) {
        console.log(`‚è∞ Triggering MIDI command at ${currentTime}ms:`, command);
        executeMIDICommand(command);
        setLastTriggeredIndex(index);
      }
    });
  }, [lastTriggeredIndex, executeMIDICommand]);

  // Update sequencer time (called by audio engine)
  const updateTime = useCallback((currentTime: number) => {
    setCurrentPlaybackTime(currentTime);

    if (isActive) {
      checkAndExecuteCommands(currentTime);
    }
  }, [isActive, checkAndExecuteCommands]);

  // Set MIDI commands
  const setMIDICommands = useCallback((newCommands: MIDICommand[]) => {
    console.log(`üéπ Setting ${newCommands.length} MIDI commands:`, newCommands);
    setCommands(newCommands);
    setLastTriggeredIndex(-1);
  }, []);

  // Load commands from lyrics
  const loadFromLyrics = useCallback((lyricsText: string) => {
    console.log(`üéº Parsing MIDI commands from lyrics:`, lyricsText);
    const parsed = parseMIDICommands(lyricsText);
    console.log(`üéπ Parsed ${parsed.length} commands:`, parsed);
    setMIDICommands(parsed);
    console.log(`‚úÖ Commands set - State: ${parsed.length}, Ref: ${commandsRef.current.length}`);
    console.log(`üéπ Final loaded MIDI commands:`, commandsRef.current);
  }, [parseMIDICommands, setMIDICommands]);

  // Get next command to be triggered
  const getNextCommand = useCallback((): MIDICommand | null => {
    const currentCommands = commandsRef.current;

    if (!currentCommands || currentCommands.length === 0) {
      return null;
    }

    for (let i = lastTriggeredIndex + 1; i < currentCommands.length; i++) {
      if (currentCommands[i].timestamp > currentPlaybackTime) {
        return currentCommands[i];
      }
    }

    return null;
  }, [lastTriggeredIndex, currentPlaybackTime]);

  // Get upcoming commands in next N seconds
  const getUpcomingCommands = useCallback((lookAheadSeconds: number = 5): MIDICommand[] => {
    const currentCommands = commandsRef.current;
    const lookAheadTime = currentPlaybackTime + (lookAheadSeconds * 1000);

    if (!currentCommands || currentCommands.length === 0) {
      return [];
    }

    return currentCommands.filter(command => 
      command.timestamp > currentPlaybackTime && 
      command.timestamp <= lookAheadTime
    );
  }, [currentPlaybackTime]);

  // Reset sequencer
  const reset = useCallback(() => {
    console.log(`üîÑ Resetting MIDI sequencer`);
    stopSequencer();
    setLastTriggeredIndex(-1);
    setCurrentPlaybackTime(0);
  }, [stopSequencer]);

  return {
    commands,
    isActive,
    lastTriggeredIndex,
    currentPlaybackTime,
    startSequencer,
    stopSequencer,
    updateTime,
    loadFromLyrics,
    parseMIDICommands,
    executeMIDICommand,
    setMIDICommands,
    getNextCommand,
    getUpcomingCommands,
    reset
  };
}

