import { useState, useEffect } from "react";
import { Slider } from "@/components/ui/slider";
import { Button } from "@/components/ui/button";
import { RotateCcw } from "lucide-react";

interface PitchShiftControlProps {
  onPitchChange: (semitones: number) => void;
  disabled?: boolean;
}

export function PitchShiftControl({ onPitchChange, disabled = false }: PitchShiftControlProps) {
  const [pitchSemitones, setPitchSemitones] = useState(0);

  // Convert semitones to musical interval names
  const getIntervalName = (semitones: number): string => {
    if (semitones === 0) return "Original Key";

    const absValue = Math.abs(semitones);
    const direction = semitones > 0 ? "up" : "down";

    const intervalNames: { [key: number]: string } = {
      1: "Minor 2nd",
      2: "Major 2nd", 
      3: "Minor 3rd",
      4: "Major 3rd",
      5: "Perfect 4th",
      6: "Tritone",
      7: "Perfect 5th",
      8: "Minor 6th",
      9: "Major 6th",
      10: "Minor 7th",
      11: "Major 7th",
      12: "Octave"
    };

    const intervalName = intervalNames[absValue] || `${absValue} semitones`;
    return `${intervalName} ${direction}`;
  };

  // Convert semitones to pitch ratio for the worklet
  const semitonesToRatio = (semitones: number): number => {
    return Math.pow(2, semitones / 12);
  };

  const handlePitchChange = (value: number[]) => {
    const newSemitones = value[0];
    setPitchSemitones(newSemitones);
    const pitchRatio = semitonesToRatio(newSemitones);
    onPitchChange(pitchRatio);
  };

  const resetPitch = () => {
    setPitchSemitones(0);
    onPitchChange(1.0); // Reset to original pitch
  };

  // Determine color based on pitch shift amount
  const getSliderColor = (): string => {
    if (pitchSemitones === 0) return "bg-gray-600";
    if (Math.abs(pitchSemitones) <= 2) return "bg-green-600";
    if (Math.abs(pitchSemitones) <= 5) return "bg-yellow-600";
    return "bg-red-600";
  };

  return (
    <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
      <div className="flex items-center justify-between mb-3">
        <h3 className="text-sm font-medium text-gray-200">Pitch Shift</h3>
        <Button
          variant="ghost"
          size="sm"
          onClick={resetPitch}
          disabled={disabled || pitchSemitones === 0}
          className="h-7 px-2 text-gray-400 hover:text-white"
          data-testid="button-reset-pitch"
        >
          <RotateCcw className="w-3 h-3" />
        </Button>
      </div>

      <div className="space-y-3">
        {/* Pitch slider */}
        <div className="px-2">
          <Slider
            value={[pitchSemitones]}
            onValueChange={handlePitchChange}
            min={-12}
            max={12}
            step={1}
            disabled={disabled}
            className="w-full"
            data-testid="slider-pitch-shift"
          />
        </div>

        {/* Current pitch display */}
        <div className="text-center space-y-1">
          <div className="text-lg font-mono text-white">
            {pitchSemitones > 0 ? '+' : ''}{pitchSemitones} 
            <span className="text-sm text-gray-400 ml-1">semitones</span>
          </div>
          <div className={`text-xs px-2 py-1 rounded ${getSliderColor()} text-white`}>
            {getIntervalName(pitchSemitones)}
          </div>
        </div>

        {/* Quick preset buttons */}
        <div className="grid grid-cols-4 gap-1 text-xs">
          {[-5, -2, +2, +5].map((preset) => (
            <Button
              key={preset}
              variant="ghost"
              size="sm"
              onClick={() => handlePitchChange([preset])}
              disabled={disabled}
              className="h-7 text-xs px-1 text-gray-400 hover:text-white"
              data-testid={`button-preset-${preset >= 0 ? 'plus' : 'minus'}${Math.abs(preset)}`}
            >
              {preset > 0 ? '+' : ''}{preset}
            </Button>
          ))}
        </div>

        {/* Info text */}
        <div className="text-xs text-gray-500 text-center">
          Real-time pitch shifting without tempo change
        </div>
      </div>
    </div>
  );
}

client/src/lib/preloaded-audio-engine.ts.content:
// Preloaded audio engine with full pitch shifting capabilities
// Uses AudioBuffer for complete audio manipulation

export interface PreloadedTrack {
  id: string;
  name: string;
  url: string;
  audioBuffer: AudioBuffer | null;
  bufferSource: AudioBufferSourceNode | null;
  gainNode: GainNode | null;
  panNode: StereoPannerNode | null;
  analyzerNode: AnalyserNode | null;
  pitchShiftNode: AudioWorkletNode | null; // For pitch shifting
  volume: number;
  balance: number;
  isMuted: boolean;
  isSolo: boolean;
  isLoaded: boolean;
}

export interface PreloadedAudioEngineState {
  isPlaying: boolean;
  currentTime: number;
  duration: number;
  tracks: PreloadedTrack[];
  masterVolume: number;
  masterGainNode: GainNode | null;
  masterOutputNode: GainNode | null;
  pitchOffset: number; // Global pitch offset in semitones
  isLoading: boolean;
  loadingProgress: number;
}

export class PreloadedAudioEngine {
  private audioContext: AudioContext;
  private state: PreloadedAudioEngineState;
  private listeners: Set<() => void> = new Set();
  private updateInterval: number | null = null;
  private startTime: number = 0;
  private pausedTime: number = 0;
  private onSongEndCallback: (() => void) | null = null;

  constructor() {
    this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    this.state = {
      isPlaying: false,
      currentTime: 0,
      duration: 0,
      tracks: [],
      masterVolume: 0.8,
      masterGainNode: null,
      masterOutputNode: null,
      pitchOffset: 0,
      isLoading: false,
      loadingProgress: 0,
    };
    this.setupMasterOutput();
  }

  private setupMasterOutput() {
    // Create master gain node (for volume control)
    this.state.masterGainNode = this.audioContext.createGain();
    this.state.masterGainNode.gain.value = this.state.masterVolume;

    // Create master output node
    this.state.masterOutputNode = this.audioContext.createGain();

    // Simple and reliable audio routing
    this.state.masterGainNode.connect(this.state.masterOutputNode);
    this.state.masterOutputNode.connect(this.audioContext.destination);

    console.log('üéµ Preloaded master output initialized');
  }

  // Pitch shifting using simple playback rate manipulation
  private createPitchShiftedBuffer(originalBuffer: AudioBuffer, semitones: number): AudioBuffer {
    if (semitones === 0) return originalBuffer;

    // Calculate pitch shift ratio (2^(semitones/12))
    const pitchRatio = Math.pow(2, semitones / 12);

    // Create new buffer with adjusted length
    const newLength = Math.floor(originalBuffer.length / pitchRatio);
    const newBuffer = this.audioContext.createBuffer(
      originalBuffer.numberOfChannels,
      newLength,
      originalBuffer.sampleRate
    );

    // Simple resampling - not perfect but functional for basic pitch shifting
    for (let channel = 0; channel < originalBuffer.numberOfChannels; channel++) {
      const originalData = originalBuffer.getChannelData(channel);
      const newData = newBuffer.getChannelData(channel);

      for (let i = 0; i < newLength; i++) {
        const originalIndex = i * pitchRatio;
        const index1 = Math.floor(originalIndex);
        const index2 = Math.min(index1 + 1, originalData.length - 1);
        const fraction = originalIndex - index1;

        // Linear interpolation
        newData[i] = originalData[index1] * (1 - fraction) + originalData[index2] * fraction;
      }
    }

    return newBuffer;
  }

  // Load and decode audio tracks with pitch processing
  async loadTracks(trackData: Array<{ id: string; name: string; url: string }>, pitchOffset: number = 0) {
    console.log(`üöÄ Preloaded load: ${trackData.length} tracks with pitch offset: ${pitchOffset}`);

    this.state.isLoading = true;
    this.state.loadingProgress = 0;
    this.state.pitchOffset = pitchOffset;

    // Clear existing tracks first
    this.clearTracks();

    // Create track references
    const tracks = trackData.map(track => ({
      id: track.id,
      name: track.name,
      url: track.url,
      audioBuffer: null as AudioBuffer | null,
      bufferSource: null as AudioBufferSourceNode | null,
      gainNode: null as GainNode | null,
      panNode: null as StereoPannerNode | null,
      analyzerNode: null as AnalyserNode | null,
      pitchShiftNode: null as AudioWorkletNode | null,
      volume: 1,
      balance: 0,
      isMuted: false,
      isSolo: false,
      isLoaded: false,
    }));

    this.state.tracks = tracks;
    this.notifyListeners();

    // Load and decode each track
    for (let i = 0; i < tracks.length; i++) {
      const track = tracks[i];

      try {
        console.log(`üîÑ Loading track ${i + 1}/${tracks.length}: ${track.name}`);

        // Fetch audio data
        const response = await fetch(track.url);
        const arrayBuffer = await response.arrayBuffer();

        // Decode audio data
        const audioBuffer = await this.audioContext.decodeAudioData(arrayBuffer);

        // Apply pitch shifting if needed
        track.audioBuffer = this.createPitchShiftedBuffer(audioBuffer, pitchOffset);
        track.isLoaded = true;

        // Update loading progress
        this.state.loadingProgress = ((i + 1) / tracks.length) * 100;

        console.log(`‚úÖ Track loaded: ${track.name} (${pitchOffset} semitones)`);
        this.notifyListeners();

      } catch (error) {
        console.error(`‚ùå Failed to load track: ${track.name}`, error);
        track.isLoaded = false;
      }
    }

    // Calculate total duration from longest track
    this.state.duration = Math.max(...tracks
      .filter(t => t.audioBuffer)
      .map(t => t.audioBuffer!.duration)
    );

    this.state.isLoading = false;
    this.state.loadingProgress = 100;

    this.notifyListeners();
    console.log(`‚úÖ Preloaded ready: ${tracks.length} tracks loaded with pitch ${pitchOffset}`);
  }

  private clearTracks() {
    // Stop any playing sources
    this.state.tracks.forEach(track => {
      if (track.bufferSource) {
        try {
          track.bufferSource.stop();
          track.bufferSource.disconnect();
        } catch (e) {
          // Source might already be stopped
        }
      }
    });

    this.state.tracks = [];
    this.state.duration = 0;
    this.state.currentTime = 0;
  }

  private setupTrackAudioNodes(track: PreloadedTrack) {
    if (!track.audioBuffer || track.bufferSource) return;

    // Create buffer source
    track.bufferSource = this.audioContext.createBufferSource();
    track.bufferSource.buffer = track.audioBuffer;

    // Create gain node for volume
    track.gainNode = this.audioContext.createGain();
    track.gainNode.gain.value = track.volume * (track.isMuted ? 0 : 1);

    // Create pan node for balance
    track.panNode = this.audioContext.createStereoPanner();
    track.panNode.pan.value = track.balance;

    // Create analyzer for VU meters
    track.analyzerNode = this.audioContext.createAnalyser();
    track.analyzerNode.fftSize = 256;

    // Connect audio graph
    track.bufferSource.connect(track.gainNode);
    track.gainNode.connect(track.panNode);
    track.panNode.connect(track.analyzerNode);
    track.analyzerNode.connect(this.state.masterGainNode!);

    // Handle track end
    track.bufferSource.onended = () => {
      track.bufferSource = null;
      // Check if all tracks ended
      const anyPlaying = this.state.tracks.some(t => t.bufferSource);
      if (!anyPlaying && this.state.isPlaying) {
        this.stop();
        this.onSongEndCallback?.();
      }
    };
  }

  async play() {
    if (this.state.isPlaying || this.state.isLoading) return;

    // Resume audio context if suspended
    if (this.audioContext.state === 'suspended') {
      await this.audioContext.resume();
    }

    console.log('‚ñ∂Ô∏è Starting preloaded playback');

    // Set up and start all tracks
    this.state.tracks.forEach(track => {
      if (track.isLoaded && track.audioBuffer) {
        this.setupTrackAudioNodes(track);
        if (track.bufferSource) {
          track.bufferSource.start(0, this.pausedTime);
        }
      }
    });

    this.state.isPlaying = true;
    this.startTime = this.audioContext.currentTime - this.pausedTime;

    // Start time update loop
    this.startTimeUpdates();
    this.notifyListeners();
  }

  pause() {
    if (!this.state.isPlaying) return;

    console.log('‚è∏Ô∏è Pausing preloaded playback');

    // Stop all buffer sources
    this.state.tracks.forEach(track => {
      if (track.bufferSource) {
        try {
          track.bufferSource.stop();
          track.bufferSource.disconnect();
        } catch (e) {
          // Source might already be stopped
        }
        track.bufferSource = null;
      }
    });

    this.pausedTime = this.state.currentTime;
    this.state.isPlaying = false;

    this.stopTimeUpdates();
    this.notifyListeners();
  }

  stop() {
    console.log('‚èπÔ∏è Stopping preloaded playback');

    // Stop all buffer sources
    this.state.tracks.forEach(track => {
      if (track.bufferSource) {
        try {
          track.bufferSource.stop();
          track.bufferSource.disconnect();
        } catch (e) {
          // Source might already be stopped
        }
        track.bufferSource = null;
      }
    });

    this.state.isPlaying = false;
    this.state.currentTime = 0;
    this.pausedTime = 0;
    this.startTime = 0;

    this.stopTimeUpdates();
    this.notifyListeners();
  }

  seekTo(time: number) {
    const wasPlaying = this.state.isPlaying;

    if (wasPlaying) {
      this.pause();
    }

    this.pausedTime = Math.max(0, Math.min(time, this.state.duration));
    this.state.currentTime = this.pausedTime;

    if (wasPlaying) {
      this.play();
    }

    this.notifyListeners();
  }

  // Time update methods
  private startTimeUpdates() {
    this.stopTimeUpdates();
    this.updateInterval = window.setInterval(() => {
      if (this.state.isPlaying) {
        this.state.currentTime = this.audioContext.currentTime - this.startTime;
        this.notifyListeners();
      }
    }, 100);
  }

  private stopTimeUpdates() {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
    }
  }

  // Track control methods
  updateTrackVolume(trackId: string, volume: number) {
    const track = this.state.tracks.find(t => t.id === trackId);
    if (track) {
      track.volume = volume;
      if (track.gainNode) {
        track.gainNode.gain.value = volume * (track.isMuted ? 0 : 1);
      }
      this.notifyListeners();
    }
  }

  updateTrackBalance(trackId: string, balance: number) {
    const track = this.state.tracks.find(t => t.id === trackId);
    if (track) {
      track.balance = balance;
      if (track.panNode) {
        track.panNode.pan.value = balance;
      }
      this.notifyListeners();
    }
  }

  updateTrackMute(trackId: string, isMuted: boolean) {
    const track = this.state.tracks.find(t => t.id === trackId);
    if (track) {
      track.isMuted = isMuted;
      if (track.gainNode) {
        track.gainNode.gain.value = track.volume * (isMuted ? 0 : 1);
      }
      this.notifyListeners();
    }
  }

  updateTrackSolo(trackId: string, isSolo: boolean) {
    const track = this.state.tracks.find(t => t.id === trackId);
    if (track) {
      track.isSolo = isSolo;

      // Update all tracks based on solo state
      const hasSoloTracks = this.state.tracks.some(t => t.isSolo);

      this.state.tracks.forEach(t => {
        const shouldPlay = !hasSoloTracks || t.isSolo;
        if (t.gainNode) {
          t.gainNode.gain.value = t.volume * (t.isMuted || !shouldPlay ? 0 : 1);
        }
      });

      this.notifyListeners();
    }
  }

  updateMasterVolume(volume: number) {
    this.state.masterVolume = volume;
    if (this.state.masterGainNode) {
      this.state.masterGainNode.gain.value = volume;
    }
    this.notifyListeners();
  }

  // Listener management
  addListener(callback: () => void) {
    this.listeners.add(callback);
  }

  removeListener(callback: () => void) {
    this.listeners.delete(callback);
  }

  private notifyListeners() {
    this.listeners.forEach(callback => callback());
  }

  // State access
  getState() {
    return { ...this.state };
  }

  // VU meter data
  getVUMeterData(trackId: string) {
    const track = this.state.tracks.find(t => t.id === trackId);
    if (!track?.analyzerNode) return { left: 0, right: 0 };

    const dataArray = new Uint8Array(track.analyzerNode.frequencyBinCount);
    track.analyzerNode.getByteFrequencyData(dataArray);

    // Calculate average level (simple approximation)
    const average = dataArray.reduce((sum, value) => sum + value, 0) / dataArray.length;
    const level = average / 255;

    return { left: level, right: level };
  }

  // Cleanup
  dispose() {
    this.clearTracks();
    this.stopTimeUpdates();
    this.listeners.clear();

    if (this.audioContext.state !== 'closed') {
      this.audioContext.close();
    }
  }

  // Song end callback
  onSongEnd(callback: () => void) {
    this.onSongEndCallback = callback;
  }
}

client/src/components/song-selector-back.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useUpgradePrompt } from "@/hooks/useSubscription";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { ListMusic, Plus, FolderOpen, Search, ExternalLink, Loader2, Trash2 } from "lucide-react";
import type { Song, InsertSong } from "@shared/schema";

interface SongSelectorProps {
  selectedSongId: string | null;
  onSongSelect: (songId: string) => void;
}

export default function SongSelector({ selectedSongId, onSongSelect }: SongSelectorProps) {
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isSearchingLyrics, setIsSearchingLyrics] = useState(false);
  const [searchResult, setSearchResult] = useState<any>(null);
  const [selectedSongs, setSelectedSongs] = useState<Set<string>>(new Set());
  const [newSong, setNewSong] = useState<InsertSong>({
    userId: "", // Will be set when creating
    title: "",
    artist: "",
    duration: 180, // Default duration, will be updated when tracks are added
    bpm: undefined,
    key: "",
    lyrics: ""
  });

  const { toast } = useToast();
  const { handleSongLimitExceeded } = useUpgradePrompt();

  // Delete song mutation
  const deleteSongMutation = useMutation({
    mutationFn: async (songId: string) => {
      const response = await apiRequest('DELETE', `/api/songs/${songId}`);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete song');
      }
      return response.json();
    },
    onSuccess: (_, songId) => {
      queryClient.invalidateQueries({ queryKey: ['/api/songs'] });
      // Clear selection if deleted song was selected
      if (selectedSongId === songId) {
        onSongSelect('');
      }
      toast({
        title: "Song deleted",
        description: "The song has been removed successfully."
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to delete song",
        variant: "destructive"
      });
    }
  });

  const { data: songsData = [], isLoading } = useQuery<Song[]>({
    queryKey: ['/api/songs']
  });

  // Sort songs alphabetically by title
  const songs = songsData.sort((a, b) => a.title.localeCompare(b.title));

  const createSongMutation = useMutation({
    mutationFn: async (songData: InsertSong) => {
      const response = await apiRequest('POST', '/api/songs', songData);
      if (!response.ok) {
        const errorData = await response.json();
        const error = new Error(errorData.message || 'Failed to create song');
        (error as any).response = response;
        (error as any).data = errorData;
        throw error;
      }
      return response.json();
    },
    onSuccess: (song) => {
      queryClient.invalidateQueries({ queryKey: ['/api/songs'] });
      setIsAddDialogOpen(false);
      setNewSong({
        userId: "", // Will be set when creating
        title: "",
        artist: "",
        duration: 180, // Default duration, will be updated when tracks are added
        bpm: undefined,
        key: "",
        lyrics: ""
      });
      onSongSelect(song.id);
      toast({
        title: "Song created",
        description: `${song.title} has been added successfully.`
      });
    },
    onError: async (error) => {
      // Check if it's a song limit error
      if (error instanceof Error && error.message.includes('song_limit_exceeded')) {
        handleSongLimitExceeded();
        return;
      }

      // Check if response contains upgrade prompt
      try {
        const errorResponse = await (error as any).response?.json();
        if (errorResponse?.error === 'song_limit_exceeded') {
          handleSongLimitExceeded();
          return;
        }
      } catch {
        // Fall through to regular error handling
      }

      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to create song",
        variant: "destructive"
      });
    }
  });

  const handleCreateSong = () => {
    if (!newSong.title.trim() || !newSong.artist.trim()) {
      toast({
        title: "Validation Error",
        description: "Title and artist are required",
        variant: "destructive"
      });
      return;
    }

    // Set userId from localStorage or default
    const userId = localStorage.getItem('userId') || 'default-user';
    createSongMutation.mutate({ ...newSong, userId });
  };

  const handleSearchLyrics = async () => {
    if (!newSong.title.trim() || !newSong.artist.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter both title and artist to search for lyrics",
        variant: "destructive"
      });
      return;
    }

    setIsSearchingLyrics(true);
    setSearchResult(null);

    try {
      const response = await apiRequest('POST', '/api/lyrics/search', {
        title: newSong.title,
        artist: newSong.artist
      });

      const data = await response.json();

      if (data.success) {
        // Direct lyrics found - unlikely with current server implementation
        setNewSong({ ...newSong, lyrics: data.lyrics });
        toast({
          title: "Lyrics Found",
          description: "Lyrics have been automatically added to your song"
        });
      } else if (data.openBrowser && data.searchResult) {
        // Found a lyrics page to open
        setSearchResult(data.searchResult);
        toast({
          title: "Lyrics Page Found",
          description: "Opening lyrics page for manual copy-paste"
        });

        // Open the URL in a new tab
        window.open(data.searchResult.url, '_blank');
      } else {
        toast({
          title: "No Lyrics Found",
          description: data.message || "Could not find lyrics for this song",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Lyrics search error:', error);
      toast({
        title: "Search Error",
        description: "Failed to search for lyrics. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsSearchingLyrics(false);
    }
  };

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Checkbox handlers
  const handleSongCheckboxChange = (songId: string, checked: boolean) => {
    const newSelected = new Set(selectedSongs);
    if (checked) {
      newSelected.add(songId);
    } else {
      newSelected.delete(songId);
    }
    setSelectedSongs(newSelected);
  };

  const handleDeleteSelectedSongs = async () => {
    const songTitles = songs.filter(s => selectedSongs.has(s.id)).map(s => s.title);
    const confirmMsg = `Delete ${selectedSongs.size} song(s)?: ${songTitles.join(', ')}\n\nThis action cannot be undone.`;

    if (window.confirm(confirmMsg)) {
      // Delete all selected songs
      for (const songId of Array.from(selectedSongs)) {
        try {
          await deleteSongMutation.mutateAsync(songId);
        } catch (error) {
          console.error('Failed to delete song:', songId, error);
        }
      }
      setSelectedSongs(new Set());
    }
  };




  const handleCardClick = (e: React.MouseEvent, songId: string) => {
    // Add comprehensive error handling to prevent crashes during song selection
    try {
      console.log(`üéµ Attempting to load song: ${songId}`);

      // Get song info for logging
      const song = songs.find(s => s.id === songId);
      const songName = song?.title || 'Unknown Song';

      console.log(`üîÑ Loading song: "${songName}"`);

      // Wrap the song selection in a timeout to prevent hanging
      const loadPromise = Promise.resolve(onSongSelect(songId));
      const timeoutPromise = new Promise((_, reject) => {
        setTimeout(() => reject(new Error('Song loading timeout')), 5000); // 5 second timeout
      });

      Promise.race([loadPromise, timeoutPromise]).catch(error => {
        console.error(`‚ùå Failed to load song "${songName}":`, error);
        // Show user-friendly error message but don't crash
        alert(`Unable to load "${songName}". This song may be corrupted. You can try deleting it using the checkbox.`);
      });

    } catch (error) {
      console.error(`‚ùå Critical error during song selection:`, error);
      // Emergency fallback - show error but keep UI responsive
      const song = songs.find(s => s.id === songId);
      const songName = song?.title || 'Unknown Song';
      alert(`Error loading "${songName}". The song may be corrupted. Use the checkbox to select and delete it.`);
    }
  };

  const handleCheckboxClick = (e: React.MouseEvent) => {
    // Prevent song selection when clicking checkbox
    e.stopPropagation();
  };

  if (isLoading) {
    return (
      <div className="bg-surface rounded-xl p-6 border border-gray-700">
        <div className="animate-pulse space-y-4">
          <div className="h-6 bg-gray-700 rounded w-1/3"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-24 bg-gray-700 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-surface rounded-xl p-6 border border-gray-700">
      <div className="mb-4">
        <div className="flex items-center justify-between mb-3">
          <h2 className="text-xl font-semibold flex items-center">
            <ListMusic className="mr-2 text-primary w-5 h-5" />
            Songs
          </h2>
          <div className="flex space-x-2">
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button 
                className="bg-primary hover:bg-blue-700 px-4 py-2 text-sm"
                data-testid="button-add-song"
              >
                <Plus className="w-4 h-4 mr-1" />
                Add Song
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-surface border-gray-700">
              <DialogHeader>
                <DialogTitle>Add New Song</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="title">Title *</Label>
                  <Input
                    id="title"
                    value={newSong.title}
                    onChange={(e) => setNewSong({ ...newSong, title: e.target.value })}
                    placeholder="Song title"
                    data-testid="input-song-title"
                  />
                </div>
                <div>
                  <Label htmlFor="artist">Artist *</Label>
                  <Input
                    id="artist"
                    value={newSong.artist}
                    onChange={(e) => setNewSong({ ...newSong, artist: e.target.value })}
                    placeholder="Artist name"
                    data-testid="input-song-artist"
                  />
                </div>
                <div>
                  <Label htmlFor="bpm">BPM</Label>
                  <Input
                    id="bpm"
                    type="number"
                    value={newSong.bpm || ""}
                    onChange={(e) => setNewSong({ ...newSong, bpm: parseInt(e.target.value) || undefined })}
                    placeholder="120"
                    data-testid="input-song-bpm"
                  />
                </div>
                <div>
                  <Label htmlFor="key">Key</Label>
                  <Input
                    id="key"
                    value={newSong.key || ""}
                    onChange={(e) => setNewSong({ ...newSong, key: e.target.value })}
                    placeholder="C major"
                    data-testid="input-song-key"
                  />
                </div>
                <div>
                  <div className="flex items-center justify-between mb-2">
                    <Label htmlFor="lyrics">Lyrics (with timestamps and MIDI commands)</Label>
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={handleSearchLyrics}
                      disabled={isSearchingLyrics || !newSong.title.trim() || !newSong.artist.trim()}
                      className="flex items-center gap-2"
                      data-testid="button-search-lyrics"
                    >
                      {isSearchingLyrics ? (
                        <Loader2 className="w-4 h-4 animate-spin" />
                      ) : (
                        <Search className="w-4 h-4" />
                      )}
                      {isSearchingLyrics ? "Searching..." : "Search Online"}
                    </Button>
                  </div>

                  <Textarea
                    id="lyrics"
                    value={newSong.lyrics || ""}
                    onChange={(e) => setNewSong({ ...newSong, lyrics: e.target.value })}
                    placeholder="[00:15] First line of lyrics&#10;[00:30] <!-- MIDI: Program Change 1 -->&#10;[00:32] Second line..."
                    className="min-h-[200px] resize-y"
                    data-testid="input-song-lyrics"
                  />
                  <div className="text-xs text-muted-foreground mt-1">
                    Tip: Use timestamps like [01:30] and add MIDI commands with &lt;!-- MIDI: Program Change 1 --&gt;
                  </div>
                </div>
                <div className="flex justify-end space-x-2">
                  <Button 
                    variant="outline" 
                    onClick={() => setIsAddDialogOpen(false)}
                    data-testid="button-cancel-song"
                  >
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleCreateSong}
                    disabled={createSongMutation.isPending}
                    data-testid="button-save-song"
                  >
                    {createSongMutation.isPending ? "Creating..." : "Create Song"}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>

          <Button 
            variant="secondary"
            className="bg-gray-700 hover:bg-gray-600 px-4 py-2 text-sm"
            data-testid="button-import-song"
          >
            <FolderOpen className="w-4 h-4 mr-1" />
            Import
          </Button>
        </div>

        {/* Delete selected songs button */}
        {selectedSongs.size > 0 && (
          <div className="flex justify-end mt-3">
            <Button
              variant="destructive"
              size="sm"
              onClick={handleDeleteSelectedSongs}
              data-testid="button-delete-selected"
            >
              <Trash2 className="w-4 h-4 mr-1" />
              Delete {selectedSongs.size} Selected Song{selectedSongs.size !== 1 ? 's' : ''}
            </Button>
          </div>
        )}
      </div>

      {songs.length === 0 ? (
        <div className="text-center py-8 text-gray-400">
          <ListMusic className="w-12 h-12 mx-auto mb-4 opacity-50" />
          <p>No songs available. Add your first song to get started.</p>
          <p className="text-sm mt-2 opacity-60">üí° Tip: Check the boxes on songs to select them for deletion</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {songs.map((song) => {

            return (
            <div key={song.id} className="relative rounded-lg">
              {/* Song card wrapper */}
              <div
                className={`transition-all duration-200 hover:shadow-lg rounded-lg ${
                  selectedSongId === song.id
                    ? 'bg-gray-800 border-2 border-primary'
                    : 'bg-gray-800 border border-gray-600 hover:bg-gray-750'
                } cursor-pointer`}
                onClick={(e) => handleCardClick(e, song.id)}
                data-testid={`song-card-${song.id}`}
              >
                <div className="p-3">
                  <div className="flex items-center justify-between mb-1">
                    <Checkbox
                      checked={selectedSongs.has(song.id)}
                      onCheckedChange={(checked) => handleSongCheckboxChange(song.id, checked as boolean)}
                      onClick={handleCheckboxClick}
                      className="mr-3"
                      data-testid={`checkbox-song-${song.id}`}
                    />
                    <h3 className="font-medium truncate">{song.title}</h3>
                    <span className={`text-xs px-2 py-0.5 rounded ${
                      selectedSongId === song.id
                        ? 'bg-secondary/20 text-secondary'
                        : 'bg-gray-600 text-gray-300'
                    }`}>
                      {selectedSongId === song.id ? 'LOADED' : 'READY'}
                    </span>
                  </div>
                  <div className="text-sm text-gray-400 space-y-0.5">
                    <div>Artist: <span className="text-gray-300">{song.artist}</span></div>
                    <div>Duration: <span className="text-gray-300">{formatDuration(song.duration)}</span></div>
                    {song.bpm && <div>BPM: <span className="text-gray-300">{song.bpm}</span></div>}
                    {song.key && <div>Key: <span className="text-gray-300">{song.key}</span></div>}
                  </div>
                </div>
              </div>
            </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

