            console.error('‚ùå Profile API returned non-JSON response');
            // Fall back to photo-only loading
            await loadPhotoOnly();
          }
        } else {
          console.error('‚ùå Profile API request failed:', userResponse.status);
          // Fall back to photo-only loading
          await loadPhotoOnly();
        }
      } catch (error) {
        console.error('Error loading user data:', error);
        // Fall back to photo-only loading
        await loadPhotoOnly();
