/**
 * DataRecoveryDialog - Handle restoration from local folder backup
 */

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { useLocalFolderBackup } from '@/hooks/use-local-folder-backup';
import { useToast } from '@/hooks/use-toast';
import { HardDriveIcon, RefreshCw, AlertTriangle, CheckCircle, FolderOpen } from 'lucide-react';

interface DataRecoveryDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onRecoveryComplete: (restoredCount: number) => void;
  userEmail: string;
}

export function DataRecoveryDialog({ 
  isOpen, 
  onClose, 
  onRecoveryComplete, 
  userEmail 
}: DataRecoveryDialogProps) {
  const [status, actions] = useLocalFolderBackup(userEmail);
  const [isRestoring, setIsRestoring] = useState(false);
  const [restoreProgress, setRestoreProgress] = useState(0);
  const [restoreStatus, setRestoreStatus] = useState('');
  const [restoreResult, setRestoreResult] = useState<{
    success: boolean;
    restored: number;
    errors: string[];
  } | null>(null);
  const { toast } = useToast();

  const handleRestore = async () => {
    console.log('üîÑ Starting handleRestore with userEmail:', userEmail);
    console.log('üîÑ Backup status:', status);

    setIsRestoring(true);
    setRestoreProgress(0);
    setRestoreStatus('Scanning local folder...');
    setRestoreResult(null);

    try {
      // Simulate progress updates
      const progressSteps = [
        { progress: 25, status: 'Reading backup manifest...' },
        { progress: 50, status: 'Restoring song metadata...' },
        { progress: 75, status: 'Validating restored data...' },
        { progress: 100, status: 'Recovery complete!' }
      ];

      for (const step of progressSteps) {
        setRestoreProgress(step.progress);
        setRestoreStatus(step.status);
        await new Promise(resolve => setTimeout(resolve, 500));
      }

      // Perform actual restoration
      console.log('üîÑ Calling actions.restoreFromBackup()');
      const result = await actions.restoreFromBackup();
      console.log('‚úÖ Restore result:', result);
      setRestoreResult(result);

      if (result.success) {
        toast({
          title: "Recovery Successful",
          description: `Restored ${result.restored} songs from local backup`,
        });

        setTimeout(() => {
          onRecoveryComplete(result.restored);
          onClose();
        }, 2000);
      } else {
        toast({
          title: "Recovery Failed",
          description: "Could not restore data from local backup",
          variant: "destructive",
        });
      }

    } catch (error) {
      console.error('Restore failed:', error);
      setRestoreResult({
        success: false,
        restored: 0,
        errors: [error instanceof Error ? error.message : 'Unknown error']
      });

      toast({
        title: "Recovery Error",
        description: "An error occurred during data recovery",
        variant: "destructive",
      });
    } finally {
      setIsRestoring(false);
    }
  };

  const handleSetupBackup = async () => {
    const success = await actions.setupBackup();
    if (success) {
      toast({
        title: "Backup Setup Complete",
        description: "Local folder backup is now active",
      });
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-lg" data-testid="dialog-data-recovery">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <HardDriveIcon className="w-5 h-5" />
            Data Recovery
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Status Card */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Local Backup Status</CardTitle>
            </CardHeader>
            <CardContent className="pt-0">
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Backup enabled:</span>
                  <span>{status.isEnabled ? 'Yes' : 'No'}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Folder:</span>
                  <span className="text-right truncate max-w-[150px]">{status.folderName}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-muted-foreground">Songs available:</span>
                  <span>{status.stats.totalSongs}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* No backup setup */}
          {!status.isEnabled && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <div className="space-y-3">
                  <p>
                    <strong>No local backup found.</strong> Set up automatic backup to protect 
                    your music library from browser data loss.
                  </p>
                  <Button 
                    onClick={handleSetupBackup}
                    size="sm"
                    className="w-full"
                    data-testid="button-setup-backup-recovery"
                  >
                    <FolderOpen className="w-4 h-4 mr-2" />
                    Set Up Local Backup
                  </Button>
                </div>
              </AlertDescription>
            </Alert>
          )}

          {/* Backup available but no songs to restore */}
          {status.isEnabled && status.stats.totalSongs === 0 && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                Local backup folder is empty. No songs available to restore.
              </AlertDescription>
            </Alert>
          )}

          {/* Recovery in progress */}
          {isRestoring && (
            <Card className="bg-blue-50 dark:bg-blue-950 border-blue-200 dark:border-blue-800">
              <CardContent className="pt-6">
                <div className="space-y-4">
                  <div className="flex items-center gap-2">
                    <RefreshCw className="w-4 h-4 animate-spin text-blue-600" />
                    <span className="text-sm font-medium">Restoring data...</span>
                  </div>
                  <Progress value={restoreProgress} className="w-full" />
                  <p className="text-xs text-muted-foreground">{restoreStatus}</p>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Recovery result */}
          {restoreResult && (
            <Card className={restoreResult.success ? 
              "bg-green-50 dark:bg-green-950 border-green-200 dark:border-green-800" :
              "bg-red-50 dark:bg-red-950 border-red-200 dark:border-red-800"
            }>
              <CardContent className="pt-6">
                <div className="flex items-start gap-3">
                  {restoreResult.success ? 
                    <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" /> :
                    <AlertTriangle className="w-5 h-5 text-red-600 mt-0.5" />
                  }
                  <div className="space-y-2">
                    <p className="text-sm font-medium">
                      {restoreResult.success ? 
                        `Successfully restored ${restoreResult.restored} songs` :
                        'Recovery failed'
                      }
                    </p>
                    {restoreResult.errors.length > 0 && (
                      <div className="space-y-1">
                        <p className="text-xs text-muted-foreground">Errors:</p>
                        {restoreResult.errors.map((error, index) => (
                          <p key={index} className="text-xs text-red-600 dark:text-red-400">
                            ‚Ä¢ {error}
                          </p>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Actions */}
          <div className="flex justify-between">
            <Button
              variant="outline"
              onClick={onClose}
              disabled={isRestoring}
              data-testid="button-cancel-recovery"
            >
              {restoreResult?.success ? 'Close' : 'Cancel'}
            </Button>

            {status.isEnabled && status.stats.totalSongs > 0 && !restoreResult?.success && (
              <Button
                onClick={handleRestore}
                disabled={isRestoring}
                data-testid="button-start-recovery"
              >
                {isRestoring ? 'Restoring...' : `Restore ${status.stats.totalSongs} Songs`}
              </Button>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

export default DataRecoveryDialog;

client/src/components/first-run-setup-wi.content:
/**
 * FirstRunSetupWizard - Guide users through local folder backup setup
 */

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { LocalDiskFileSystem } from '@/lib/local-disk-file-system';
import { StorageOrchestrator } from '@/lib/storage-orchestrator';
import { FolderOpen, Shield, Smartphone, Monitor, AlertTriangle, CheckCircle, X } from 'lucide-react';

interface SetupWizardProps {
  isOpen: boolean;
  onClose: () => void;
  onSetupComplete: (enabled: boolean) => void;
  userEmail: string;
}

interface PlatformInfo {
  isSupported: boolean;
  browserName: string;
  platformName: string;
  recommendation: 'recommended' | 'limited' | 'not-supported';
}

export function FirstRunSetupWizard({ isOpen, onClose, onSetupComplete, userEmail }: SetupWizardProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [platformInfo, setPlatformInfo] = useState<PlatformInfo | null>(null);
  const [isSettingUp, setIsSettingUp] = useState(false);
  const [setupProgress, setSetupProgress] = useState(0);
  const [setupError, setSetupError] = useState<string | null>(null);
  const [selectedChoice, setSelectedChoice] = useState<'local' | 'browser' | null>(null);

  const totalSteps = 3;

  useEffect(() => {
    if (isOpen) {
      detectPlatform();
    }
  }, [isOpen]);

  const detectPlatform = () => {
    const userAgent = navigator.userAgent;
    const isSupported = LocalDiskFileSystem.isSupported();

    let browserName = 'Unknown';
    let platformName = 'Unknown';
    let recommendation: PlatformInfo['recommendation'] = 'not-supported';

    // Detect browser
    if (userAgent.includes('Chrome') && !userAgent.includes('Edg')) {
      browserName = 'Chrome';
    } else if (userAgent.includes('Edg')) {
      browserName = 'Edge';
    } else if (userAgent.includes('Firefox')) {
      browserName = 'Firefox';
    } else if (userAgent.includes('Safari') && !userAgent.includes('Chrome')) {
      browserName = 'Safari';
    }

    // Detect platform
    if (userAgent.includes('Android')) {
      platformName = 'Android';
      if (browserName === 'Chrome' || browserName === 'Edge') {
        recommendation = 'recommended';
      } else {
        recommendation = 'limited';
      }
    } else if (userAgent.includes('iPhone') || userAgent.includes('iPad')) {
      platformName = 'iOS';
      recommendation = 'limited';
    } else if (userAgent.includes('Windows')) {
      platformName = 'Windows';
      if (browserName === 'Chrome' || browserName === 'Edge') {
        recommendation = 'recommended';
      } else {
        recommendation = 'limited';
      }
    } else if (userAgent.includes('Mac')) {
      platformName = 'macOS';
      if (browserName === 'Chrome' || browserName === 'Edge') {
        recommendation = 'recommended';
      } else {
        recommendation = 'limited';
      }
    } else if (userAgent.includes('Linux')) {
      platformName = 'Linux';
      if (browserName === 'Chrome' || browserName === 'Edge') {
        recommendation = 'recommended';
      } else {
        recommendation = 'limited';
      }
    }

    setPlatformInfo({
      isSupported,
      browserName,
      platformName,
      recommendation
    });
  };

  const handleSetupLocalFolder = async () => {
    setIsSettingUp(true);
    setSetupProgress(0);
    setSetupError(null);

    try {
      const orchestrator = StorageOrchestrator.getInstance();

      // Step 1: Request folder access
      setSetupProgress(25);
      const success = await orchestrator.setupLocalFolderBackup();

      if (!success) {
        throw new Error('Failed to setup local folder backup');
      }

      // Step 2: Migrate existing data
      setSetupProgress(50);
      await new Promise(resolve => setTimeout(resolve, 500)); // Show progress

      // Step 3: Initialize auto-save
      setSetupProgress(75);
      await orchestrator.performFullBackup(userEmail);

      // Complete
      setSetupProgress(100);

      setTimeout(() => {
        onSetupComplete(true);
        onClose();
      }, 1000);

    } catch (error) {
      console.error('Setup failed:', error);
      setSetupError(error instanceof Error ? error.message : 'Setup failed');
      setSetupProgress(0);
    } finally {
      setIsSettingUp(false);
    }
  };

  const handleContinueWithBrowser = () => {
    onSetupComplete(false);
    onClose();
  };

  const renderStep1 = () => (
    <div className="space-y-6">
      <div className="text-center space-y-4">
        <Shield className="w-16 h-16 mx-auto text-blue-500" />
        <div>
          <h3 className="text-xl font-semibold mb-2">Protect Your Music Library</h3>
          <p className="text-muted-foreground">
            Set up automatic backup to keep your songs safe from browser data loss
          </p>
        </div>
      </div>

      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <strong>Important:</strong> Browser storage can be cleared by cache cleanup, 
          browser updates, or device changes. Setting up local folder backup ensures 
          your music library is always protected.
        </AlertDescription>
      </Alert>

      <div className="flex justify-end">
        <Button onClick={() => setCurrentStep(2)}>
          Continue
        </Button>
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="space-y-6">
      <div className="text-center">
        <h3 className="text-xl font-semibold mb-2">Choose Your Setup</h3>
        <p className="text-muted-foreground">
          Select the storage method that works best for your device
        </p>
      </div>

      {/* Platform compatibility info */}
      {platformInfo && (
        <Card className="bg-muted/50">
          <CardHeader className="pb-3">
            <div className="flex items-center gap-2">
              {platformInfo.platformName.includes('Android') || platformInfo.platformName.includes('iOS') ? 
                <Smartphone className="w-5 h-5" /> : 
                <Monitor className="w-5 h-5" />
              }
              <span className="font-medium">
                {platformInfo.browserName} on {platformInfo.platformName}
              </span>
              <Badge variant={
                platformInfo.recommendation === 'recommended' ? 'default' :
                platformInfo.recommendation === 'limited' ? 'secondary' : 'destructive'
              }>
                {platformInfo.recommendation === 'recommended' ? 'Fully Supported' :
                 platformInfo.recommendation === 'limited' ? 'Limited Support' : 'Not Supported'}
              </Badge>
            </div>
          </CardHeader>
        </Card>
      )}

      <div className="grid gap-4">
        {/* Local Folder Option */}
        <Card 
          className={`cursor-pointer transition-all ${
            selectedChoice === 'local' ? 'ring-2 ring-primary' : 'hover:bg-muted/50'
          } ${platformInfo?.recommendation === 'not-supported' ? 'opacity-50' : ''}`}
          onClick={() => platformInfo?.recommendation !== 'not-supported' && setSelectedChoice('local')}
        >
          <CardHeader>
            <div className="flex items-start gap-3">
              <FolderOpen className="w-6 h-6 mt-1 text-primary" />
              <div className="flex-1">
                <CardTitle className="text-lg">Local Folder Backup (Recommended)</CardTitle>
                <CardDescription className="mt-2">
                  Choose a folder on your device for automatic, continuous backup. 
                  Your music library will be saved to your selected folder and automatically 
                  synced whenever you make changes.
                </CardDescription>
                <div className="flex flex-wrap gap-2 mt-3">
                  <Badge variant="outline">‚úÖ Survives browser clearing</Badge>
                  <Badge variant="outline">‚úÖ Works offline</Badge>
                  <Badge variant="outline">‚úÖ Cross-device compatible</Badge>
                  {platformInfo?.recommendation === 'recommended' && 
                    <Badge>Recommended for your device</Badge>
                  }
                </div>
              </div>
            </div>
          </CardHeader>
        </Card>

        {/* Browser Only Option */}
        <Card 
          className={`cursor-pointer transition-all ${
            selectedChoice === 'browser' ? 'ring-2 ring-primary' : 'hover:bg-muted/50'
          }`}
          onClick={() => setSelectedChoice('browser')}
        >
          <CardHeader>
            <div className="flex items-start gap-3">
              <Shield className="w-6 h-6 mt-1 text-orange-500" />
              <div className="flex-1">
                <CardTitle className="text-lg">Browser Storage Only</CardTitle>
                <CardDescription className="mt-2">
                  Use only browser storage with manual export backups. 
                  You'll need to manually export your library to protect against data loss.
                </CardDescription>
                <div className="flex flex-wrap gap-2 mt-3">
                  <Badge variant="outline">‚ö†Ô∏è Manual backups needed</Badge>
                  <Badge variant="outline">‚úÖ Works on all browsers</Badge>
                  <Badge variant="outline">‚úÖ No folder permissions</Badge>
                  {platformInfo?.recommendation === 'limited' && 
                    <Badge variant="secondary">Recommended for your device</Badge>
                  }
                </div>
              </div>
            </div>
          </CardHeader>
        </Card>
      </div>

      <div className="flex justify-between">
        <Button variant="outline" onClick={() => setCurrentStep(1)}>
          Back
        </Button>
        <Button 
          onClick={() => setCurrentStep(3)}
          disabled={!selectedChoice}
        >
          Continue
        </Button>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="space-y-6">
      {selectedChoice === 'local' ? (
        <div className="text-center space-y-4">
          <FolderOpen className="w-16 h-16 mx-auto text-primary" />
          <div>
            <h3 className="text-xl font-semibold mb-2">Set Up Local Folder Backup</h3>
            <p className="text-muted-foreground">
              Click "Choose Folder" to select where your music library will be automatically backed up
            </p>
          </div>

          {isSettingUp && (
            <div className="space-y-4">
              <Progress value={setupProgress} className="w-full" />
              <p className="text-sm text-muted-foreground">
                {setupProgress < 25 ? 'Requesting folder access...' :
                 setupProgress < 50 ? 'Creating backup structure...' :
                 setupProgress < 75 ? 'Migrating existing data...' :
                 setupProgress < 100 ? 'Initializing auto-save...' : 'Setup complete!'}
              </p>
            </div>
          )}

          {setupError && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>{setupError}</AlertDescription>
            </Alert>
          )}
        </div>
      ) : (
        <div className="text-center space-y-4">
          <Shield className="w-16 h-16 mx-auto text-orange-500" />
          <div>
            <h3 className="text-xl font-semibold mb-2">Browser Storage Confirmed</h3>
            <p className="text-muted-foreground">
              Your music will be stored in browser storage. Remember to regularly export 
              your library using the export feature to protect against data loss.
            </p>
          </div>

          <Alert>
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription>
              <strong>Reminder:</strong> Use the export feature regularly to create backups 
              of your music library. You can access this from the main menu.
            </AlertDescription>
          </Alert>
        </div>
      )}

      <div className="flex justify-between">
        <Button variant="outline" onClick={() => setCurrentStep(2)}>
          Back
        </Button>
        <div className="space-x-2">
          {selectedChoice === 'local' && (
            <Button 
              onClick={handleSetupLocalFolder}
              disabled={isSettingUp}
              data-testid="button-setup-local-folder"
            >
              {isSettingUp ? 'Setting up...' : 'Choose Folder'}
            </Button>
          )}
          {selectedChoice === 'browser' && (
            <Button 
              onClick={handleContinueWithBrowser}
              data-testid="button-continue-browser"
            >
              Continue with Browser Storage
            </Button>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl" data-testid="dialog-setup-wizard">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="text-2xl">Welcome to StageTracker</DialogTitle>
            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              Step {currentStep} of {totalSteps}
            </div>
          </div>
        </DialogHeader>

        <div className="py-4">
          {currentStep === 1 && renderStep1()}
          {currentStep === 2 && renderStep2()}
          {currentStep === 3 && renderStep3()}
        </div>
      </DialogContent>
    </Dialog>
  );
}

export default FirstRunSetupWizard;

